// Let's examine the Excel workbooks to extract pricing and technical data
const files = [
    "trainer resource toolkit  Module 1 Workbook.xlsx",
    "Solar  Interview Summary Based Qualitative and Quantitative 1.xlsx",
    "Iraqi_Solar_Training_ENHANCED_Workbook.xlsx"
];

console.log("Examining Excel files for Iraqi solar data...\n");

// Read and analyze the interview summary file
try {
    const fileContent = await window.fs.readFile("Solar  Interview Summary Based Qualitative and Quantitative 1.xlsx");
    const XLSX = await import('xlsx');

    const workbook = XLSX.read(fileContent, {
        cellStyles: true,
        cellFormulas: true,
        cellDates: true,
        cellNF: true,
        sheetStubs: true
    });

    console.log("Workbook sheets found:", Object.keys(workbook.Sheets));

    // Let's examine the structure of the first sheet
    const firstSheetName = Object.keys(workbook.Sheets)[0];
    const sheet = workbook.Sheets[firstSheetName];

    // Get all cells
    const range = XLSX.utils.decode_range(sheet['!ref']);
    console.log(`\nSheet "${firstSheetName}" dimensions: ${range.e.r + 1} rows x ${range.e.c + 1} columns`);

    // Sample first 10 rows to understand structure
    console.log("\nFirst 10 rows preview:");
    for (let row = 0; row <= Math.min(9, range.e.r); row++) {
        let rowData = [];
        for (let col = 0; col <= range.e.c; col++) {
            const cellAddress = XLSX.utils.encode_cell({r: row, c: col});
            const cell = sheet[cellAddress];
            rowData.push(cell ? cell.v : '');
        }
        console.log(`Row ${row + 1}:`, rowData.filter(v => v !== '').slice(0, 5)); // Show first 5 non-empty values
    }

} catch (error) {
    console.error("Error reading file:", error);
}
