// Let's examine the structure of both professional tools
const file1 = await window.fs.readFile('Iraqi_Solar_COMPLETE_Professional_Tool.xlsx');
const file2 = await window.fs.readFile('Iraqi_Solar_PROFESSIONAL_Tool_FIXED.xlsx');

import * as XLSX from 'xlsx';

// Read the first workbook
const workbook1 = XLSX.read(file1, {
    cellStyles: true,
    cellFormulas: true,
    cellDates: true,
    cellNF: true,
    sheetStubs: true
});

// Read the second workbook
const workbook2 = XLSX.read(file2, {
    cellStyles: true,
    cellFormulas: true,
    cellDates: true,
    cellNF: true,
    sheetStubs: true
});

// Get sheet names from both workbooks
console.log("COMPLETE Professional Tool sheets:");
console.log(workbook1.SheetNames);

console.log("\nPROFESSIONAL Tool FIXED sheets:");
console.log(workbook2.SheetNames);

// Let's examine the structure of key tabs from each
console.log("\n=== Examining COMPLETE Tool ===");
workbook1.SheetNames.forEach(sheetName => {
    const sheet = workbook1.Sheets[sheetName];
    const range = sheet['!ref'];
    console.log(`\n${sheetName}: Range ${range}`);

    // Look at first few cells to understand structure
    if (sheet['A1']) console.log(`  A1: ${sheet['A1'].v}`);
    if (sheet['A2']) console.log(`  A2: ${sheet['A2'].v}`);
    if (sheet['B1']) console.log(`  B1: ${sheet['B1'].v}`);
});

console.log("\n=== Examining FIXED Tool Structure ===");
workbook2.SheetNames.forEach(sheetName => {
    const sheet = workbook2.Sheets[sheetName];
    const range = sheet['!ref'];
    console.log(`\n${sheetName}: Range ${range}`);

    // Look at key cells to understand what each sheet does
    const cellsToCheck = ['A1', 'A3', 'A5', 'B1', 'C1'];
    cellsToCheck.forEach(cell => {
        if (sheet[cell] && sheet[cell].v) {
            console.log(`  ${cell}: ${sheet[cell].v}`);
        }
    });
});

// Let's also check if there's a troubleshooting section anywhere
console.log("\n=== Searching for Troubleshooting Content ===");
workbook1.SheetNames.forEach(sheetName => {
    const sheet = workbook1.Sheets[sheetName];
    const range = XLSX.utils.decode_range(sheet['!ref']);

    for (let row = range.s.r; row <= Math.min(range.e.r, 50); row++) {
        for (let col = range.s.c; col <= Math.min(range.e.c, 10); col++) {
            const cellAddress = XLSX.utils.encode_cell({r: row, c: col});
            const cell = sheet[cellAddress];
            if (cell && cell.v && typeof cell.v === 'string') {
                if (cell.v.toLowerCase().includes('troubleshoot') || 
                    cell.v.toLowerCase().includes('fault') ||
                    cell.v.toLowerCase().includes('diagnostic')) {
                    console.log(`Found in ${sheetName} at ${cellAddress}: ${cell.v}`);
                }
            }
        }
    }
});

