// Let me examine the trainer resource toolkit workbook to see what cost and consumption data is available
const workbookData = await window.fs.readFile('trainer resource toolkit  Module 1 Workbook.xlsx');

import * as XLSX from 'xlsx';
const workbook = XLSX.read(workbookData, {
    cellStyles: true,    
    cellFormulas: true,  
    cellDates: true,     
    cellNF: true,        
    sheetStubs: true     
});

// First, let's see what sheets are available
console.log("Available sheets:");
console.log(workbook.SheetNames);

// Let's also see the workbook metadata
if (workbook.Workbook) {
    console.log("\nWorkbook metadata:");
    console.log(workbook.Workbook);
}

// Let's try a simpler approach to read the workbook
import * as XLSX from 'xlsx';

try {
    const workbookData = await window.fs.readFile('trainer resource toolkit  Module 1 Workbook.xlsx');
    const workbook = XLSX.read(workbookData);

    console.log("Sheet names:", workbook.SheetNames);

    // Look at the first sheet
    if (workbook.SheetNames.length > 0) {
        const firstSheet = workbook.Sheets[workbook.SheetNames[0]];
        console.log("\nFirst sheet name:", workbook.SheetNames[0]);

        // Get a sample of the data
        const range = XLSX.utils.decode_range(firstSheet['!ref']);
        console.log("Sheet range:", firstSheet['!ref']);

        // Look at first few cells
        for (let row = range.s.r; row <= Math.min(range.s.r + 10, range.e.r); row++) {
            for (let col = range.s.c; col <= Math.min(range.s.c + 5, range.e.c); col++) {
                const cellAddress = XLSX.utils.encode_cell({ r: row, c: col });
                const cell = firstSheet[cellAddress];
                if (cell && cell.v) {
                    console.log(`${cellAddress}: ${cell.v}`);
                }
            }
        }
    }
} catch (error) {
    console.log("Error reading workbook:", error.message);
}
