const fileContent = await window.fs.readFile('paste.txt', { encoding: 'utf8' });

// Extract the data between the triple brackets
const match = fileContent.match(/\[\[\[(.*?)\]\]\]/s);
if (!match) {
    console.log("Could not find data in triple brackets");
    console.log("File content preview:", fileContent.slice(0, 500));
} else {
    const dataSection = match[1];

    // Split by newlines and clean up the entries
    const countries = dataSection.split('\n')
        .map(line => line.trim())
        .filter(line => line.length > 0);

    console.log("Total entries:", countries.length);
    console.log("First 10 entries:", countries.slice(0, 10));

    // Count frequencies
    const countryCount = {};

    countries.forEach(country => {
        // Clean up some entries
        let cleanCountry = country.trim();

        // Convert "beirut" to "Lebanon" since it's the capital
        if (cleanCountry.toLowerCase() === 'beirut') {
            cleanCountry = 'Lebanon';
        }

        // Skip regional entries and global entries for the main count
        if (cleanCountry.startsWith('REGION -') || cleanCountry.startsWith('GLOBAL -')) {
            return;
        }

        // For compound entries, take the first country mentioned
        if (cleanCountry.includes(',')) {
            cleanCountry = cleanCountry.split(',')[0].trim();
        }

        countryCount[cleanCountry] = (countryCount[cleanCountry] || 0) + 1;
    });

    // Sort by frequency
    const sortedCountries = Object.entries(countryCount)
        .sort((a, b) => b[1] - a[1]);

    console.log("\nTop 10 countries by frequency:");
    sortedCountries.slice(0, 10).forEach(([country, count], index) => {
        console.log(`${index + 1}. ${country}: ${count}`);
    });

    // Get top 7 for the pie chart
    const top7 = sortedCountries.slice(0, 7);
    const others = sortedCountries.slice(7).reduce((sum, [_, count]) => sum + count, 0);

    // Create data for pie chart
    const pieData = [
        ...top7.map(([country, count]) => ({ country, count })),
        { country: 'Others', count: others }
    ];

    console.log("\nPie chart data (Top 7 + Others):");
    pieData.forEach(item => {
        const percentage = ((item.count / countries.filter(c => !c.startsWith('REGION') && !c.startsWith('GLOBAL')).length) * 100).toFixed(1);
        console.log(`${item.country}: ${item.count} (${percentage}%)`);
    });

    // Store the data for the artifact
    window.chartData = pieData;
    window.top7Countries = top7;
}

