import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Legend, Tooltip } from 'recharts';

const CountryPieChart = () => {
  const data = [
    { country: 'Lebanon', count: 419, percentage: 44.4 },
    { country: 'Nigeria', count: 99, percentage: 10.5 },
    { country: 'Kenya', count: 89, percentage: 9.4 },
    { country: 'Jordan', count: 61, percentage: 6.5 },
    { country: 'Egypt', count: 39, percentage: 4.1 },
    { country: 'Palestine', count: 37, percentage: 3.9 },
    { country: 'Uganda', count: 26, percentage: 2.8 },
    { country: 'Others', count: 174, percentage: 18.4 }
  ];

  const COLORS = [
    '#e74c3c', // Red for Lebanon (dominant)
    '#3498db', // Blue for Nigeria
    '#2ecc71', // Green for Kenya
    '#f39c12', // Orange for Jordan
    '#9b59b6', // Purple for Egypt
    '#1abc9c', // Teal for Palestine
    '#f1c40f', // Yellow for Uganda
    '#95a5a6'  // Gray for Others
  ];

  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      return (
        <div className="bg-white p-3 border rounded shadow-lg">
          <p className="font-semibold">{data.country}</p>
          <p className="text-sm">Count: {data.count}</p>
          <p className="text-sm">Percentage: {data.percentage}%</p>
        </div>
      );
    }
    return null;
  };

  const renderCustomLabel = ({ country, percentage }) => {
    if (percentage < 3) return ''; // Don't show labels for small slices
    return `${country}\n${percentage}%`;
  };

  return (
    <div className="w-full max-w-6xl mx-auto p-6 bg-white">
      <div className="text-center mb-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-2">Country Distribution Analysis</h1>
        <p className="text-gray-600">Based on 944 country entries</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Pie Chart */}
        <div className="lg:col-span-2">
          <ResponsiveContainer width="100%" height={500}>
            <PieChart>
              <Pie
                data={data}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={renderCustomLabel}
                outerRadius={180}
                fill="#8884d8"
                dataKey="count"
              >
                {data.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip content={<CustomTooltip />} />
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* Data Table */}
        <div className="lg:col-span-1">
          <h2 className="text-xl font-bold mb-4 text-gray-800">Top 7 Countries + Others</h2>
          <div className="space-y-2">
            {data.map((item, index) => (
              <div key={item.country} className="flex items-center justify-between p-3 bg-gray-50 rounded">
                <div className="flex items-center space-x-3">
                  <div 
                    className="w-4 h-4 rounded"
                    style={{ backgroundColor: COLORS[index] }}
                  ></div>
                  <span className="font-medium text-sm">{item.country}</span>
                </div>
                <div className="text-right">
                  <div className="font-bold text-sm">{item.count}</div>
                  <div className="text-xs text-gray-500">{item.percentage}%</div>
                </div>
              </div>
            ))}
          </div>

          <div className="mt-6 p-4 bg-blue-50 rounded">
            <h3 className="font-bold text-sm text-blue-800 mb-2">Key Insights</h3>
            <ul className="text-xs text-blue-700 space-y-1">
              <li>• Lebanon dominates with 44.4% of all entries</li>
              <li>• Top 3 countries account for 64.3% of data</li>
              <li>• Nigeria and Kenya are close in frequency</li>
              <li>• MENA region is heavily represented</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CountryPieChart;

