// Read the Excel file
const response = await window.fs.readFile('Iraqi_Solar_V442.xlsx');
const workbook = XLSX.read(response, {
    cellStyles: true,
    cellFormulas: true,
    cellDates: true,
    cellNF: true,
    sheetStubs: true
});

// Get overview of the workbook structure
console.log("Workbook Structure:");
console.log("==================");
console.log("Sheet names:", workbook.SheetNames);
