import * as XLSX from 'xlsx';

// Read the Excel file
const response = await window.fs.readFile('Iraqi_Solar_V442.xlsx');
const workbook = XLSX.read(response, {
    cellStyles: true,
    cellFormulas: true,
    cellDates: true,
    cellNF: true,
    sheetStubs: true
});

// Get overview of the workbook structure
console.log("Workbook Structure:");
console.log("==================");
console.log("Sheet names:", workbook.SheetNames);
console.log("\nNumber of sheets:", workbook.SheetNames.length);

// Look at each sheet's basic info
workbook.SheetNames.forEach((sheetName, index) => {
    const sheet = workbook.Sheets[sheetName];
    const range = sheet['!ref'];
    console.log(`\n${index + 1}. Sheet: "${sheetName}"`);
    console.log(`   Range: ${range || 'Empty sheet'}`);

    // Count non-empty cells
    let cellCount = 0;
    if (range) {
        const keys = Object.keys(sheet);
        cellCount = keys.filter(key => key.indexOf('!') !== 0).length;
    }
    console.log(`   Non-empty cells: ${cellCount}`);
});
