// Let's examine the first few sheets to understand the structure
import * as XLSX from 'xlsx';

const response = await window.fs.readFile('Architecture  Interview Summary Based Qualitative and Quantitative.xlsx');
const workbook = XLSX.read(response);

// Function to explore a sheet
function exploreSheet(sheetName) {
  console.log(`\n=== ${sheetName} ===`);
  const sheet = workbook.Sheets[sheetName];

  // Get the range
  const range = XLSX.utils.decode_range(sheet['!ref']);
  console.log(`Range: ${sheet['!ref']} (${range.e.r + 1} rows, ${range.e.c + 1} columns)`);

  // Convert to JSON to see the data
  const data = XLSX.utils.sheet_to_json(sheet, { header: 1 });

  // Show first few rows
  console.log("\nFirst 5 rows:");
  data.slice(0, 5).forEach((row, i) => {
    console.log(`Row ${i}: ${JSON.stringify(row)}`);
  });

  // Show column headers if they exist
  if (data.length > 0) {
    console.log("\nColumn headers:");
    console.log(data[0]);
  }
}

// Explore first three sheets
['Laith Abdulla', 'Ahmed Al-Obaidy', 'Fatima Eyad'].forEach(sheetName => {
  exploreSheet(sheetName);
});

// Let's extract the actual insights from each sheet
import * as XLSX from 'xlsx';

const response = await window.fs.readFile('Architecture  Interview Summary Based Qualitative and Quantitative.xlsx');
const workbook = XLSX.read(response);

// Function to extract insights from a sheet
function extractInsights(sheetName) {
  console.log(`\n=== Insights from ${sheetName} ===`);
  const sheet = workbook.Sheets[sheetName];
  const data = XLSX.utils.sheet_to_json(sheet, { header: 1 });

  // Find where insights start (usually after "1 โ High-value insights")
  let insightStartRow = -1;
  for (let i = 0; i < data.length; i++) {
    if (data[i][0] && data[i][0].toString().includes("High-value insights")) {
      insightStartRow = i + 2; // Skip the header row
      break;
    }
  }

  if (insightStartRow === -1) return;

  // Extract insights
  for (let i = insightStartRow; i < Math.min(insightStartRow + 5, data.length); i++) {
    const row = data[i];
    if (row && row.length > 0 && row[0]) { // Check if row has content
      console.log(`\nInsight ${row[0]}:`);
      console.log(`Summary: ${row[2] || 'N/A'}`);
      console.log(`Course Relevance: ${row[3] || 'N/A'}`);
      console.log(`Action: ${row[4] || 'N/A'}`);
      console.log(`Impact: ${row[5] || 'N/A'}`);
    }
  }
}

// Extract insights from all sheets
workbook.SheetNames.forEach(sheetName => {
  extractInsights(sheetName);
});
