import * as XLSX from 'xlsx';
import _ from 'lodash';

// Read the first file: BEIT Module Development Interviews Data Summary
const file1Response = await window.fs.readFile('BEIT Module Development Interviews  Data Summary.xlsx');
const workbook1 = XLSX.read(file1Response, {
    cellStyles: true,
    cellFormulas: true,
    cellDates: true,
    cellNF: true,
    sheetStubs: true
});

console.log("=== BEIT Module Development Interviews Workbook ===");
console.log("Sheet names:", workbook1.SheetNames);
console.log("\nExamining structure of first sheet:");

const firstSheet = workbook1.Sheets[workbook1.SheetNames[0]];
const data1 = XLSX.utils.sheet_to_json(firstSheet, { header: 1 });

console.log("First 5 rows:");
console.log(data1.slice(0, 5));
console.log("\nTotal rows:", data1.length);
console.log("\nColumn headers (if any):", data1[0]);

// Let's start by just checking if we can read the files
const fileList = ['BEIT Module Development Interviews  Data Summary.xlsx', 
                  'Insulation  Interview Summary Based Qualitative and Quantitative.xlsx',
                  'Module 2_ Trainers Feedback.xlsx'];

console.log("Checking file availability...");

for (let filename of fileList) {
    try {
        const response = await window.fs.readFile(filename);
        console.log(`✓ ${filename} - Size: ${response.length} bytes`);
    } catch (error) {
        console.log(`✗ ${filename} - Error: ${error.message}`);
    }
}
