import * as XLSX from 'xlsx';
import _ from 'lodash';

// First file - Module 2 Trainers Feedback
const file1 = await window.fs.readFile('Module 2_ Trainers Feedback.xlsx');
const workbook1 = XLSX.read(file1);
const sheet1 = workbook1.Sheets[workbook1.SheetNames[0]];
const data1 = XLSX.utils.sheet_to_json(sheet1);

// Look for inspiring quotes
console.log("=== INSPIRING STRATEGIES & HIGH AFFECT MOMENTS FROM TRAINERS ===");
const inspiringKeywords = ['story', 'personal', 'emotion', 'impact', 'success', 'transform', 'inspire', 'motivat', 'engag', 'real', 'testimon', 'comfort', 'wellness', 'trust', 'quality', 'demo', 'practical'];

const inspiringFeedback = data1.filter(item => {
    const text = (item['Feedback/Suggestion'] || '').toLowerCase();
    return inspiringKeywords.some(keyword => text.includes(keyword));
});

console.log("\nFeedback with inspiring/motivational elements:");
inspiringFeedback.forEach((item, idx) => {
    console.log(`\n${idx + 1}. From ${item['Expert Name'] || 'Expert'} (${item['Category/Topic']}):`);
    console.log(`   "${item['Feedback/Suggestion']}"`);
    console.log(`   Priority: ${item['Priority']}, Status: ${item['Status']}`);
});

// Second file - BEIT Module Development Interviews
console.log("\n\n=== BEIT MODULE DEVELOPMENT INTERVIEWS ===");
const file2 = await window.fs.readFile('BEIT Module Development Interviews  Data Summary.xlsx');
const workbook2 = XLSX.read(file2);

console.log("Sheet names:", workbook2.SheetNames);

// Get all data from all sheets
workbook2.SheetNames.forEach(sheetName => {
    console.log(`\n--- Sheet: ${sheetName} ---`);
    const sheet = workbook2.Sheets[sheetName];
    const data = XLSX.utils.sheet_to_json(sheet);
    console.log("Number of entries:", data.length);

    if (data.length > 0) {
        console.log("Columns:", Object.keys(data[0]));

        // Look for goals, audience info, and inspiring content
        if (sheetName.includes('Summary') || sheetName.includes('Goals')) {
            console.log("\nFull data from this sheet:");
            data.forEach((row, idx) => {
                console.log(`\nEntry ${idx + 1}:`);
                Object.entries(row).forEach(([key, value]) => {
                    if (value && value.toString().trim()) {
                        console.log(`  ${key}: ${value}`);
                    }
                });
            });
        }
    }
});

// Focus on the Insulation sheet
const insulationSheet = workbook2.Sheets['Insulation'];
const insulationData = XLSX.utils.sheet_to_json(insulationSheet);

console.log("=== INSULATION MODULE INSIGHTS ===");
console.log("Total insights:", insulationData.length);

// Group by expert
const insulationByExpert = _.groupBy(insulationData, 'Expert Name');
console.log("\nExperts interviewed:");
Object.keys(insulationByExpert).forEach(expert => {
    const expertData = insulationByExpert[expert][0];
    console.log(`- ${expert} (${expertData.Role}): ${insulationByExpert[expert].length} insights`);
});

// High priority insights
const highPriorityInsulation = insulationData.filter(item => item.Priority === 'High');
console.log(`\nHigh Priority Insights: ${highPriorityInsulation.length}`);

// Get key categories
const categories = _.groupBy(insulationData, 'Category');
console.log("\nInsight Categories:");
Object.keys(categories).forEach(cat => {
    console.log(`- ${cat}: ${categories[cat].length} insights`);
});

// Extract goals and audience information
console.log("\n=== TRAINING GOALS AND AUDIENCE ===");
const goalKeywords = ['goal', 'objective', 'purpose', 'aim', 'target', 'audience', 'participant', 'trainee'];
const goalsAndAudience = insulationData.filter(item => {
    const text = (item['Key Insight Summary'] + ' ' + item['Action Needed']).toLowerCase();
    return goalKeywords.some(keyword => text.includes(keyword));
});

console.log("\nInsights about goals and audience:");
goalsAndAudience.forEach((item, idx) => {
    console.log(`\n${idx + 1}. From ${item['Expert Name']} (${item.Category}):`);
    console.log(`   Insight: "${item['Key Insight Summary']}"`);
    console.log(`   Action: "${item['Action Needed']}"`);
    console.log(`   Priority: ${item.Priority}`);
});

// High affect moments and inspiring strategies
console.log("\n=== HIGH AFFECT MOMENTS & INSPIRING STRATEGIES ===");
const affectKeywords = ['story', 'personal', 'emotion', 'impact', 'success', 'transform', 'inspire', 'motivat', 'engag', 'real', 'experience', 'demo', 'hands-on', 'practical', 'visual'];

const highAffectInsights = insulationData.filter(item => {
    const text = (item['Key Insight Summary'] + ' ' + item['Action Needed']).toLowerCase();
    return affectKeywords.some(keyword => text.includes(keyword));
});

console.log(`\nFound ${highAffectInsights.length} insights with high affect/inspiring elements:`);
highAffectInsights.slice(0, 10).forEach((item, idx) => {
    console.log(`\n${idx + 1}. ${item['Expert Name']} - ${item.Category}:`);
    console.log(`   "${item['Key Insight Summary']}"`);
    console.log(`   Action: "${item['Action Needed']}"`);
});

import * as XLSX from 'xlsx';
import _ from 'lodash';

console.log("=== COMPREHENSIVE ANALYSIS OF ALL TRAINING FEEDBACK FILES ===\n");

// Read all three files
const file1 = await window.fs.readFile('Module 2_ Trainers Feedback.xlsx');
const workbook1 = XLSX.read(file1);
const trainersData = XLSX.utils.sheet_to_json(workbook1.Sheets[workbook1.SheetNames[0]]);

const file2 = await window.fs.readFile('BEIT Module Development Interviews  Data Summary.xlsx');
const workbook2 = XLSX.read(file2);
const insulationData = XLSX.utils.sheet_to_json(workbook2.Sheets['Insulation']);

const file3 = await window.fs.readFile('Insulation  Interview Summary Based Qualitative and Quantitative.xlsx');
const workbook3 = XLSX.read(file3);

// Analyze third file structure
console.log("Third file sheets:", workbook3.SheetNames);
const summaryData = XLSX.utils.sheet_to_json(workbook3.Sheets[workbook3.SheetNames[0]]);
console.log("Summary data rows:", summaryData.length);
if (summaryData.length > 0) {
    console.log("Columns:", Object.keys(summaryData[0]));
}

// Extract key information about goals and audience
console.log("\n=== TRAINING PROGRAM GOALS ===");

// From insulation insights
const goalInsights = insulationData.filter(item => {
    const text = (item['Key Insight Summary'] || '').toLowerCase();
    return text.includes('goal') || text.includes('objective') || text.includes('purpose') || 
           text.includes('train') || text.includes('learn');
});

console.log("\nKey Training Goals from Expert Interviews:");
goalInsights.forEach((item, idx) => {
    if (item['Key Insight Summary']) {
        console.log(`\n${idx + 1}. ${item['Expert Name']} (${item.Role}):`);
        console.log(`   "${item['Key Insight Summary']}"`);
    }
});

// Audience analysis
console.log("\n\n=== TARGET AUDIENCE ===");
const audienceInsights = insulationData.filter(item => {
    const text = ((item['Key Insight Summary'] || '') + ' ' + (item['Action Needed'] || '')).toLowerCase();
    return text.includes('audience') || text.includes('trainee') || text.includes('participant') || 
           text.includes('contractor') || text.includes('engineer') || text.includes('skill');
});

console.log("\nTarget Audience Insights:");
audienceInsights.slice(0, 5).forEach((item, idx) => {
    console.log(`\n${idx + 1}. From ${item['Expert Name']}:`);
    console.log(`   "${item['Key Insight Summary']}"`);
    if (item['Action Needed']) {
        console.log(`   Recommendation: "${item['Action Needed']}"`);
    }
});

// Extract verbatim inspiring quotes
console.log("\n\n=== VERBATIM INSPIRING QUOTES & HIGH AFFECT STRATEGIES ===");

// From all sources
const allInspiringContent = [];

// From trainers feedback
trainersData.forEach(item => {
    const text = (item['Feedback/Suggestion'] || '').toLowerCase();
    if (text.includes('story') || text.includes('real') || text.includes('demo') || 
        text.includes('personal') || text.includes('comfort') || text.includes('transform')) {
        allInspiringContent.push({
            source: 'Trainers Feedback',
            expert: item['Expert Name'] || 'Expert',
            category: item['Category/Topic'],
            quote: item['Feedback/Suggestion'],
            priority: item['Priority']
        });
    }
});

// From insulation insights
insulationData.forEach(item => {
    const text = ((item['Key Insight Summary'] || '') + ' ' + (item['Action Needed'] || '')).toLowerCase();
    if (text.includes('story') || text.includes('demo') || text.includes('hands-on') || 
        text.includes('visual') || text.includes('experience') || text.includes('practical') ||
        text.includes('engage') || text.includes('motivat')) {
        allInspiringContent.push({
            source: 'Development Interviews',
            expert: item['Expert Name'],
            category: item['Category'],
            quote: item['Key Insight Summary'],
            action: item['Action Needed'],
            priority: item['Priority']
        });
    }
});

console.log(`\nFound ${allInspiringContent.length} inspiring/high-affect elements:`);
allInspiringContent.slice(0, 15).forEach((item, idx) => {
    console.log(`\n${idx + 1}. ${item.expert} (${item.source}):`);
    console.log(`   Category: ${item.category}`);
    console.log(`   "${item.quote}"`);
    if (item.action) {
        console.log(`   Action needed: "${item.action}"`);
    }
    console.log(`   Priority: ${item.priority}`);
});

// Let's examine the summary file more thoroughly
console.log("\n=== EXAMINING QUALITATIVE/QUANTITATIVE SUMMARY FILE ===");

// Check all sheets in the third file
workbook3.SheetNames.forEach(sheetName => {
    console.log(`\n--- Sheet: ${sheetName} ---`);
    const sheet = workbook3.Sheets[sheetName];
    const data = XLSX.utils.sheet_to_json(sheet);
    console.log(`Entries: ${data.length}`);

    // Look for content beyond basic info
    if (data.length > 0) {
        // Check if there are columns beyond the basic ones
        const columns = Object.keys(data[0]);
        console.log("Columns:", columns);

        // If there are more columns with actual content, display them
        const contentColumns = columns.filter(col => 
            !['Name', 'Module', 'Date of Interview', 'Role', 'Transcript Link'].includes(col)
        );

        if (contentColumns.length > 0) {
            console.log("Content columns found:", contentColumns);
            console.log("\nSample content:");
            data.slice(0, 3).forEach((row, idx) => {
                console.log(`\nEntry ${idx + 1}:`);
                contentColumns.forEach(col => {
                    if (row[col]) {
                        console.log(`  ${col}: "${row[col]}"`);
                    }
                });
            });
        }
    }
});

// Now let's extract key skills and learning objectives from all data
console.log("\n\n=== SKILLS AND LEARNING OBJECTIVES ANALYSIS ===");

// From insulation data
const skillsData = insulationData.filter(item => {
    const text = ((item['Key Insight Summary'] || '') + ' ' + (item['Action Needed'] || '')).toLowerCase();
    return text.includes('skill') || text.includes('learn') || text.includes('teach') || 
           text.includes('understand') || text.includes('know') || text.includes('ability');
});

console.log("\nKey Skills and Learning Points:");
const skills = {
    technical: [],
    sales: [],
    practical: [],
    awareness: []
};

skillsData.forEach(item => {
    const category = item['Category'] || '';
    const insight = item['Key Insight Summary'] || '';
    const action = item['Action Needed'] || '';

    if (category.toLowerCase().includes('tech') || category.toLowerCase().includes('material')) {
        skills.technical.push({ insight, action, expert: item['Expert Name'] });
    } else if (category.toLowerCase().includes('sale') || category.toLowerCase().includes('behav')) {
        skills.sales.push({ insight, action, expert: item['Expert Name'] });
    } else if (category.toLowerCase().includes('demo') || category.toLowerCase().includes('practical')) {
        skills.practical.push({ insight, action, expert: item['Expert Name'] });
    } else {
        skills.awareness.push({ insight, action, expert: item['Expert Name'] });
    }
});

console.log("\nTECHNICAL SKILLS:");
skills.technical.slice(0, 5).forEach((s, i) => {
    console.log(`${i + 1}. "${s.insight}"`);
    if (s.action) console.log(`   Action: "${s.action}"`);
});

console.log("\nSALES & BEHAVIORAL SKILLS:");
skills.sales.slice(0, 5).forEach((s, i) => {
    console.log(`${i + 1}. "${s.insight}"`);
    if (s.action) console.log(`   Action: "${s.action}"`);
});

console.log("\nPRACTICAL/HANDS-ON SKILLS:");
skills.practical.slice(0, 5).forEach((s, i) => {
    console.log(`${i + 1}. "${s.insight}"`);
    if (s.action) console.log(`   Action: "${s.action}"`);
});

