    return text.includes('phase') || text.includes('start small') || text.includes('pilot') || text.includes('gradual');
});

phasedQuotes.forEach(item => {
    console.log("\nSource:", item['Expert Name'] || 'Expert');
    console.log("Quote:", item['Feedback/Suggestion'] || item['Key Insight Summary']);
