import * as XLSX from 'xlsx';

// Let's examine the trainer resource toolkit workbook first
const trainerToolkitData = await window.fs.readFile('trainer resource toolkit  Module 1 Workbook.xlsx');
const trainerWorkbook = XLSX.read(trainerToolkitData, {
    cellStyles: true,
    cellFormulas: true,
    cellDates: true,
    cellNF: true,
    sheetStubs: true
});

console.log("=== TRAINER RESOURCE TOOLKIT ANALYSIS ===");
console.log("Sheet names:", trainerWorkbook.SheetNames);

// Get basic info about each sheet
trainerWorkbook.SheetNames.forEach(sheetName => {
    const worksheet = trainerWorkbook.Sheets[sheetName];
    const range = XLSX.utils.decode_range(worksheet['!ref'] || 'A1:A1');
    console.log(`\nSheet: ${sheetName}`);
    console.log(`Range: ${worksheet['!ref']}`);
    console.log(`Dimensions: ${range.e.r + 1} rows x ${range.e.c + 1} columns`);
});

// Let's examine the content of the first sheet - Ahmed's Cafe Load & Baseline ROI
const ahmetsSheet = trainerWorkbook.Sheets["Ahmeds Cafe Load & Baseline ROI"];

console.log("=== AHMED'S CAFE LOAD & BASELINE ROI SHEET ===");

// Get the first 10 rows to understand the structure
for (let row = 1; row <= 15; row++) {
    let rowData = [];
    for (let col = 0; col < 10; col++) {
        const cellAddress = XLSX.utils.encode_cell({ r: row - 1, c: col });
        const cell = ahmetsSheet[cellAddress];
        if (cell && cell.v !== undefined) {
            rowData.push(cell.v);
        } else {
            rowData.push('');
        }
    }
    if (rowData.some(cell => cell !== '')) {
        console.log(`Row ${row}:`, rowData.slice(0, 6).join(' | '));
    }
}

console.log("\n=== KEY SECTIONS ===");
// Look for key sections
const keyRanges = ['A20:F25', 'A30:F35', 'A40:F45'];
keyRanges.forEach(range => {
    console.log(`\nSection ${range}:`);
    const decoded = XLSX.utils.decode_range(range);
    for (let row = decoded.s.r; row <= Math.min(decoded.e.r, decoded.s.r + 3); row++) {
        let rowData = [];
        for (let col = decoded.s.c; col <= decoded.e.c; col++) {
            const cellAddress = XLSX.utils.encode_cell({ r: row, c: col });
            const cell = ahmetsSheet[cellAddress];
            if (cell && cell.v !== undefined) {
                rowData.push(cell.v);
            } else {
                rowData.push('');
            }
        }
        if (rowData.some(cell => cell !== '')) {
            console.log(rowData.join(' | '));
        }
    }
});

import * as XLSX from 'xlsx';

// Reload the trainer resource toolkit workbook
const trainerToolkitData = await window.fs.readFile('trainer resource toolkit  Module 1 Workbook.xlsx');
const trainerWorkbook = XLSX.read(trainerToolkitData, {
    cellStyles: true,
    cellFormulas: true,
    cellDates: true,
    cellNF: true,
    sheetStubs: true
});

// Convert the first sheet to JSON to see the structure better
const ahmetsSheet = trainerWorkbook.Sheets["Ahmeds Cafe Load & Baseline ROI"];
const ahmetsData = XLSX.utils.sheet_to_json(ahmetsSheet, { header: 1, defval: '' });

console.log("=== AHMED'S CAFE LOAD & BASELINE ROI CONTENT ===");
console.log("First 20 rows of content:");

ahmetsData.slice(0, 20).forEach((row, index) => {
    if (row.some(cell => cell !== '')) {
        console.log(`Row ${index + 1}:`, row.slice(0, 8).join(' | '));
    }
});

// Look for specific sections
console.log("\n=== LOOKING FOR KEY SECTIONS ===");
ahmetsData.forEach((row, index) => {
    const firstCell = row[0] ? row[0].toString().toLowerCase() : '';
    if (firstCell.includes('load') || firstCell.includes('roi') || firstCell.includes('ahmed') || firstCell.includes('cost')) {
        console.log(`Row ${index + 1} (Key):`, row.slice(0, 6).join(' | '));
    }
});

import * as XLSX from 'xlsx';

// Reload and examine just the structure first
const trainerToolkitData = await window.fs.readFile('trainer resource toolkit  Module 1 Workbook.xlsx');
const workbook = XLSX.read(trainerToolkitData);

console.log("TRAINER RESOURCE TOOLKIT - Sheet Structure:");
console.log("Sheets:", workbook.SheetNames);

// For each sheet, get a few key cells to understand the content
workbook.SheetNames.forEach(sheetName => {
    console.log(`\n--- ${sheetName} ---`);
    const sheet = workbook.Sheets[sheetName];

    // Get A1, A2, A3, B1, B2, B3 to understand headers
    const keyCells = ['A1', 'A2', 'A3', 'B1', 'B2', 'B3', 'C1', 'D1'];
    keyCells.forEach(cellAddr => {
        const cell = sheet[cellAddr];
        if (cell && cell.v) {
            console.log(`  ${cellAddr}: ${cell.v}`);
        }
    });
});

// Let's try the enhanced workbook instead, which might be simpler
import * as XLSX from 'xlsx';

const enhancedData = await window.fs.readFile('Iraqi_Solar_Training_ENHANCED_Workbook.xlsx');
const enhancedWorkbook = XLSX.read(enhancedData);

console.log("=== IRAQI SOLAR TRAINING ENHANCED WORKBOOK ===");
console.log("Sheet names:", enhancedWorkbook.SheetNames);

// Just get basic info about each sheet
enhancedWorkbook.SheetNames.forEach(sheetName => {
    const sheet = enhancedWorkbook.Sheets[sheetName];
    console.log(`\nSheet: ${sheetName}`);
    console.log(`Range: ${sheet['!ref'] || 'Empty'}`);

    // Get first few cells
    if (sheet['A1']) console.log(`A1: ${sheet['A1'].v}`);
    if (sheet['A2']) console.log(`A2: ${sheet['A2'].v}`);
    if (sheet['B1']) console.log(`B1: ${sheet['B1'].v}`);
});
