function animate() {
    if (!isRunning) return;

    // Physics simulation
    const deltaTime = 1/30; // 30 FPS for smoother movement
    simulationTime += (timeScale * deltaTime);

    // Calculate forces
    const submergedFraction = Math.max(0.3, fillLevel / 100);
    const exposedFraction = 1 - submergedFraction;
    const sailMultiplier = hasSail ? 3.0 : 1;
    const seaAnchorMultiplier = document.getElementById('hasSeaAnchor').checked ? 1.8 : 1;

    // Wind force (westward, but reduced effect)
    const windForceX = -windSpeed * exposedFraction * sailMultiplier * 1.2 * deltaTime;
    const windForceY = windSpeed * exposedFraction * sailMultiplier * 0.3 * deltaTime;

    // Current force (northeastward along coast) - enhanced by sea anchor
    const currentAngle = Math.PI / 4; // 45 degrees from east (more northward)
    const currentForceX = currentSpeed * Math.cos(currentAngle) * submergedFraction * seaAnchorMultiplier * 25 * deltaTime;
    const currentForceY = -currentSpeed * Math.sin(currentAngle) * submergedFraction * seaAnchorMultiplier * 25 * deltaTime;

    // Total movement (much more visible now)
    const moveX = (windForceX + currentForceX);
    const moveY = (windForceY + currentForceY);

    // Apply forces
    bottleX += moveX;
    bottleY += moveY;

    // Keep bottle in bounds
    bottleX = Math.max(10, Math.min(1190, bottleX));
    bottleY = Math.max(10, Math.min(590, bottleY));

    // Update bottle position
    const bottle = document.getElementById('bottle');
    bottle.style.left = bottleX + 'px';
    bottle.style.top = bottleY + 'px';

    // Add to trail more frequently for visibility
    if (trail.length === 0 || Math.sqrt(Math.pow(bottleX - trail[trail.length-1].x, 2) + Math.pow(bottleY - trail[trail.length-1].y, 2)) > 3) {
        trail.push({x: bottleX, y: bottleY});
        if (trail.length > 100) {
            trail.shift();
        }
        updateTrailVisual();
    }

    // Check if reached Gaza area
    const distanceToGaza = Math.sqrt(Math.pow(gazaX - bottleX, 2) + Math.pow(gazaY - bottleY, 2));
    if (distanceToGaza < 35) {
        isRunning = false;
        document.getElementById('playBtn').textContent = 'üéâ Reached Gaza!';
        setTimeout(() => alert('üéâ Success! Package reached the Gaza coastline!'), 100);
        return;
    }

    updatePhysics();
    animationId = requestAnimationFrame(animate);
}

