function updatePhysics() {
    fillLevel = document.getElementById('fillLevel').value;
    bottleSize = document.getElementById('bottleSize').value;
    windSpeed = document.getElementById('windSpeed').value;
    currentSpeed = document.getElementById('currentSpeed').value;
    timeScale = document.getElementById('timeScale').value;
    hasSail = document.getElementById('hasSail').checked;
    const hasSeaAnchor = document.getElementById('hasSeaAnchor').checked;

    document.getElementById('fillDisplay').textContent = fillLevel + '%';
    document.getElementById('sizeDisplay').textContent = bottleSize + 'L';
    document.getElementById('windDisplay').textContent = windSpeed + ' m/s';
    document.getElementById('currentDisplay').textContent = currentSpeed + ' m/s';
    document.getElementById('timeDisplay').textContent = timeScale + ' hr/sec';

    // Calculate wind vs current effect
    const submergedFraction = Math.max(0.3, fillLevel / 100);
    const exposedFraction = 1 - submergedFraction;
    const sailMultiplier = hasSail ? 3.0 : 1;
    const seaAnchorMultiplier = hasSeaAnchor ? 1.8 : 1;

    const windEffect = exposedFraction * sailMultiplier * 0.05;
    const currentEffect = submergedFraction * seaAnchorMultiplier;

    const totalEffect = windEffect + currentEffect;
    const windPercentage = Math.round((windEffect / totalEffect) * 100);
    const currentPercentage = Math.round((currentEffect / totalEffect) * 100);

    document.getElementById('windEffect').textContent = windPercentage + '%';
    document.getElementById('currentEffect').textContent = currentPercentage + '%';

    // Show/hide sail
    document.getElementById('sail').style.display = hasSail ? 'block' : 'none';

    updateInfoPanel();
}

