function updateInfoPanel() {
    // Update current info (generally northward along coast)
    document.getElementById('currentInfo').textContent = `${currentSpeed} m/s NE`;

    // Update wind info (generally westward in summer)
    document.getElementById('windInfo').textContent = `${windSpeed} m/s W`;

    // Calculate effective drift rate
    const submergedFraction = Math.max(0.3, fillLevel / 100);
    const exposedFraction = 1 - submergedFraction;
    const sailMultiplier = hasSail ? 3.0 : 1;
    const seaAnchorMultiplier = document.getElementById('hasSeaAnchor').checked ? 1.8 : 1;

    const windComponent = windSpeed * exposedFraction * sailMultiplier * 0.05;
    const currentComponent = currentSpeed * submergedFraction * seaAnchorMultiplier;
    const totalDrift = Math.sqrt(windComponent*windComponent + currentComponent*currentComponent);

    document.getElementById('driftInfo').textContent = `${totalDrift.toFixed(2)} m/s`;

    // Update time
    const hours = Math.floor(simulationTime);
    const minutes = Math.floor((simulationTime - hours) * 60);
    document.getElementById('timeInfo').textContent = `${hours.toString().padStart(2,'0')}:${minutes.toString().padStart(2,'0')}`;

    // Calculate ETA (very rough estimate)
    if (bottleX > 0 && bottleY > 0) {
        const distanceToGaza = Math.sqrt(Math.pow(gazaX - bottleX, 2) + Math.pow(gazaY - bottleY, 2));
        const pixelsPerKm = 8; // rough scale
        const distanceKm = distanceToGaza / pixelsPerKm;
        const etaDays = (distanceKm * 1000) / (totalDrift * 86400); // seconds to days
        document.getElementById('eta').textContent = `${etaDays.toFixed(1)} days`;
        document.getElementById('distanceInfo').textContent = `${distanceKm.toFixed(1)} km`;
    }
}

