import React from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, Legend } from 'recharts';

const GazaAidCharts = () => {
  const barData = [
    {
      name: 'Gaza Starvation\nLevel',
      bottles: 100000,
      description: '600 cal/person/day'
    },
    {
      name: 'Gaza Recommended\nLevel', 
      bottles: 350000,
      description: '2,100 cal/person/day'
    },
    {
      name: 'Egypt Daily\nSoda Consumption',
      bottles: 11000000,
      description: '11M bottles/day'
    }
  ];

  const pieData = [
    {
      name: 'Gaza Starvation Need',
      value: 0.9,
      bottles: 100000
    },
    {
      name: 'Gaza Recommended Need', 
      value: 2.3,
      bottles: 250000
    },
    {
      name: 'Remaining Egypt Capacity',
      value: 96.8,
      bottles: 10650000
    }
  ];

  const COLORS = ['#ef4444', '#f59e0b', '#10b981'];

  const formatTooltip = (value, name) => {
    if (name === 'bottles') {
      return [`${value.toLocaleString()} bottles/day`, 'Daily Bottles'];
    }
    return [value, name];
  };

  const formatPieTooltip = (value, name, props) => {
    return [
      `${value}% (${props.payload.bottles.toLocaleString()} bottles)`,
      name
    ];
  };

  return (
    <div className="w-full space-y-8 p-6 bg-white">
      <div className="text-center mb-8">
        <h1 className="text-2xl font-bold text-gray-800 mb-2">
          Gaza Aid Logistics: Scale Comparison
        </h1>
        <p className="text-gray-600">
          Daily bottle requirements vs regional consumption capacity
        </p>
      </div>

      {/* Bar Chart */}
      <div className="bg-gray-50 p-6 rounded-lg">
        <h2 className="text-xl font-semibold mb-4 text-gray-800">
          Daily Bottle Requirements Comparison
        </h2>
        <ResponsiveContainer width="100%" height={400}>
          <BarChart data={barData} margin={{ top: 20, right: 30, left: 20, bottom: 60 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis 
              dataKey="name" 
              angle={-45}
              textAnchor="end"
              height={80}
              fontSize={12}
            />
            <YAxis 
              tickFormatter={(value) => `${(value / 1000000).toFixed(1)}M`}
              fontSize={12}
            />
            <Tooltip 
              formatter={formatTooltip}
              labelFormatter={(label) => `Category: ${label.replace('\n', ' ')}`}
              contentStyle={{
                backgroundColor: '#f9fafb',
                border: '1px solid #e5e7eb',
                borderRadius: '6px'
              }}
            />
            <Bar dataKey="bottles" fill="#3b82f6" />
          </BarChart>
        </ResponsiveContainer>
        <div className="mt-4 text-sm text-gray-600">
          <p><strong>Key insight:</strong> Even Gaza's recommended caloric needs represent only 3.2% of Egypt's daily soda bottle consumption.</p>
        </div>
      </div>

      {/* Pie Chart */}
      <div className="bg-gray-50 p-6 rounded-lg">
        <h2 className="text-xl font-semibold mb-4 text-gray-800">
          Gaza Needs as Percentage of Egypt's Soda Production
        </h2>
        <ResponsiveContainer width="100%" height={400}>
          <PieChart>
            <Pie
              data={pieData}
              cx="50%"
              cy="50%"
              outerRadius={120}
              fill="#8884d8"
              dataKey="value"
              label={({name, value}) => `${name}: ${value}%`}
              labelLine={false}
            >
              {pieData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip formatter={formatPieTooltip} />
            <Legend />
          </PieChart>
        </ResponsiveContainer>
        <div className="mt-4 text-sm text-gray-600">
          <p><strong>Scale perspective:</strong> Gaza's starvation-level needs represent less than 1% of regional bottle production capacity.</p>
        </div>
      </div>

      {/* Data Summary */}
      <div className="bg-blue-50 p-6 rounded-lg">
        <h3 className="text-lg font-semibold mb-3 text-gray-800">Data Summary</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
          <div>
            <h4 className="font-medium text-gray-700">Caloric Requirements</h4>
            <ul className="mt-2 space-y-1 text-gray-600">
              <li>• Starvation level: 600 cal/person/day</li>
              <li>• Recommended: 2,100 cal/person/day</li>
              <li>• PlumpyNut: 12,000 cal per 2L bottle</li>
            </ul>
          </div>
          <div>
            <h4 className="font-medium text-gray-700">Bottle Calculations</h4>
            <ul className="mt-2 space-y-1 text-gray-600">
              <li>• Gaza starvation need: 100k bottles/day</li>
              <li>• Gaza recommended: 350k bottles/day</li>
              <li>• Egypt soda consumption: 11M bottles/day</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GazaAidCharts;
