          onMousemove={handleMouseMove}
          onMouseup={handleMouseUp}
          ref={stageRef}
        >
          <Layer>
            {lines.map((line, i) => (
              <Line
                key={i}
                points={line.points}
                stroke="#333"
                strokeWidth={2}
                tension={0.5}
                lineCap="round"
              />
            ))}
          </Layer>
        </Stage>

        {!readOnly && (
          <button 
            className="clear-drawing"
            onClick={() => setLines([])}
          >
            Clear Drawing
          </button>
