// pages/journals/[slug]/[@username].tsx
import { useRouter } from 'next/router';
import { useState, useEffect } from 'react';
import Canvas from '@/components/journal/Canvas';
import { supabase } from '@/lib/supabase';

export default function UserJournal() {
  const router = useRouter();
  const { slug, username } = router.query;

  const [journal, setJournal] = useState(null);
  const [currentDay, setCurrentDay] = useState(1);
  const [prompt, setPrompt] = useState(null);
  const [entry, setEntry] = useState(null);
  const [saving, setSaving] = useState(false);

  // Calculate current day based on start date
  const calculateCurrentDay = (startedAt) => {
    const start = new Date(startedAt);
    const now = new Date();
    const days = Math.floor((now - start) / (1000 * 60 * 60 * 24)) + 1;
    return days;
  };

  // Load journal data
  useEffect(() => {
    if (!slug || !username) return;

    const loadJournal = async () => {
      // Get user's journal instance
      const { data: userJournal } = await supabase
        .from('user_journals')
        .select(`
          *,
          template:journal_templates(*)
        `)
        .eq('username', username.replace('@', ''))
        .eq('template.slug', slug)
        .single();

      if (!userJournal) {
        // Need to subscribe first
        router.push(`/journals/${slug}`);
        return;
      }

      setJournal(userJournal);

      const day = calculateCurrentDay(userJournal.started_at);
      setCurrentDay(day);

      // Get prompt for current day
      const { data: promptData } = await supabase
        .from('journal_prompts')
        .select('*')
        .eq('template_id', userJournal.template_id)
        .eq('day_number', day)
        .single();

      setPrompt(promptData);

      // Get user's entry if exists
      const { data: entryData } = await supabase
        .from('journal_entries')
        .select('*')
        .eq('user_journal_id', userJournal.id)
        .eq('day_number', day)
        .single();

      setEntry(entryData);
    };

    loadJournal();
  }, [slug, username]);

  // Save entry
  const saveEntry = async (content) => {
    setSaving(true);

    const entryData = {
      user_journal_id: journal.id,
      day_number: currentDay,
      page_number: 1,
      content: content.text,
      drawing_data: content.drawing,
      word_count: content.text.split(/\s+/).length,
      updated_at: new Date()
    };

    if (entry?.id) {
      // Update existing
      await supabase
        .from('journal_entries')
        .update(entryData)
        .eq('id', entry.id);
    } else {
      // Create new
      const { data } = await supabase
        .from('journal_entries')
        .insert(entryData)
        .select()
        .single();

      setEntry(data);
    }

    setSaving(false);
  };

  if (!journal || !prompt) {
    return <div>Loading...</div>;
  }

  const canGoTomorrow = currentDay < calculateCurrentDay(journal.started_at);
  const canGoYesterday = currentDay > 1;

  return (
    <div className="journal-page">
      <header>
        <h1>{journal.template.title}</h1>
        <div className="day-indicator">
          Day {currentDay} / {journal.template.duration_days || '‚àû'}
        </div>
        <div className="date">
          {new Date().toLocaleDateString('en-US', { 
            weekday: 'long',
            year: 'numeric',
            month: 'long',
            day: 'numeric'
          })}
        </div>
      </header>

      <div className="prompt-section">
        <h2>Today's Prompt</h2>
        <p className="prompt-text">
          {prompt.pages[0].prompt}
        </p>
      </div>

      <div className="canvas-section">
        <Canvas
          value={{
            text: entry?.content || '',
            drawing: entry?.drawing_data || []
          }}
          onChange={saveEntry}
          placeholder="Start writing your response..."
        />
        {saving && <div className="saving-indicator">Saving...</div>}
      </div>

      <nav className="day-navigation">
        <button 
          onClick={() => setCurrentDay(currentDay - 1)}
          disabled={!canGoYesterday}
        >
          ‚Üê Yesterday
        </button>

        <button className="share-button">
          Share Entry
        </button>

        <button 
          onClick={() => setCurrentDay(currentDay + 1)}
          disabled={!canGoTomorrow}
        >
          Tomorrow ‚Üí
          {!canGoTomorrow && " (locked)"}
        </button>
      </nav>

      <style jsx>{`
        .journal-page {
          max-width: 900px;
          margin: 0 auto;
          padding: 40px 20px;
        }

        header {
          text-align: center;
          margin-bottom: 40px;
        }

        h1 {
          font-size: 32px;
          font-family: 'Georgia', serif;
          margin-bottom: 10px;
        }

        .day-indicator {
          font-size: 14px;
          color: #666;
          margin-bottom: 5px;
        }

        .date {
          font-size: 16px;
          color: #888;
        }

        .prompt-section {
          background: #f9f9f9;
          padding: 20px;
          border-radius: 8px;
          margin-bottom: 30px;
        }

        .prompt-text {
          font-size: 20px;
          line-height: 1.6;
          font-style: italic;
        }

        .saving-indicator {
          position: fixed;
          top: 20px;
          right: 20px;
          background: #4CAF50;
          color: white;
          padding: 10px 20px;
          border-radius: 4px;
        }

        .day-navigation {
          display: flex;
          justify-content: space-between;
          margin-top: 40px;
        }

        .day-navigation button {
          padding: 10px 20px;
          background: white;
          border: 1px solid #ddd;
          border-radius: 4px;
          cursor: pointer;
          transition: all 0.2s;
        }

        .day-navigation button:hover:not(:disabled) {
          background: #f5f5f5;
        }

        .day-navigation button:disabled {
          opacity: 0.5;
          cursor: not-allowed;
        }

        .share-button {
          background: #007AFF !important;
          color: white !important;
          border: none !important;
        }
      `}</style>
    </div>
  );
}
```

This gives you:
1. **Clean database** structure with templates, prompts, journals, and entries
2. **Simple Canvas** that combines text and drawing with auto-save
3. **Day-based navigation** with locked future days (skeuomorphic like a real journal)
4. **Subscription model** where users get their own journal instance

