import openpyxl
import json

wb = openpyxl.load_workbook('BEIT_Insulation_Master_Workbook_20250811_095642.xlsx')

info = {
    'sheet_names': wb.sheetnames,
    'sheet_details': {}
}

for sheet_name in wb.sheetnames:
    ws = wb[sheet_name]
    info['sheet_details'][sheet_name] = {
        'dimensions': f"{ws.max_row} rows x {ws.max_column} cols",
        'first_10_rows': []
    }

    # Get first 10 rows to understand structure
    for row in range(1, min(11, ws.max_row + 1)):
        row_data = []
        for col in range(1, min(8, ws.max_column + 1)):
            cell = ws.cell(row, col)
            value = str(cell.value) if cell.value else ''
            if len(value) > 50:
                value = value[:50] + '...'
            row_data.append(value)
        info['sheet_details'][sheet_name]['first_10_rows'].append(row_data)

print(json.dumps(info, indent=2, ensure_ascii=False))
EOF

