         ‚îî‚îÄ Page 1

üî• THE MIGRATION
sql-- =====================================================
-- COMPREHENSIVE CLEANUP MIGRATION
-- WARNING: This drops and rebuilds your schema
-- BACKUP YOUR DATABASE FIRST
-- =====================================================

BEGIN;

-- =====================================================
-- STEP 1: DROP ALL LEGACY CRUFT
-- =====================================================

-- Legacy tables to drop
DROP TABLE IF EXISTS public.assignment_analytics CASCADE;
DROP TABLE IF EXISTS public.cohort_invitations CASCADE;
DROP TABLE IF EXISTS public.collections CASCADE;
DROP TABLE IF EXISTS public.contributed_pages CASCADE;
DROP TABLE IF EXISTS public.curation_items CASCADE;
DROP TABLE IF EXISTS public.curations CASCADE;
DROP TABLE IF EXISTS public.email_notifications CASCADE;
DROP TABLE IF EXISTS public.exercise_submissions CASCADE;
DROP TABLE IF EXISTS public.facilitator_notes CASCADE;
DROP TABLE IF EXISTS public.letterbox_items CASCADE;
DROP TABLE IF EXISTS public.notebook_presence CASCADE;
DROP TABLE IF EXISTS public.notifications CASCADE;
DROP TABLE IF EXISTS public.notification_preferences CASCADE;
DROP TABLE IF EXISTS public.page_comments CASCADE;
DROP TABLE IF EXISTS public.page_events CASCADE;
DROP TABLE IF EXISTS public.page_gifts CASCADE;
DROP TABLE IF EXISTS public.page_links CASCADE;
DROP TABLE IF EXISTS public.prompts CASCADE;
DROP TABLE IF EXISTS public.publications CASCADE;
DROP TABLE IF EXISTS public.relationships CASCADE;
DROP TABLE IF EXISTS public.stroke_events CASCADE;
DROP TABLE IF EXISTS public.strokes CASCADE;
DROP TABLE IF EXISTS public.user_book_progress CASCADE;

-- Views to drop
DROP VIEW IF EXISTS public.notebooks_with_user_id CASCADE;
DROP VIEW IF EXISTS public.pages_legacy_view CASCADE;

-- =====================================================
-- STEP 2: DROP UNUSED FUNCTIONS
-- =====================================================

DROP FUNCTION IF EXISTS public.aggregate_user_notifications CASCADE;
DROP FUNCTION IF EXISTS public.can_user_view_gallery CASCADE;
DROP FUNCTION IF EXISTS public.cleanup_expired_events CASCADE;
DROP FUNCTION IF EXISTS public.clear_stale_users CASCADE;
DROP FUNCTION IF EXISTS public.count_started_submissions CASCADE;
DROP FUNCTION IF EXISTS public.create_default_notification_preferences CASCADE;
DROP FUNCTION IF EXISTS public.create_group_invitation CASCADE;
DROP FUNCTION IF EXISTS public.create_notification_for_invitation CASCADE;
DROP FUNCTION IF EXISTS public.ensure_card_thread_consistency CASCADE;
DROP FUNCTION IF EXISTS public.ensure_daily_section_type CASCADE;
DROP FUNCTION IF EXISTS public.ensure_page_schema CASCADE;
DROP FUNCTION IF EXISTS public.expire_old_invitations CASCADE;
DROP FUNCTION IF EXISTS public.generate_conversation_slug CASCADE;
DROP FUNCTION IF EXISTS public.get_next_prompt_for_group CASCADE;
DROP FUNCTION IF EXISTS public.get_node_threads CASCADE;
DROP FUNCTION IF EXISTS public.get_user_books CASCADE;
DROP FUNCTION IF EXISTS public.is_group_facilitator CASCADE;
DROP FUNCTION IF EXISTS public.migrate_conversations_to_nodes CASCADE;
DROP FUNCTION IF EXISTS public.migrate_conversations_to_threads CASCADE;
DROP FUNCTION IF EXISTS public.migrate_pages_to_tabs CASCADE;
DROP FUNCTION IF EXISTS public.notify_host_of_submission CASCADE;
DROP FUNCTION IF EXISTS public.notify_host_on_submission CASCADE;
DROP FUNCTION IF EXISTS public.refresh_assignment_analytics CASCADE;
DROP FUNCTION IF EXISTS public.reorder_guide_prompts CASCADE;
DROP FUNCTION IF EXISTS public.request_feature_submission CASCADE;
DROP FUNCTION IF EXISTS public.respond_to_feature_request CASCADE;
DROP FUNCTION IF EXISTS public.send_next_invitation CASCADE;
DROP FUNCTION IF EXISTS public.touch_exercise_submissions CASCADE;
DROP FUNCTION IF EXISTS public.trigger_update_timestamp CASCADE;
DROP FUNCTION IF EXISTS public.update_blocks_updated_at CASCADE;
DROP FUNCTION IF EXISTS public.update_modified_column CASCADE;
DROP FUNCTION IF EXISTS public.update_page_word_count CASCADE;
DROP FUNCTION IF EXISTS public.update_pages_updated_at CASCADE;
DROP FUNCTION IF EXISTS public.update_stroke_timestamp CASCADE;
DROP FUNCTION IF EXISTS public.update_thread_timestamp CASCADE;
DROP FUNCTION IF EXISTS public.update_user_context CASCADE;
DROP FUNCTION IF EXISTS public.user_has_entity_access CASCADE;

-- Keep these (we'll recreate clean versions):
-- handle_new_user
-- get_or_create_journal_folder
-- get_or_create_group_folder
-- clone_template_section_once
-- publish_journal_range
-- generate_slug
-- get_group_host
-- update_notebook_tabs_updated_at
-- ensure_parent_is_folder
-- update_updated_at_column

-- =====================================================
-- STEP 3: RENAME & CLEAN CORE TABLES
-- =====================================================

-- Rename notebook_tabs ‚Üí folders (we'll split this)
ALTER TABLE IF EXISTS public.notebook_tabs RENAME TO folders_and_sections_temp;

-- Rename pages ‚Üí pages_temp (we'll recreate)
ALTER TABLE IF EXISTS public.pages RENAME TO pages_temp;

-- =====================================================
-- STEP 4: CREATE CLEAN SCHEMA
-- =====================================================

-- Notebooks: one per user
CREATE TABLE public.notebooks (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  owner_id uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  title text NOT NULL DEFAULT 'My Notebook',
  description text,
  is_default boolean DEFAULT true,
  settings jsonb DEFAULT '{"theme": "default"}'::jsonb,
  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now(),

  CONSTRAINT one_default_per_user UNIQUE (owner_id, is_default) WHERE is_default = true
);

CREATE INDEX idx_notebooks_owner ON notebooks(owner_id);

-- Folders: top-level containers (Journal, group folders)
CREATE TABLE public.folders (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  notebook_id uuid NOT NULL REFERENCES notebooks(id) ON DELETE CASCADE,

  title text NOT NULL DEFAULT 'Untitled Folder',
  order_index integer DEFAULT 0,

  -- System folders (Journal)
  is_system_folder boolean DEFAULT false,

  -- Group folders
  source_group_id uuid REFERENCES groups(id) ON DELETE SET NULL,

  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now()
);

CREATE INDEX idx_folders_notebook ON folders(notebook_id, order_index);
CREATE INDEX idx_folders_group ON folders(source_group_id) WHERE source_group_id IS NOT NULL;

-- Sections: things inside folders (daily sections, assignments, custom)
CREATE TABLE public.sections (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  folder_id uuid NOT NULL REFERENCES folders(id) ON DELETE CASCADE,

  title text NOT NULL DEFAULT 'Untitled',
  section_type text DEFAULT 'custom' CHECK (section_type IN ('daily', 'assignment', 'custom')),
  order_index integer DEFAULT 0,

  -- For daily sections
  date date,

  -- For assignments
  assignment_metadata jsonb DEFAULT '{}',

  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now(),

  -- Constraints
  UNIQUE(folder_id, date) WHERE date IS NOT NULL
);

CREATE INDEX idx_sections_folder ON sections(folder_id, order_index);
CREATE INDEX idx_sections_daily ON sections(folder_id, date) WHERE section_type = 'daily';

-- Pages: physical A4 sheets inside sections
CREATE TABLE public.pages (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  section_id uuid NOT NULL REFERENCES sections(id) ON DELETE CASCADE,

  page_number integer NOT NULL DEFAULT 1,
  content jsonb NOT NULL DEFAULT '{"type": "doc", "content": []}'::jsonb,
  metadata jsonb DEFAULT '{}',

  word_count integer DEFAULT 0,
  char_count integer DEFAULT 0,

  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now(),

  UNIQUE(section_id, page_number)
);

CREATE INDEX idx_pages_section ON pages(section_id, page_number);
CREATE INDEX idx_pages_search ON pages USING gin((content::text) gin_trgm_ops);

-- Groups: cohorts running guides
CREATE TABLE public.groups (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  host_id uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  guide_id uuid REFERENCES guides(id) ON DELETE SET NULL,

  name text NOT NULL,
  starts_at date NOT NULL,
  schedule jsonb NOT NULL, -- {frequency: 'weekly', day: 'monday', time: '10:00'}
  timezone text DEFAULT 'UTC',

  join_code text NOT NULL UNIQUE,
  max_members integer,
  is_public boolean DEFAULT false,
  host_participates boolean DEFAULT false,

  vocabulary jsonb DEFAULT '{"unit_singular": "Exercise", "unit_plural": "Exercises"}'::jsonb,
  visibility_mode text DEFAULT 'curated' CHECK (visibility_mode IN ('curated', 'shared')),

  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now()
);

CREATE INDEX idx_groups_host ON groups(host_id);
CREATE INDEX idx_groups_guide ON groups(guide_id);
CREATE INDEX idx_groups_code ON groups(join_code);

-- Group members
CREATE TABLE public.group_members (
  group_id uuid NOT NULL REFERENCES groups(id) ON DELETE CASCADE,
  user_id uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  role text DEFAULT 'member' CHECK (role IN ('member', 'host')),
  joined_at timestamptz DEFAULT now(),
  paid_at timestamptz,
  PRIMARY KEY (group_id, user_id)
);

CREATE INDEX idx_members_user ON group_members(user_id);

-- Guides: templates/curricula
CREATE TABLE public.guides (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  author_id uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  title text NOT NULL,
  description text,
  slug text UNIQUE,

  label_singular text DEFAULT 'Exercise',
  label_plural text DEFAULT 'Exercises',

  navigation_type text DEFAULT 'linear' CHECK (navigation_type IN ('linear', 'gated', 'free')),
  status text DEFAULT 'draft' CHECK (status IN ('draft', 'published')),

  config jsonb DEFAULT '{}',
  schedule jsonb,
  current_version integer DEFAULT 1,

  is_published boolean DEFAULT false,
  published_at timestamptz,

  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now()
);

CREATE INDEX idx_guides_author ON guides(author_id);
CREATE INDEX idx_guides_slug ON guides(slug);
CREATE INDEX idx_guides_published ON guides(is_published, published_at);

-- Guide pages: exercises/prompts in a guide
CREATE TABLE public.guide_pages (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  guide_id uuid NOT NULL REFERENCES guides(id) ON DELETE CASCADE,

  title text NOT NULL,
  content jsonb NOT NULL DEFAULT '{"type": "doc", "content": []}'::jsonb,

  order_index integer NOT NULL,
  requires_completion_of uuid REFERENCES guide_pages(id) ON DELETE SET NULL,
  unlock_condition jsonb,

  added_at timestamptz DEFAULT now(),

  UNIQUE(guide_id, order_index)
);

CREATE INDEX idx_guide_pages_guide ON guide_pages(guide_id, order_index);

-- Mail items: universal inbox
CREATE TABLE public.mail_items (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  recipient_id uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  type text CHECK (type IN ('invitation', 'workbook', 'gift', 'share')),

  -- Source
  source_group_id uuid REFERENCES groups(id) ON DELETE CASCADE,
  source_user_id uuid REFERENCES auth.users(id) ON DELETE CASCADE,

  -- Payload
  payload jsonb DEFAULT '{}', -- {guide_page_id, title, message, due_date}
  scheduled_for date DEFAULT CURRENT_DATE,

  -- State
  status text DEFAULT 'unread' CHECK (status IN ('unread', 'read', 'archived', 'ignored')),
  added_to_notebook boolean DEFAULT false,
  cloned_section_id uuid REFERENCES sections(id) ON DELETE SET NULL,

  created_at timestamptz DEFAULT now(),
  read_at timestamptz,
  added_at timestamptz
);

CREATE INDEX idx_mail_inbox ON mail_items(recipient_id, scheduled_for) 
  WHERE status = 'unread' AND added_to_notebook = false;

-- Snapshots: submissions, publishing
CREATE TABLE public.snapshots (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  section_id uuid NOT NULL REFERENCES sections(id) ON DELETE CASCADE,
  created_by uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  snapshot_type text CHECK (snapshot_type IN ('submission', 'publish', 'backup')),
  version integer DEFAULT 1,

  payload jsonb NOT NULL, -- {pages: [{page_number, content, strokes}], metadata: {...}}
  replay_window jsonb,

  created_at timestamptz DEFAULT now(),

  UNIQUE(section_id, version)
);

CREATE INDEX idx_snapshots_section ON snapshots(section_id, version DESC);
CREATE INDEX idx_snapshots_user ON snapshots(created_by, created_at DESC);

-- Books: published content
CREATE TABLE public.books (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  creator_id uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  title text NOT NULL,
  description text,
  slug text UNIQUE,

  source_type text CHECK (source_type IN ('journal', 'guide', 'custom')),
  source_guide_id uuid REFERENCES guides(id) ON DELETE SET NULL,

  snapshot_data jsonb NOT NULL, -- {sections: [{id, title, pages: [...]}]}

  is_published boolean DEFAULT false,
  published_at timestamptz,

  config jsonb DEFAULT '{}',

  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now()
);

CREATE INDEX idx_books_creator ON books(creator_id);
CREATE INDEX idx_books_guide ON books(source_guide_id);
CREATE INDEX idx_books_published ON books(published_at) WHERE is_published = true;

-- Ink strokes: drawing/handwriting
CREATE TABLE public.ink_strokes (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  page_id uuid NOT NULL REFERENCES pages(id) ON DELETE CASCADE,
  user_id uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  stroke_data jsonb NOT NULL,
  tool_type text CHECK (tool_type IN ('pen', 'marker', 'highlighter', 'eraser')),
  color text,
  width double precision,

  started_at timestamptz NOT NULL,
  ended_at timestamptz NOT NULL,
  created_at timestamptz DEFAULT now()
);

CREATE INDEX idx_strokes_page ON ink_strokes(page_id, started_at);

-- Text events: for replay
CREATE TABLE public.text_events (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  page_id uuid NOT NULL REFERENCES pages(id) ON DELETE CASCADE,
  user_id uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  event_type text CHECK (event_type IN ('insert', 'delete', 'format')),
  position integer NOT NULL,
  content text,
  node_path jsonb,
  metadata jsonb DEFAULT '{}',

  timestamp timestamptz DEFAULT now()
);

CREATE INDEX idx_text_events_page ON text_events(page_id, timestamp);

-- Writing sessions: analytics
CREATE TABLE public.writing_sessions (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  section_id uuid NOT NULL REFERENCES sections(id) ON DELETE CASCADE,

  started_at timestamptz DEFAULT now(),
  ended_at timestamptz,

  page_ids uuid[] DEFAULT '{}',
  total_duration_ms integer DEFAULT 0,
  total_keystrokes integer DEFAULT 0,
  total_words_written integer DEFAULT 0
);

CREATE INDEX idx_sessions_user ON writing_sessions(user_id, started_at DESC);

-- Released prompts: tracking what's been sent
CREATE TABLE public.released_prompts (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  group_id uuid NOT NULL REFERENCES groups(id) ON DELETE CASCADE,
  guide_page_id uuid NOT NULL REFERENCES guide_pages(id) ON DELETE CASCADE,

  sequence_number integer NOT NULL,
  released_at timestamptz DEFAULT now(),
  closes_at timestamptz,

  UNIQUE(group_id, sequence_number)
);

CREATE INDEX idx_released_group ON released_prompts(group_id, sequence_number);

-- Page shares: gallery/visibility
CREATE TABLE public.page_shares (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  page_id uuid NOT NULL REFERENCES pages(id) ON DELETE CASCADE,
  group_id uuid NOT NULL REFERENCES groups(id) ON DELETE CASCADE,
  released_id uuid REFERENCES released_prompts(id) ON DELETE SET NULL,
  user_id uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  shared_at timestamptz DEFAULT now()
);

CREATE INDEX idx_shares_group ON page_shares(group_id, released_id);

-- Section-group links: tracking relationships
CREATE TABLE public.section_group_links (
  section_id uuid NOT NULL REFERENCES sections(id) ON DELETE CASCADE,
  group_id uuid NOT NULL REFERENCES groups(id) ON DELETE CASCADE,
  reason text DEFAULT 'invitation',
  created_at timestamptz DEFAULT now(),
  PRIMARY KEY (section_id, group_id)
);

-- Shared resources: collaboration
CREATE TABLE public.shared_resources (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  resource_type text CHECK (resource_type IN ('notebook', 'folder', 'section')),
  resource_id uuid NOT NULL,
  owner_id uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  shared_with_id uuid NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  permission_level text DEFAULT 'view' CHECK (permission_level IN ('view', 'comment', 'edit')),
  created_at timestamptz DEFAULT now()
);

CREATE INDEX idx_shared_owner ON shared_resources(owner_id);
CREATE INDEX idx_shared_recipient ON shared_resources(shared_with_id);

-- User preferences
CREATE TABLE public.user_preferences (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id uuid NOT NULL UNIQUE REFERENCES auth.users(id) ON DELETE CASCADE,
  preferences jsonb DEFAULT '{"ai_settings": {"response_length": "medium"}, "default_view": "chat"}'::jsonb,
  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now()
);

-- Profiles (keep clean)
CREATE TABLE IF NOT EXISTS public.profiles (
  id uuid PRIMARY KEY REFERENCES auth.users(id) ON DELETE CASCADE,
  email text,
  full_name text,
  avatar_url text,
  username text UNIQUE CHECK (username ~ '^[a-z0-9][a-z0-9-]{2,38}[a-z0-9]$'),
  timezone text DEFAULT 'UTC',
  settings jsonb DEFAULT '{}',
  preferences jsonb DEFAULT '{}',
  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now()
);

-- =====================================================
-- STEP 5: MIGRATE DATA FROM OLD SCHEMA
-- =====================================================

-- Migrate notebooks (clean up fields)
INSERT INTO notebooks (id, owner_id, title, description, is_default, settings, created_at, updated_at)
SELECT id, owner_id, 
  COALESCE(title, 'My Notebook'),
  description,
  COALESCE(is_default, true),
  COALESCE(settings, '{"theme": "default"}'::jsonb),
  created_at,
  updated_at
FROM (SELECT DISTINCT ON (id) * FROM notebooks) n
ON CONFLICT (id) DO NOTHING;

-- Migrate folders (from notebook_tabs where section_type = 'folder')
INSERT INTO folders (id, notebook_id, title, order_index, is_system_folder, source_group_id, created_at, updated_at)
SELECT id, notebook_id, title, order_index, 
  COALESCE(is_system_folder, false),
  source_group_id,
  created_at, updated_at
FROM folders_and_sections_temp
WHERE section_type = 'folder' OR parent_folder_id IS NULL;

-- Migrate sections (from notebook_tabs where section_type != 'folder')
INSERT INTO sections (id, folder_id, title, section_type, order_index, date, assignment_metadata, created_at, updated_at)
SELECT id, parent_folder_id, title, section_type, order_index, date,
  COALESCE(assignment_metadata, '{}'::jsonb),
  created_at, updated_at
FROM folders_and_sections_temp
WHERE section_type != 'folder' AND parent_folder_id IS NOT NULL;

-- Migrate pages
INSERT INTO pages (id, section_id, page_number, content, metadata, word_count, char_count, created_at, updated_at)
SELECT p.id, p.tab_id, p.page_number,
  COALESCE(p.content, '{"type": "doc", "content": []}'::jsonb),
  COALESCE(p.metadata, '{}'::jsonb),
  COALESCE(p.word_count, 0),
  COALESCE(p.char_count, 0),
  p.created_at, p.updated_at
FROM pages_temp p
WHERE EXISTS (SELECT 1 FROM sections WHERE id = p.tab_id);

-- Clean up temp tables
DROP TABLE IF EXISTS folders_and_sections_temp CASCADE;
DROP TABLE IF EXISTS pages_temp CASCADE;

-- =====================================================
-- STEP 6: CORE FUNCTIONS
-- =====================================================

-- Auto-create notebook on signup
CREATE OR REPLACE FUNCTION public.handle_new_user()
RETURNS TRIGGER
SECURITY DEFINER
SET search_path = public
LANGUAGE plpgsql
AS $$
DECLARE
  v_notebook_id uuid;
  v_journal_id uuid;
BEGIN
  -- Create profile
  INSERT INTO profiles (id, email, full_name)
  VALUES (NEW.id, NEW.email, NEW.raw_user_meta_data->>'full_name')
  ON CONFLICT (id) DO NOTHING;

  -- Create default notebook
  INSERT INTO notebooks (owner_id, title, is_default)
  VALUES (NEW.id, 'My Notebook', true)
  RETURNING id INTO v_notebook_id;

  -- Create Journal folder
  INSERT INTO folders (notebook_id, title, is_system_folder, order_index)
  VALUES (v_notebook_id, 'Journal', true, 0)
  RETURNING id INTO v_journal_id;

  RETURN NEW;
END;
$$;

DROP TRIGGER IF EXISTS on_auth_user_created ON auth.users;
CREATE TRIGGER on_auth_user_created
  AFTER INSERT ON auth.users
  FOR EACH ROW
  EXECUTE FUNCTION handle_new_user();

-- Get or create journal folder
CREATE OR REPLACE FUNCTION public.get_or_create_journal_folder(p_notebook_id uuid)
RETURNS uuid
SECURITY INVOKER
LANGUAGE plpgsql
AS $$
DECLARE
  v_folder_id uuid;
BEGIN
  SELECT id INTO v_folder_id
  FROM folders
  WHERE notebook_id = p_notebook_id
    AND is_system_folder = true
    AND title = 'Journal'
  LIMIT 1;

  IF v_folder_id IS NULL THEN
    INSERT INTO folders (notebook_id, title, is_system_folder, order_index)
    VALUES (p_notebook_id, 'Journal', true, 0)
    RETURNING id INTO v_folder_id;
  END IF;

  RETURN v_folder_id;
END;
$$;

-- Get or create group folder
CREATE OR REPLACE FUNCTION public.get_or_create_group_folder(
  p_notebook_id uuid,
  p_group_id uuid,
  p_group_name text
)
RETURNS uuid
SECURITY INVOKER
LANGUAGE plpgsql
AS $$
DECLARE
  v_folder_id uuid;
BEGIN
  SELECT id INTO v_folder_id
  FROM folders
  WHERE notebook_id = p_notebook_id
    AND source_group_id = p_group_id
  LIMIT 1;

  IF v_folder_id IS NULL THEN
    INSERT INTO folders (
      notebook_id,
      title,
      source_group_id,
      order_index
    )
    VALUES (
      p_notebook_id,
      p_group_name,
      p_group_id,
      (SELECT COALESCE(MAX(order_index), 0) + 1 FROM folders WHERE notebook_id = p_notebook_id)
    )
    RETURNING id INTO v_folder_id;
  END IF;

  RETURN v_folder_id;
END;
$$;

-- Get or create daily section (idempotent)
CREATE OR REPLACE FUNCTION public.get_or_create_daily_section(
  p_notebook_id uuid,
  p_date date
)
RETURNS uuid
SECURITY INVOKER
LANGUAGE plpgsql
AS $$
DECLARE
  v_journal_id uuid;
  v_section_id uuid;
  v_page_id uuid;
  v_title text;
BEGIN
  -- Ensure journal exists
  v_journal_id := get_or_create_journal_folder(p_notebook_id);

  -- Check if section exists
  SELECT id INTO v_section_id
  FROM sections
  WHERE folder_id = v_journal_id
    AND date = p_date
    AND section_type = 'daily';

  IF v_section_id IS NULL THEN
    -- Create section
    v_title := to_char(p_date, 'Mon DD, YYYY');

    INSERT INTO sections (
      folder_id,
      title,
      section_type,
      date,
      order_index
    )
    VALUES (
      v_journal_id,
      v_title,
      'daily',
      p_date,
      (SELECT COALESCE(MAX(order_index), 0) + 1 FROM sections WHERE folder_id = v_journal_id)
    )
    RETURNING id INTO v_section_id;

    -- Create first page
    INSERT INTO pages (section_id, page_number, content)
    VALUES (v_section_id, 1, '{"type": "doc", "content": []}')
    RETURNING id INTO v_page_id;
  END IF;

  RETURN v_section_id;
END;
$$;

-- Clone template (for assignments)
CREATE OR REPLACE FUNCTION public.clone_guide_page_to_section(
  p_guide_page_id uuid,
  p_folder_id uuid,
  p_title text,
  p_metadata jsonb DEFAULT '{}'
)
RETURNS uuid
SECURITY INVOKER
LANGUAGE plpgsql
AS $$
DECLARE
  v_section_id uuid;
  v_content jsonb;
BEGIN
  -- Get guide page content
  SELECT content INTO v_content
  FROM guide_pages
  WHERE id = p_guide_page_id;

  IF v_content IS NULL THEN
    RAISE EXCEPTION 'Guide page not found';
  END IF;

  -- Create section
  INSERT INTO sections (
    folder_id,
    title,
    section_type,
    assignment_metadata,
    order_index
  )
  VALUES (
    p_folder_id,
    p_title,
    'assignment',
    jsonb_build_object('guide_page_id', p_guide_page_id) || p_metadata,
    (SELECT COALESCE(MAX(order_index), 0) + 1 FROM sections WHERE folder_id = p_folder_id)
  )
  RETURNING id INTO v_section_id;

  -- Create page with content
  INSERT INTO pages (section_id, page_number, content)
  VALUES (v_section_id, 1, v_content);

  RETURN v_section_id;
END;
$$;

-- Publish journal range as book
CREATE OR REPLACE FUNCTION public.publish_journal_range(
  p_notebook_id uuid,
  p_start_date date,
  p_end_date date,
  p_creator_id uuid,
  p_title text DEFAULT NULL
)
RETURNS uuid
SECURITY INVOKER
LANGUAGE plpgsql
AS $$
DECLARE
  v_book_id uuid;
  v_journal_id uuid;
  v_sections jsonb := '[]'::jsonb;
  v_section RECORD;
  v_pages jsonb;
BEGIN
  -- Get journal folder
  v_journal_id := get_or_create_journal_folder(p_notebook_id);

  -- Collect all daily sections in range
  FOR v_section IN
    SELECT id, title, date
    FROM sections
    WHERE folder_id = v_journal_id
      AND date BETWEEN p_start_date AND p_end_date
      AND section_type = 'daily'
    ORDER BY date
  LOOP
    -- Get pages for this section
    SELECT jsonb_agg(
      jsonb_build_object(
        'page_number', page_number,
        'content', content,
        'word_count', word_count
      ) ORDER BY page_number
    ) INTO v_pages
    FROM pages
    WHERE section_id = v_section.id;

    -- Add section to snapshot
    v_sections := v_sections || jsonb_build_object(
      'id', v_section.id,
      'title', v_section.title,
      'date', v_section.date,
      'pages', COALESCE(v_pages, '[]'::jsonb)
    );
  END LOOP;

  -- Create book
  INSERT INTO books (
    creator_id,
    title,
    source_type,
    snapshot_data,
    is_published,
    published_at
  ) VALUES (
    p_creator_id,
    COALESCE(p_title, 'Journal: ' || to_char(p_start_date, 'Mon DD') || ' - ' || to_char(p_end_date, 'Mon DD, YYYY')),
    'journal',
    jsonb_build_object('sections', v_sections),
    true,
    now()
  )
  RETURNING id INTO v_book_id;

  RETURN v_book_id;
END;
$$;

-- Update timestamps
CREATE OR REPLACE FUNCTION public.update_updated_at_column()
RETURNS TRIGGER
LANGUAGE plpgsql
AS $$
BEGIN
  NEW.updated_at = now();
  RETURN NEW;
END;
$$;

-- Update word count (using JSONB, not JSONPath)
CREATE OR REPLACE FUNCTION public.update_page_word_count()
RETURNS TRIGGER
LANGUAGE plpgsql
AS $$
DECLARE
  v_text text;
  v_word_count integer;
  v_char_count integer;
BEGIN
  -- Extract text from TipTap JSON using JSONB operators
  v_text := regexp_replace(NEW.content::text, '<[^>]+>', '', 'g');
  v_text := regexp_replace(v_text, '[{}":\[\]]', '', 'g');

  v_char_count := length(v_text);
  v_word_count := array_length(regexp_split_to_array(trim(v_text), '\s+'), 1);

  NEW.word_count := COALESCE(v_word_count, 0);
  NEW.char_count := v_char_count;

  RETURN NEW;
END;
$$;

