import { createServerSupabaseClient } from '@supabase/auth-helpers-nextjs'
import { NextApiRequest, NextApiResponse } from 'next'

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  const supabase = createServerSupabaseClient({ req, res })

  const { data: { user } } = await supabase.auth.getUser()
  if (!user) return res.status(401).json({ error: 'Unauthorized' })

  try {
    // 1. Get default notebook
    const { data: notebook } = await supabase
      .from('notebooks')
      .select('id')
      .eq('owner_id', user.id)
      .eq('is_default', true)
      .single()

    if (!notebook) {
      return res.status(404).json({ error: 'No default notebook' })
    }

    // 2. Get or create today's section
    const { data: sectionId, error: sectionError } = await supabase
      .rpc('get_or_create_daily_section', {
        p_notebook_id: notebook.id,
        p_date: new Date().toISOString().split('T')[0]
      })

    if (sectionError) throw sectionError

    // 3. Get section with pages
    const { data: section } = await supabase
      .from('sections')
      .select('*, pages(*)')
      .eq('id', sectionId)
      .single()

    // 4. Get today's mail (inline)
    const { data: mail } = await supabase
      .from('today_mail_items')
      .select('*')
      .eq('recipient_id', user.id)

    res.json({
      section,
      mail: mail || []
    })
  } catch (error) {
    console.error('Error in /today:', error)
    res.status(500).json({ error: 'Internal server error' })
  }
}
