sql-- Send a page as a letter/gift
CREATE OR REPLACE FUNCTION public.send_page_letter(
  p_page_id uuid,
  p_sender_id uuid,
  p_recipient_id uuid,
  p_message text DEFAULT NULL
)
RETURNS uuid
LANGUAGE plpgsql
AS $$
DECLARE
  v_mail_id uuid;
  v_page_content jsonb;
  v_page_title text;
BEGIN
  -- Get page content
  SELECT 
    content,
    (SELECT title FROM folders WHERE id = pages.folder_id)
  INTO v_page_content, v_page_title
  FROM pages
  WHERE id = p_page_id;

  -- Create mail item
  INSERT INTO mail_items (
    recipient_id,
    type,
    source_user_id,
    payload,
    scheduled_for,
    status
  ) VALUES (
    p_recipient_id,
    'letter',
    p_sender_id,
    jsonb_build_object(
      'page_id', p_page_id,
      'page_title', v_page_title,
      'page_content', v_page_content,
      'message', p_message
    ),
    CURRENT_DATE,
    'unread'
  )
  RETURNING id INTO v_mail_id;

  RETURN v_mail_id;
END;
$$;
