-- Update word count (JSONB only, no JSONPath)
CREATE OR REPLACE FUNCTION public.update_page_word_count()
RETURNS TRIGGER
LANGUAGE plpgsql
AS $$
DECLARE
  v_text text;
  v_word_count integer;
  v_char_count integer;
BEGIN
  -- Extract text using JSONB operators with recursive CTE
  WITH RECURSIVE extract_text(val) AS (
    SELECT NEW.content
    UNION ALL
    SELECT elem
    FROM extract_text, jsonb_array_elements(
      CASE 
        WHEN jsonb_typeof(val) = 'array' THEN val
        WHEN jsonb_typeof(val) = 'object' AND val ? 'content' THEN val->'content'
        ELSE '[]'::jsonb
      END
    ) AS elem
  )
  SELECT string_agg(elem->>'text', ' ')
  INTO v_text
  FROM extract_text, jsonb_array_elements_text(
    CASE WHEN jsonb_typeof(val) = 'object' AND val ? 'text' 
         THEN jsonb_build_array(val->>'text')
         ELSE '[]'::jsonb 
    END
  ) AS elem;

  v_char_count := COALESCE(length(v_text), 0);
  v_word_count := COALESCE(
    array_length(
      regexp_split_to_array(trim(COALESCE(v_text, '')), '\s+'),
      1
    ),
    0
  );

  NEW.word_count := v_word_count;
  NEW.char_count := v_char_count;

  RETURN NEW;
END;
$$;

-- Ensure page schema (JSONB operators)
CREATE OR REPLACE FUNCTION public.ensure_page_schema()
RETURNS TRIGGER
LANGUAGE plpgsql
AS $$
BEGIN
  -- Ensure content is an object
  IF jsonb_typeof(NEW.content) IS DISTINCT FROM 'object' THEN
    NEW.content := '{"type":"doc","content":[]}'::jsonb;
  END IF;

  -- Ensure type = doc
  IF (NEW.content->>'type') IS DISTINCT FROM 'doc' THEN
    NEW.content := jsonb_set(NEW.content, '{type}', to_jsonb('doc'));
  END IF;

  -- Ensure content array exists
  IF NOT (NEW.content ? 'content') OR jsonb_typeof(NEW.content->'content') <> 'array' THEN
    NEW.content := jsonb_set(NEW.content, '{content}', '[]'::jsonb);
  END IF;

  RETURN NEW;
END;
$$;

