-- Send a page as a letter
CREATE OR REPLACE FUNCTION public.send_page_letter(
  p_page_id uuid,
  p_sender_id uuid,
  p_recipient_id uuid,
  p_message text DEFAULT NULL
)
RETURNS uuid
LANGUAGE plpgsql
SECURITY INVOKER
AS $$
DECLARE
  v_mail_id uuid;
  v_page_content jsonb;
  v_section_title text;
BEGIN
  -- Get page content and section title
  SELECT 
    p.content,
    nt.title
  INTO v_page_content, v_section_title
  FROM pages p
  JOIN notebook_tabs nt ON nt.id = p.folder_id
  WHERE p.id = p_page_id;

  IF v_page_content IS NULL THEN
    RAISE EXCEPTION 'Page not found';
  END IF;

  -- Create mail item
  INSERT INTO mail_items (
    recipient_id,
    type,
    source_user_id,
    payload,
    scheduled_for,
    status
  ) VALUES (
    p_recipient_id,
    'letter',
    p_sender_id,
    jsonb_build_object(
      'page_id', p_page_id,
      'section_title', v_section_title,
      'page_content', v_page_content,
      'message', p_message,
      'sender_name', (SELECT full_name FROM profiles WHERE id = p_sender_id)
    ),
    CURRENT_DATE,
    'unread'
  )
  RETURNING id INTO v_mail_id;

  RETURN v_mail_id;
END;
$$;

-- Add letter to my notebook
CREATE OR REPLACE FUNCTION public.add_letter_to_notebook(
  p_mail_id uuid,
  p_notebook_id uuid,
  p_folder_id uuid DEFAULT NULL
)
RETURNS uuid
LANGUAGE plpgsql
SECURITY INVOKER
AS $$
DECLARE
  v_mail_item RECORD;
  v_section_id uuid;
  v_page_id uuid;
BEGIN
  -- Get mail item
  SELECT * INTO v_mail_item
  FROM mail_items
  WHERE id = p_mail_id;

  IF NOT FOUND THEN
    RAISE EXCEPTION 'Mail item not found';
  END IF;

  -- Create section in specified folder (or Journal)
  IF p_folder_id IS NULL THEN
    p_folder_id := get_or_create_journal_folder(p_notebook_id);
  END IF;

  INSERT INTO notebook_tabs (
    notebook_id,
    parent_folder_id,
    title,
    section_type
  ) VALUES (
    p_notebook_id,
    p_folder_id,
    'Letter: ' || (v_mail_item.payload->>'section_title'),
    'custom'
  )
  RETURNING id INTO v_section_id;

  -- Create page with letter content
  INSERT INTO pages (
    folder_id,
    page_number,
    content
  ) VALUES (
    v_section_id,
    1,
    v_mail_item.payload->'page_content'
  )
  RETURNING id INTO v_page_id;

  -- Mark mail as added
  UPDATE mail_items
  SET 
    added_to_notebook = true,
    notebook_section_id = v_section_id,
    added_at = now(),
    status = 'read'
  WHERE id = p_mail_id;

  RETURN v_section_id;
END;
$$;

-- =====================================================
-- STEP 5: ASSIGNMENT SNAPSHOTS (For Submissions)
-- =====================================================

CREATE TABLE IF NOT EXISTS public.assignment_snapshots (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  section_id uuid NOT NULL REFERENCES notebook_tabs(id) ON DELETE CASCADE,
  created_by uuid NOT NULL REFERENCES auth.users(id),

  version integer DEFAULT 1,
  snapshot_type text DEFAULT 'submission' CHECK (snapshot_type IN ('submission', 'draft', 'revision')),

  -- Snapshot payload: pages + metadata
  payload jsonb NOT NULL,

  -- Optional: replay window for showing writing process
  replay_window jsonb,

  metadata jsonb DEFAULT '{}',
  created_at timestamptz DEFAULT now(),

  UNIQUE(section_id, version)
);

CREATE INDEX idx_snapshots_section ON assignment_snapshots(section_id, version DESC);
CREATE INDEX idx_snapshots_user ON assignment_snapshots(created_by, created_at DESC);

-- Submit function
CREATE OR REPLACE FUNCTION public.submit_assignment(
  p_section_id uuid,
  p_user_id uuid,
  p_include_replay boolean DEFAULT false
)
RETURNS uuid
LANGUAGE plpgsql
SECURITY INVOKER
AS $$
DECLARE
  v_snapshot_id uuid;
  v_pages jsonb;
  v_strokes jsonb;
  v_replay jsonb;
  v_version integer;
BEGIN
  -- Get next version
  SELECT COALESCE(MAX(version), 0) + 1
  INTO v_version
  FROM assignment_snapshots
  WHERE section_id = p_section_id;

  -- Snapshot all pages
  SELECT jsonb_agg(
    jsonb_build_object(
      'page_number', page_number,
      'content', content,
      'word_count', word_count,
      'created_at', created_at,
      'updated_at', updated_at
    ) ORDER BY page_number
  )
  INTO v_pages
  FROM pages
  WHERE folder_id = p_section_id;

  -- Snapshot ink strokes
  SELECT jsonb_agg(
    jsonb_build_object(
      'page_id', page_id,
      'stroke_data', stroke_data,
      'started_at', started_at,
      'ended_at', ended_at
    ) ORDER BY started_at
  )
  INTO v_strokes
  FROM ink_strokes
  WHERE page_id IN (SELECT id FROM pages WHERE folder_id = p_section_id);

  -- Optional: replay data
  IF p_include_replay THEN
    SELECT jsonb_agg(
      jsonb_build_object(
        'page_id', page_id,
        'event_type', event_type,
        'position', position,
        'content', content,
        'timestamp', timestamp
      ) ORDER BY timestamp
    )
    INTO v_replay
    FROM text_events
    WHERE page_id IN (SELECT id FROM pages WHERE folder_id = p_section_id);
  END IF;

  -- Create snapshot
  INSERT INTO assignment_snapshots (
    section_id,
    created_by,
    version,
    snapshot_type,
    payload,
    replay_window
  ) VALUES (
    p_section_id,
    p_user_id,
    v_version,
    'submission',
    jsonb_build_object(
      'pages', COALESCE(v_pages, '[]'::jsonb),
      'strokes', COALESCE(v_strokes, '[]'::jsonb),
      'submitted_at', now()
    ),
    CASE WHEN p_include_replay THEN
      jsonb_build_object('events', COALESCE(v_replay, '[]'::jsonb))
    ELSE NULL END
  )
  RETURNING id INTO v_snapshot_id;

  RETURN v_snapshot_id;
END;
$$;

-- =====================================================
-- STEP 6: TRIGGERS (Clean & Safe)
-- =====================================================

DROP TRIGGER IF EXISTS trigger_update_word_count ON pages;
CREATE TRIGGER trigger_update_word_count
  BEFORE INSERT OR UPDATE OF content ON pages
  FOR EACH ROW
  EXECUTE FUNCTION update_page_word_count();

DROP TRIGGER IF EXISTS trigger_ensure_page_schema ON pages;
CREATE TRIGGER trigger_ensure_page_schema
  BEFORE INSERT OR UPDATE ON pages
  FOR EACH ROW
  EXECUTE FUNCTION ensure_page_schema();

DROP TRIGGER IF EXISTS trigger_update_pages_updated_at ON pages;
CREATE TRIGGER trigger_update_pages_updated_at
  BEFORE UPDATE ON pages
  FOR EACH ROW
  EXECUTE FUNCTION update_updated_at();

DROP TRIGGER IF EXISTS trigger_update_notebook_tabs_updated_at ON notebook_tabs;
CREATE TRIGGER trigger_update_notebook_tabs_updated_at
  BEFORE UPDATE ON notebook_tabs
  FOR EACH ROW
  EXECUTE FUNCTION update_updated_at();

-- =====================================================
-- STEP 7: RLS POLICIES (Secure)
-- =====================================================

ALTER TABLE pages ENABLE ROW LEVEL SECURITY;
ALTER TABLE mail_items ENABLE ROW LEVEL SECURITY;
ALTER TABLE assignment_snapshots ENABLE ROW LEVEL SECURITY;

-- Pages: owner via notebook
DROP POLICY IF EXISTS "Users own pages" ON pages;
CREATE POLICY "Users own pages" ON pages
  FOR ALL USING (
    EXISTS (
      SELECT 1 FROM notebook_tabs nt
      JOIN notebooks n ON n.id = nt.notebook_id
      WHERE nt.id = pages.folder_id AND n.owner_id = auth.uid()
    )
  )
  WITH CHECK (
    EXISTS (
      SELECT 1 FROM notebook_tabs nt
      JOIN notebooks n ON n.id = nt.notebook_id
      WHERE nt.id = pages.folder_id AND n.owner_id = auth.uid()
    )
  );

-- Mail: recipient only
DROP POLICY IF EXISTS "Users see their mail" ON mail_items;
CREATE POLICY "Users see their mail" ON mail_items
  FOR SELECT USING (recipient_id = auth.uid());

DROP POLICY IF EXISTS "Users update their mail" ON mail_items;
CREATE POLICY "Users update their mail" ON mail_items
  FOR UPDATE USING (recipient_id = auth.uid())
  WITH CHECK (recipient_id = auth.uid());

-- Snapshots: creator or group host
DROP POLICY IF EXISTS "Users access snapshots" ON assignment_snapshots;
CREATE POLICY "Users access snapshots" ON assignment_snapshots
  FOR SELECT USING (
    created_by = auth.uid() OR
    EXISTS (
      SELECT 1 FROM section_group_links sgl
      JOIN groups g ON g.id = sgl.group_id
      WHERE sgl.section_id = assignment_snapshots.section_id
        AND g.host_id = auth.uid()
    )
  );

-- =====================================================
-- STEP 8: INDEXES
-- =====================================================

-- Already applied in your performance migration
-- Just ensure these exist:
CREATE INDEX IF NOT EXISTS idx_pages_folder_page ON pages(folder_id, page_number);
CREATE INDEX IF NOT EXISTS idx_mail_today ON mail_items(recipient_id, scheduled_for) 
  WHERE status = 'unread' AND added_to_notebook = false;

-- =====================================================
-- STEP 9: VIEWS FOR CONVENIENCE
-- =====================================================

CREATE OR REPLACE VIEW today_inbox AS
SELECT
  m.id,
  m.type,
  m.recipient_id,
  m.source_group_id,
  g.name AS group_name,
  m.payload,
  m.scheduled_for,
  m.created_at
FROM mail_items m
LEFT JOIN groups g ON g.id = m.source_group_id
WHERE m.scheduled_for = CURRENT_DATE
  AND m.added_to_notebook = false
  AND m.status = 'unread';

COMMIT;

-- =====================================================
