-- =====================================================
-- STEP 3: CREATE CLEAN SCHEMA
-- =====================================================

-- Profiles (keep clean, add fields if needed)
CREATE TABLE IF NOT EXISTS public.profiles (
  id UUID PRIMARY KEY REFERENCES auth.users(id) ON DELETE CASCADE,
  email TEXT,
  full_name TEXT,
  avatar_url TEXT,
  username TEXT UNIQUE CHECK (username ~ '^[a-z0-9][a-z0-9-]{2,38}[a-z0-9]$'),
  timezone TEXT DEFAULT 'UTC',
  settings JSONB DEFAULT '{}',
  preferences JSONB DEFAULT '{}',
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Notebooks: one per user, personal workspace
CREATE TABLE public.notebooks (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  owner_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  title TEXT NOT NULL DEFAULT 'My Notebook',
  description TEXT,
  is_default BOOLEAN DEFAULT TRUE,
  settings JSONB DEFAULT '{"theme": "default"}'::jsonb,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW(),

  CONSTRAINT one_default_per_user UNIQUE (owner_id, is_default) 
    WHERE is_default = TRUE
);

-- Folders: top-level containers (Journal, group folders)
CREATE TABLE public.folders (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  notebook_id UUID NOT NULL REFERENCES notebooks(id) ON DELETE CASCADE,

  title TEXT NOT NULL DEFAULT 'Untitled Folder',
  order_index INTEGER DEFAULT 0,

  -- System folders (Journal)
  is_system_folder BOOLEAN DEFAULT FALSE,

  -- Group folders
  source_group_id UUID REFERENCES groups(id) ON DELETE SET NULL,

  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Sections: containers inside folders (daily, assignment, custom)
CREATE TABLE public.sections (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  folder_id UUID NOT NULL REFERENCES folders(id) ON DELETE CASCADE,

  title TEXT NOT NULL DEFAULT 'Untitled',
  section_type TEXT DEFAULT 'custom' 
    CHECK (section_type IN ('daily', 'assignment', 'custom')),
  order_index INTEGER DEFAULT 0,

  -- For daily sections
  date DATE,

  -- For assignments (single source of truth)
  assignment_metadata JSONB DEFAULT '{}',
  /* Structure:
  {
    "guide_page_id": "uuid",
    "status": "pending" | "begun" | "submitted" | "reviewed",
    "due_at": "2025-10-27T23:59:59Z",
    "mail_item_id": "uuid",
    "snooze_until": "2025-10-20T09:00:00Z",
    "pin": false,
    "submitted_at": "2025-10-16T14:30:00Z"
  }
  */

  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW(),

  -- Constraints
  UNIQUE(folder_id, date) WHERE date IS NOT NULL
);

-- Pages: physical A4 sheets inside sections
CREATE TABLE public.pages (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  section_id UUID NOT NULL REFERENCES sections(id) ON DELETE CASCADE,

  page_number INTEGER NOT NULL DEFAULT 1,
  content JSONB NOT NULL DEFAULT '{"type": "doc", "content": []}'::jsonb,
  metadata JSONB DEFAULT '{}',

  word_count INTEGER DEFAULT 0,
  char_count INTEGER DEFAULT 0,

  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW(),

  UNIQUE(section_id, page_number)
);

-- Groups: salons/cohorts running guides
CREATE TABLE public.groups (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  host_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  guide_id UUID REFERENCES guides(id) ON DELETE SET NULL,

  name TEXT NOT NULL,
  starts_at DATE NOT NULL,
  schedule JSONB NOT NULL, 
  -- { "frequency": "weekly", "day": "monday", "time": "10:00" }
  timezone TEXT DEFAULT 'UTC',

  join_code TEXT NOT NULL UNIQUE,
  max_members INTEGER,
  is_public BOOLEAN DEFAULT FALSE,
  host_participates BOOLEAN DEFAULT FALSE,

  vocabulary JSONB DEFAULT '{
    "unit_singular": "Exercise", 
    "unit_plural": "Exercises"
  }'::jsonb,
  visibility_mode TEXT DEFAULT 'curated' 
    CHECK (visibility_mode IN ('curated', 'shared')),

  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Group members
CREATE TABLE public.group_members (
  group_id UUID NOT NULL REFERENCES groups(id) ON DELETE CASCADE,
  user_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  role TEXT DEFAULT 'member' CHECK (role IN ('member', 'host')),
  joined_at TIMESTAMPTZ DEFAULT NOW(),
  paid_at TIMESTAMPTZ,
  PRIMARY KEY (group_id, user_id)
);

-- Guides: templates/curricula for groups
CREATE TABLE public.guides (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  author_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  title TEXT NOT NULL,
  description TEXT,
  slug TEXT UNIQUE,

  vocabulary JSONB DEFAULT '{
    "unit_singular": "Exercise",
    "unit_plural": "Exercises"
  }'::jsonb,

  navigation_type TEXT DEFAULT 'linear' 
    CHECK (navigation_type IN ('linear', 'gated', 'free')),
  status TEXT DEFAULT 'draft' CHECK (status IN ('draft', 'published')),

  config JSONB DEFAULT '{}',
  schedule JSONB,
  current_version INTEGER DEFAULT 1,

  is_published BOOLEAN DEFAULT FALSE,
  published_at TIMESTAMPTZ,

  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Guide pages: exercises/prompts within a guide
CREATE TABLE public.guide_pages (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  guide_id UUID NOT NULL REFERENCES guides(id) ON DELETE CASCADE,

  title TEXT NOT NULL,
  content JSONB NOT NULL DEFAULT '{"type": "doc", "content": []}'::jsonb,

  order_index INTEGER NOT NULL,
  requires_completion_of UUID REFERENCES guide_pages(id) ON DELETE SET NULL,
  unlock_condition JSONB,

  added_at TIMESTAMPTZ DEFAULT NOW(),

  UNIQUE(guide_id, order_index)
);

-- Mail items: universal inbox (staging area)
CREATE TABLE public.mail_items (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  recipient_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  type TEXT CHECK (type IN ('invitation', 'workbook', 'gift', 'share')),

  -- Source
  source_group_id UUID REFERENCES groups(id) ON DELETE CASCADE,
  source_user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,

  -- Payload
  payload JSONB DEFAULT '{}',
  /* Structure:
  {
    "guide_page_id": "uuid",
    "title": "Cave Allegory",
    "message": "Looking forward to your thoughts!",
    "due_at": "2025-10-27T23:59:59Z",
    "group_name": "Philosophy 101"
  }
  */
  scheduled_for DATE DEFAULT CURRENT_DATE,

  -- State
  status TEXT DEFAULT 'unread' 
    CHECK (status IN ('unread', 'read', 'archived', 'ignored')),
  added_to_notebook BOOLEAN DEFAULT FALSE,
  cloned_section_id UUID REFERENCES sections(id) ON DELETE SET NULL,

  created_at TIMESTAMPTZ DEFAULT NOW(),
  read_at TIMESTAMPTZ,
  added_at TIMESTAMPTZ
);

-- Snapshots: frozen submissions
CREATE TABLE public.snapshots (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  section_id UUID NOT NULL REFERENCES sections(id) ON DELETE CASCADE,
  created_by UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  snapshot_type TEXT CHECK (snapshot_type IN ('submission', 'publish', 'backup')),
  version INTEGER DEFAULT 1,

  payload JSONB NOT NULL,
  /* Structure:
  {
    "section": { "id": "uuid", "title": "..." },
    "pages": [
      {
        "page_number": 1,
        "content": {...},
        "word_count": 523
      }
    ],
    "snapshot_created_at": "2025-10-16T14:30:00Z"
  }
  */
  replay_window JSONB,
  /* Structure:
  {
    "events": [
      {
        "event_type": "insert",
        "position": 10,
        "content": "hello",
        "timestamp": "2025-10-16T10:05:23Z"
      }
    ]
  }
  */

  created_at TIMESTAMPTZ DEFAULT NOW(),

  UNIQUE(section_id, version)
);

-- Books: published content (journals, guides)
CREATE TABLE public.books (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  creator_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  title TEXT NOT NULL,
  description TEXT,
  slug TEXT UNIQUE,

  source_type TEXT CHECK (source_type IN ('journal', 'guide', 'custom')),
  source_guide_id UUID REFERENCES guides(id) ON DELETE SET NULL,

  snapshot_data JSONB NOT NULL,
  /* Structure:
  {
    "sections": [
      {
        "id": "uuid",
        "title": "Oct 16, 2025",
        "pages": [...]
      }
    ]
  }
  */

  is_published BOOLEAN DEFAULT FALSE,
  published_at TIMESTAMPTZ,

  config JSONB DEFAULT '{}',

  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
