-- =====================================================

-- Handle new user signup
CREATE OR REPLACE FUNCTION public.handle_new_user()
RETURNS TRIGGER
SECURITY DEFINER
SET search_path = public
LANGUAGE plpgsql
AS $$
DECLARE
  v_notebook_id UUID;
  v_journal_id UUID;
BEGIN
  -- Create profile
  INSERT INTO profiles (id, email, full_name)
  VALUES (
    NEW.id, 
    NEW.email, 
    NEW.raw_user_meta_data->>'full_name'
  )
  ON CONFLICT (id) DO NOTHING;

  -- Create default notebook
  INSERT INTO notebooks (owner_id, title, is_default)
  VALUES (NEW.id, 'My Notebook', TRUE)
  RETURNING id INTO v_notebook_id;

  -- Create Journal folder
  INSERT INTO folders (
    notebook_id, 
    title, 
    is_system_folder, 
    order_index
  )
  VALUES (v_notebook_id, 'Journal', TRUE, 0)
  RETURNING id INTO v_journal_id;

  RETURN NEW;
END;
$$;

-- Get or create journal folder
CREATE OR REPLACE FUNCTION public.get_or_create_journal_folder(
  p_notebook_id UUID
)
RETURNS UUID
SECURITY INVOKER
LANGUAGE plpgsql
AS $$
DECLARE
  v_folder_id UUID;
BEGIN
  SELECT id INTO v_folder_id
  FROM folders
  WHERE notebook_id = p_notebook_id
    AND is_system_folder = TRUE
    AND title = 'Journal'
  LIMIT 1;

  IF v_folder_id IS NULL THEN
    INSERT INTO folders (
      notebook_id, 
      title, 
      is_system_folder, 
      order_index
    )
    VALUES (p_notebook_id, 'Journal', TRUE, 0)
    RETURNING id INTO v_folder_id;
  END IF;

  RETURN v_folder_id;
END;
$$;

-- Get or create group folder (idempotent)
CREATE OR REPLACE FUNCTION public.get_or_create_group_folder(
  p_notebook_id UUID,
  p_group_id UUID,
  p_group_name TEXT
)
RETURNS UUID
SECURITY INVOKER
LANGUAGE plpgsql
AS $$
DECLARE
  v_folder_id UUID;
BEGIN
  SELECT id INTO v_folder_id
  FROM folders
  WHERE notebook_id = p_notebook_id
    AND source_group_id = p_group_id
  LIMIT 1;

  IF v_folder_id IS NULL THEN
    INSERT INTO folders (
      notebook_id,
      title,
      source_group_id,
      order_index
    )
    VALUES (
      p_notebook_id,
      p_group_name,
      p_group_id,
      (SELECT COALESCE(MAX(order_index), 0) + 1 
       FROM folders 
       WHERE notebook_id = p_notebook_id)
    )
    RETURNING id INTO v_folder_id;
  END IF;

  RETURN v_folder_id;
END;
$$;

-- Get or create daily section (idempotent)
CREATE OR REPLACE FUNCTION public.get_or_create_daily_section(
  p_notebook_id UUID,
  p_date DATE
)
RETURNS UUID
SECURITY INVOKER
LANGUAGE plpgsql
AS $$
DECLARE
  v_journal_id UUID;
  v_section_id UUID;
  v_page_id UUID;
  v_title TEXT;
BEGIN
  -- Ensure journal exists
  v_journal_id := get_or_create_journal_folder(p_notebook_id);

  -- Check if section exists
  SELECT id INTO v_section_id
  FROM sections
  WHERE folder_id = v_journal_id
    AND date = p_date
    AND section_type = 'daily';

  IF v_section_id IS NULL THEN
    -- Create section
    v_title := to_char(p_date, 'Mon DD, YYYY');

    INSERT INTO sections (
      folder_id,
      title,
      section_type,
      date,
      order_index
    )
    VALUES (
      v_journal_id,
      v_title,
      'daily',
      p_date,
      (SELECT COALESCE(MAX(order_index), 0) + 1 
       FROM sections 
       WHERE folder_id = v_journal_id)
    )
    RETURNING id INTO v_section_id;

    -- Create first page
    INSERT INTO pages (section_id, page_number, content)
    VALUES (v_section_id, 1, '{"type": "doc", "content": []}')
    RETURNING id INTO v_page_id;
  END IF;

  RETURN v_section_id;
END;
$$;

-- Clone guide page to section (idempotent)
CREATE OR REPLACE FUNCTION public.clone_guide_page_to_section(
  p_guide_page_id UUID,
  p_folder_id UUID,
  p_title TEXT,
  p_metadata JSONB DEFAULT '{}'
)
RETURNS UUID
SECURITY INVOKER
LANGUAGE plpgsql
AS $$
DECLARE
  v_section_id UUID;
  v_content JSONB;
BEGIN
  -- GUARD: Check if already cloned
  SELECT id INTO v_section_id
  FROM sections
  WHERE folder_id = p_folder_id
    AND assignment_metadata->>'guide_page_id' = p_guide_page_id::text;

  IF v_section_id IS NOT NULL THEN
    RETURN v_section_id; -- Already exists
  END IF;

  -- Get guide page content
  SELECT content INTO v_content
  FROM guide_pages
  WHERE id = p_guide_page_id;

  IF v_content IS NULL THEN
    RAISE EXCEPTION 'Guide page not found: %', p_guide_page_id;
  END IF;

  -- Create section
  INSERT INTO sections (
    folder_id,
    title,
    section_type,
    assignment_metadata,
    order_index
  )
  VALUES (
    p_folder_id,
    p_title,
    'assignment',
    jsonb_build_object('guide_page_id', p_guide_page_id) || p_metadata,
    (SELECT COALESCE(MAX(order_index), 0) + 1 
     FROM sections 
     WHERE folder_id = p_folder_id)
  )
  RETURNING id INTO v_section_id;

  -- Create page with content
  INSERT INTO pages (section_id, page_number, content)
  VALUES (v_section_id, 1, v_content);

  RETURN v_section_id;
END;
$$;

-- Create submission snapshot
CREATE OR REPLACE FUNCTION public.create_submission_snapshot(
  p_section_id UUID,
  p_user_id UUID,
  p_include_replay BOOLEAN DEFAULT FALSE
)
RETURNS UUID
SECURITY INVOKER
LANGUAGE plpgsql
AS $$
DECLARE
  v_snapshot_id UUID;
  v_version INTEGER;
  v_pages JSONB;
  v_strokes JSONB;
  v_events JSONB;
BEGIN
  -- Get next version
  SELECT COALESCE(MAX(version), 0) + 1 INTO v_version
  FROM snapshots
  WHERE section_id = p_section_id;

  -- Snapshot all pages
  SELECT jsonb_agg(
    jsonb_build_object(
      'page_number', page_number,
      'content', content,
      'word_count', word_count,
      'created_at', created_at,
      'updated_at', updated_at
    ) ORDER BY page_number
  ) INTO v_pages
  FROM pages
  WHERE section_id = p_section_id;

  -- Optional: Snapshot strokes
  IF p_include_replay THEN
    SELECT jsonb_agg(
      jsonb_build_object(
        'page_id', page_id,
        'stroke_data', stroke_data,
        'started_at', started_at,
        'ended_at', ended_at
      ) ORDER BY started_at
    ) INTO v_strokes
    FROM ink_strokes
    WHERE page_id IN (
      SELECT id FROM pages WHERE section_id = p_section_id
    );

    SELECT jsonb_agg(
      jsonb_build_object(
        'page_id', page_id,
        'event_type', event_type,
        'position', position,
        'content', content,
        'timestamp', timestamp
      ) ORDER BY timestamp
    ) INTO v_events
    FROM text_events
    WHERE page_id IN (
      SELECT id FROM pages WHERE section_id = p_section_id
    );
  END IF;

  -- Create snapshot
  INSERT INTO snapshots (
    section_id,
    created_by,
    version,
    snapshot_type,
    payload,
    replay_window
  ) VALUES (
    p_section_id,
    p_user_id,
    v_version,
    'submission',
    jsonb_build_object(
      'pages', COALESCE(v_pages, '[]'::jsonb),
      'strokes', COALESCE(v_strokes, '[]'::jsonb),
      'submitted_at', NOW()
    ),
    CASE WHEN p_include_replay THEN
      jsonb_build_object('events', COALESCE(v_events, '[]'::jsonb))
    ELSE NULL END
  )
  RETURNING id INTO v_snapshot_id;

  -- Update section status
  UPDATE sections
  SET assignment_metadata = assignment_metadata || 
    jsonb_build_object(
      'status', 'submitted',
      'submitted_at', NOW()
    )
  WHERE id = p_section_id;

  RETURN v_snapshot_id;
END;
$$;

-- Publish journal range as book
CREATE OR REPLACE FUNCTION public.publish_journal_range(
  p_notebook_id UUID,
  p_start_date DATE,
  p_end_date DATE,
  p_creator_id UUID,
  p_title TEXT DEFAULT NULL
)
RETURNS UUID
SECURITY INVOKER
LANGUAGE plpgsql
AS $$
DECLARE
  v_book_id UUID;
  v_journal_id UUID;
  v_sections JSONB := '[]'::jsonb;
  v_section RECORD;
  v_pages JSONB;
BEGIN
  -- Get journal folder
  v_journal_id := get_or_create_journal_folder(p_notebook_id);

  -- Collect all daily sections in range
  FOR v_section IN
    SELECT id, title, date
    FROM sections
    WHERE folder_id = v_journal_id
      AND date BETWEEN p_start_date AND p_end_date
      AND section_type = 'daily'
    ORDER BY date
  LOOP
    -- Get pages for this section
    SELECT jsonb_agg(
      jsonb_build_object(
        'page_number', page_number,
        'content', content,
        'word_count', word_count
      ) ORDER BY page_number
    ) INTO v_pages
    FROM pages
    WHERE section_id = v_section.id;

    -- Add section to snapshot
    v_sections := v_sections || jsonb_build_object(
      'id', v_section.id,
      'title', v_section.title,
      'date', v_section.date,
      'pages', COALESCE(v_pages, '[]'::jsonb)
    );
  END LOOP;

  -- Create book
  INSERT INTO books (
    creator_id,
    title,
    source_type,
    snapshot_data,
    is_published,
    published_at
  ) VALUES (
    p_creator_id,
    COALESCE(
      p_title, 
      'Journal: ' || to_char(p_start_date, 'Mon DD') || 
      ' - ' || to_char(p_end_date, 'Mon DD, YYYY')
    ),
    'journal',
    jsonb_build_object('sections', v_sections),
    TRUE,
    NOW()
  )
  RETURNING id INTO v_book_id;

  RETURN v_book_id;
END;
$$;

-- Update timestamps
CREATE OR REPLACE FUNCTION public.update_updated_at_column()
RETURNS TRIGGER
LANGUAGE plpgsql
AS $$
BEGIN
  NEW.updated_at = NOW();
  RETURN NEW;
END;
$$;

-- Update word count (JSONB operators only)
CREATE OR REPLACE FUNCTION public.update_page_word_count()
RETURNS TRIGGER
LANGUAGE plpgsql
AS $$
DECLARE
  v_text TEXT;
  v_word_count INTEGER;
  v_char_count INTEGER;
BEGIN
  -- Extract text from TipTap JSON using recursive CTE
  WITH RECURSIVE extract_text(val) AS (
    SELECT NEW.content
    UNION ALL
    SELECT elem
    FROM extract_text, jsonb_array_elements(
      CASE 
        WHEN jsonb_typeof(val) = 'array' THEN val
        WHEN jsonb_typeof(val) = 'object' AND val ? 'content' 
          THEN val->'content'
        ELSE '[]'::jsonb
      END
    ) AS elem
  )
  SELECT string_agg(elem->>'text', ' ')
  INTO v_text
  FROM extract_text, jsonb_array_elements_text(
    CASE 
      WHEN jsonb_typeof(val) = 'object' AND val ? 'text' 
      THEN jsonb_build_array(val->>'text')
      ELSE '[]'::jsonb 
    END
  ) AS elem;

  v_char_count := COALESCE(length(v_text), 0);
  v_word_count := COALESCE(
    array_length(
      regexp_split_to_array(trim(COALESCE(v_text, '')), '\s+'),
      1
    ),
    0
  );

  NEW.word_count := v_word_count;
  NEW.char_count := v_char_count;

  RETURN NEW;
END;
$$;

