  */
  replay_window JSONB,
  /* Structure:
  {
    "events": [
      {
        "event_type": "insert",
        "position": 10,
        "content": "hello",
        "timestamp": "2025-10-16T10:05:23Z"
      }
    ]
  }
  */

  created_at TIMESTAMPTZ DEFAULT NOW(),

  UNIQUE(section_id, version)
);

-- Books: published content (journals, guides)
CREATE TABLE public.books (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  creator_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  title TEXT NOT NULL,
  description TEXT,
  slug TEXT UNIQUE,

  source_type TEXT CHECK (source_type IN ('journal', 'guide', 'custom')),
  source_guide_id UUID REFERENCES guides(id) ON DELETE SET NULL,

  snapshot_data JSONB NOT NULL,
  /* Structure:
  {
    "sections": [
      {
        "id": "uuid",
        "title": "Oct 16, 2025",
        "pages": [...]
      }
    ]
  }
  */

  is_published BOOLEAN DEFAULT FALSE,
  published_at TIMESTAMPTZ,

  config JSONB DEFAULT '{}',

  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Ink strokes: drawing/handwriting data
CREATE TABLE public.ink_strokes (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  page_id UUID NOT NULL REFERENCES pages(id) ON DELETE CASCADE,
  user_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  stroke_data JSONB NOT NULL,
  /* Structure:
  {
    "points": [[x, y, pressure, timestamp], ...],
    "color": "#000000",
    "width": 2.0,
    "tool": "pen" | "marker" | "highlighter" | "eraser"
  }
  */
  tool_type TEXT CHECK (tool_type IN ('pen', 'marker', 'highlighter', 'eraser')),
  color TEXT,
  width DOUBLE PRECISION,

  started_at TIMESTAMPTZ NOT NULL,
  ended_at TIMESTAMPTZ NOT NULL,
  created_at TIMESTAMPTZ DEFAULT NOW()
);

-- Text events: typing/editing for replay
CREATE TABLE public.text_events (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  page_id UUID NOT NULL REFERENCES pages(id) ON DELETE CASCADE,
  user_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,

  event_type TEXT NOT NULL 
    CHECK (event_type IN ('insert', 'delete', 'format')),
  position INTEGER NOT NULL,
  content TEXT,
  node_path JSONB,
  metadata JSONB DEFAULT '{}',

  timestamp TIMESTAMPTZ DEFAULT NOW()
);

-- Writing sessions: analytics (optional)
CREATE TABLE public.writing_sessions (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  section_id UUID NOT NULL REFERENCES sections(id) ON DELETE CASCADE,

  started_at TIMESTAMPTZ DEFAULT NOW(),
  ended_at TIMESTAMPTZ,

  page_ids UUID[] DEFAULT '{}',
  total_duration_ms INTEGER DEFAULT 0,
  total_keystrokes INTEGER DEFAULT 0,
  total_words_written INTEGER DEFAULT 0
);

-- Released prompts: tracking what's been sent
CREATE TABLE public.released_prompts (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  group_id UUID NOT NULL REFERENCES groups(id) ON DELETE CASCADE,
  guide_page_id UUID NOT NULL REFERENCES guide_pages(id) ON DELETE CASCADE,

  sequence_number INTEGER NOT NULL,
  released_at TIMESTAMPTZ DEFAULT NOW(),
  closes_at TIMESTAMPTZ,

  UNIQUE(group_id, sequence_number)
);

-- Page shares: gallery/visibility tracking
CREATE TABLE public.page_shares (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  page_id UUID NOT NULL REFERENCES pages(id) ON DELETE CASCADE,
  group_id UUID NOT NULL REFERENCES groups(id) ON DELETE CASCADE,
  released_id UUID REFERENCES released_prompts(id) ON DELETE SET NULL,
  user_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  shared_at TIMESTAMPTZ DEFAULT NOW()
);

-- Section-group links: relationship tracking
CREATE TABLE public.section_group_links (
  section_id UUID NOT NULL REFERENCES sections(id) ON DELETE CASCADE,
  group_id UUID NOT NULL REFERENCES groups(id) ON DELETE CASCADE,
  reason TEXT DEFAULT 'invitation',
  created_at TIMESTAMPTZ DEFAULT NOW(),
  PRIMARY KEY (section_id, group_id)
);

-- Shared resources: collaboration (future)
CREATE TABLE public.shared_resources (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  resource_type TEXT 
    CHECK (resource_type IN ('notebook', 'folder', 'section')),
  resource_id UUID NOT NULL,
  owner_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  shared_with_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  permission_level TEXT DEFAULT 'view' 
    CHECK (permission_level IN ('view', 'comment', 'edit')),
  created_at TIMESTAMPTZ DEFAULT NOW()
);

-- User preferences
CREATE TABLE public.user_preferences (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id UUID NOT NULL UNIQUE REFERENCES auth.users(id) ON DELETE CASCADE,
