-- Enable RLS on all tables
ALTER TABLE notebooks ENABLE ROW LEVEL SECURITY;
ALTER TABLE folders ENABLE ROW LEVEL SECURITY;
ALTER TABLE sections ENABLE ROW LEVEL SECURITY;
ALTER TABLE pages ENABLE ROW LEVEL SECURITY;
ALTER TABLE groups ENABLE ROW LEVEL SECURITY;
ALTER TABLE group_members ENABLE ROW LEVEL SECURITY;
ALTER TABLE guides ENABLE ROW LEVEL SECURITY;
ALTER TABLE guide_pages ENABLE ROW LEVEL SECURITY;
ALTER TABLE mail_items ENABLE ROW LEVEL SECURITY;
ALTER TABLE snapshots ENABLE ROW LEVEL SECURITY;
ALTER TABLE books ENABLE ROW LEVEL SECURITY;
ALTER TABLE ink_strokes ENABLE ROW LEVEL SECURITY;
ALTER TABLE text_events ENABLE ROW LEVEL SECURITY;

-- Notebooks
DROP POLICY IF EXISTS "Users own notebooks" ON notebooks;
CREATE POLICY "Users own notebooks" ON notebooks
  FOR ALL USING (owner_id = auth.uid())
  WITH CHECK (owner_id = auth.uid());

-- Folders
DROP POLICY IF EXISTS "Users own folders" ON folders;
CREATE POLICY "Users own folders" ON folders
  FOR ALL USING (
    EXISTS (
      SELECT 1 FROM notebooks 
      WHERE id = folders.notebook_id 
        AND owner_id = auth.uid()
    )
  )
  WITH CHECK (
    EXISTS (
      SELECT 1 FROM notebooks 
      WHERE id = folders.notebook_id 
        AND owner_id = auth.uid()
    )
  );

-- Sections
DROP POLICY IF EXISTS "Users own sections" ON sections;
CREATE POLICY "Users own sections" ON sections
  FOR ALL USING (
    EXISTS (
      SELECT 1 FROM folders f
      JOIN notebooks n ON n.id = f.notebook_id
      WHERE f.id = sections.folder_id 
        AND n.owner_id = auth.uid()
    )
  )
  WITH CHECK (
    EXISTS (
      SELECT 1 FROM folders f
      JOIN notebooks n ON n.id = f.notebook_id
      WHERE f.id = sections.folder_id 
        AND n.owner_id = auth.uid()
    )
  );

-- Pages
DROP POLICY IF EXISTS "Users own pages" ON pages;
CREATE POLICY "Users own pages" ON pages
  FOR ALL USING (
    EXISTS (
      SELECT 1 FROM sections s
      JOIN folders f ON f.id = s.folder_id
      JOIN notebooks n ON n.id = f.notebook_id
      WHERE s.id = pages.section_id 
        AND n.owner_id = auth.uid()
    )
  )
  WITH CHECK (
    EXISTS (
      SELECT 1 FROM sections s
      JOIN folders f ON f.id = s.folder_id
      JOIN notebooks n ON n.id = f.notebook_id
      WHERE s.id = pages.section_id 
        AND n.owner_id = auth.uid()
    )
  );

-- Groups
DROP POLICY IF EXISTS "Users access groups" ON groups;
CREATE POLICY "Users access groups" ON groups
  FOR SELECT USING (
    host_id = auth.uid() OR
    EXISTS (
      SELECT 1 FROM group_members 
      WHERE group_id = groups.id 
        AND user_id = auth.uid()
    )
  );

DROP POLICY IF EXISTS "Hosts manage groups" ON groups;
CREATE POLICY "Hosts manage groups" ON groups
  FOR ALL USING (host_id = auth.uid())
  WITH CHECK (host_id = auth.uid());

-- Mail
DROP POLICY IF EXISTS "Users see their mail" ON mail_items;
CREATE POLICY "Users see their mail" ON mail_items
  FOR SELECT USING (recipient_id = auth.uid());

DROP POLICY IF EXISTS "Users update their mail" ON mail_items;
CREATE POLICY "Users update their mail" ON mail_items
  FOR UPDATE USING (recipient_id = auth.uid())
  WITH CHECK (recipient_id = auth.uid());

-- Snapshots
DROP POLICY IF EXISTS "Users view own snapshots" ON snapshots;
CREATE POLICY "Users view own snapshots" ON snapshots
  FOR SELECT USING (created_by = auth.uid());

DROP POLICY IF EXISTS "Hosts view member snapshots" ON snapshots;
CREATE POLICY "Hosts view member snapshots" ON snapshots
  FOR SELECT USING (
    EXISTS (
      SELECT 1 FROM sections s
      WHERE s.id = snapshots.section_id
        AND EXISTS (
          SELECT 1 FROM folders f
          JOIN groups g ON g.id = f.source_group_id
          WHERE f.id = s.folder_id
            AND g.host_id = auth.uid()
        )
    )
  );

-- Guides
DROP POLICY IF EXISTS "Anyone views published guides" ON guides;
CREATE POLICY "Anyone views published guides" ON guides
  FOR SELECT USING (
    is_published = TRUE OR author_id = auth.uid()
  );

DROP POLICY IF EXISTS "Authors manage guides" ON guides;
CREATE POLICY "Authors manage guides" ON guides
  FOR ALL USING (author_id = auth.uid())
  WITH CHECK (author_id = auth.uid());

-- =====================================================
-- STEP 8: HELPFUL VIEWS
-- =====================================================

CREATE OR REPLACE VIEW user_notebooks_summary AS
SELECT
  n.id,
  n.title,
  n.owner_id,
  n.is_default,
  COUNT(DISTINCT f.id) AS folder_count,
  COUNT(DISTINCT s.id) AS section_count,
  COUNT(DISTINCT p.id) AS page_count,
  MAX(p.updated_at) AS last_page_update
FROM notebooks n
LEFT JOIN folders f ON f.notebook_id = n.id
LEFT JOIN sections s ON s.folder_id = f.id
LEFT JOIN pages p ON p.section_id = s.id
GROUP BY n.id;

CREATE OR REPLACE VIEW today_mail_items AS
SELECT
  m.id,
  m.recipient_id,
  m.payload->>'title' AS title,
  m.payload->>'message' AS message,
  m.payload->>'guide_page_id' AS guide_page_id,
  m.source_group_id,
  g.name AS group_name,
  m.scheduled_for,
  m.added_to_notebook,
  m.status
FROM mail_items m
LEFT JOIN groups g ON g.id = m.source_group_id
WHERE m.scheduled_for = CURRENT_DATE
  AND m.added_to_notebook = FALSE
  AND m.status = 'unread';

COMMIT;

