// /api/sections/submit.php

if ($_SERVER['REQUEST_METHOD'] !== 'POST') {
  header('HTTP/1.1 405 Method Not Allowed');
  exit('Method Not Allowed');
}

$section_id = $_POST['section_id'] ?? null;
$include_replay = $_POST['include_replay'] === 'true';

