### API: Identify Nature Photo

```typescript
// POST /api/nature/identify

import { OpenAI } from 'openai';

const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY! });

export async function handler(req: Request) {
  const formData = await req.formData();
  const photo = formData.get('photo') as File;
  const gpsLocation = JSON.parse(formData.get('gps_location') as string);
  const userId = formData.get('user_id') as string;
  const pageId = formData.get('page_id') as string;

  // 1. Upload photo to storage
  const photoUrl = await uploadToStorage(photo);

  // 2. Identify using GPT-4V
  const identificationResponse = await openai.chat.completions.create({
    model: 'gpt-4-vision-preview',
    messages: [
      {
        role: 'user',
        content: [
          {
            type: 'text',
            text: `Identify this organism. Provide:
            1. Common name
            2. Scientific name (binomial nomenclature)
            3. Brief ecological context (1-2 sentences)
            4. Confidence level (0.0 - 1.0)

            Respond in JSON format:
            {
              "common_name": "...",
              "scientific_name": "...",
              "ecological_context": "...",
              "confidence": 0.92
            }`
          },
          {
            type: 'image_url',
            image_url: { url: photoUrl }
          }
        ]
      }
    ],
    max_tokens: 500
  });

  const identification = JSON.parse(
    identificationResponse.choices[0].message.content
  );

  // 3. Generate reflection prompt
  const promptResponse = await openai.chat.completions.create({
    model: 'gpt-4',
    messages: [
      {
        role: 'system',
        content: `Generate a philosophical reflection prompt based on a nature observation.
                  Connect the natural phenomenon to human experience.
                  Be poetic, brief (1-2 sentences), thought-provoking.`
      },
      {
        role: 'user',
        content: `Observed: ${identification.common_name} (${identification.scientific_name})
                  Context: ${identification.ecological_context}

                  Generate a reflection prompt.`
      }
    ],
    temperature: 0.8,
    max_tokens: 150
  });

  const reflectionPrompt = promptResponse.choices[0].message.content;

  // 4. Get place name from coordinates (reverse geocoding)
  const placeName = await reverseGeocode(gpsLocation);

  // 5. Save observation
  const { data: observation } = await supabase
    .from('nature_observations')
    .insert({
      user_id: userId,
      page_id: pageId,
      photo_url: photoUrl,
      gps_location: gpsLocation,
      place_name: placeName,
      identified_as: identification.common_name,
      scientific_name: identification.scientific_name,
      ecological_context: identification.ecological_context,
      confidence: identification.confidence,
      reflection_prompt: reflectionPrompt
    })
    .select()
    .single();

  return new Response(JSON.stringify({ observation }));
}

// Helper: Reverse geocode (coordinates ‚Üí place name)
async function reverseGeocode(location: { lat: number; lng: number }) {
  const response = await fetch(
    `https://nominatim.openstreetmap.org/reverse?format=json&lat=${location.lat}&lon=${location.lng}`
  );
  const data = await response.json();
  return data.display_name;
}
```

