### Database Schema

```sql
CREATE TABLE location_boards (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  group_id UUID REFERENCES groups(id) ON DELETE CASCADE,

  name TEXT NOT NULL, -- "Bluestone Caf√© Board"
  description TEXT,

  gps_location JSONB NOT NULL,
  /*
  {
    "lat": 40.7128,
    "lng": -74.0060,
    "radius": 200,
    "place_name": "Bluestone Caf√©",
    "address": "123 Main St, Brooklyn, NY"
  }
  */

  settings JSONB DEFAULT '{}',
  /*
  {
    "post_expiry_days": 30,
    "max_posts_per_user_per_day": 5,
    "requires_approval": false
  }
  */

  created_by UUID REFERENCES auth.users(id),
  created_at TIMESTAMPTZ DEFAULT NOW()
);

CREATE TABLE board_posts (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  board_id UUID NOT NULL REFERENCES location_boards(id) ON DELETE CASCADE,
  user_id UUID NOT NULL REFERENCES auth.users(id),

  content TEXT NOT NULL CHECK (length(content) <= 280), -- Tweet-length

  posted_from_location JSONB, -- Where they were when posting

  expires_at TIMESTAMPTZ,
  created_at TIMESTAMPTZ DEFAULT NOW()
);

CREATE INDEX idx_location_boards_group ON location_boards(group_id);
CREATE INDEX idx_board_posts_board ON board_posts(board_id, created_at DESC);

-- Auto-delete expired posts
CREATE OR REPLACE FUNCTION delete_expired_posts()
RETURNS void
LANGUAGE plpgsql
AS $$
BEGIN
  DELETE FROM board_posts
  WHERE expires_at < NOW();
END;
$$;

-- Schedule daily cleanup (using pg_cron or similar)
SELECT cron.schedule('delete-expired-posts', '0 0 * * *', 
  'SELECT delete_expired_posts()');
```

