### API: Post to Board

```typescript
// POST /api/boards/{id}/posts

export async function handler(req: Request) {
  const { boardId } = req.params;
  const { content, userLocation, userId } = await req.json();

  // 1. Validate content
  if (!content || content.length > 280) {
    return new Response('Content must be 1-280 characters', { 
      status: 400 
    });
  }

  // 2. Get board settings
  const { data: board } = await supabase
    .from('location_boards')
    .select('gps_location, settings')
    .eq('id', boardId)
    .single();

  // 3. Verify location
  const distance = calculateDistance(userLocation, board.gps_location);
  if (distance > (board.gps_location.radius || 100)) {
    return new Response('You must be at the location to post', { 
      status: 403 
    });
  }

  // 4. Check rate limit
  const settings = board.settings || {};
  const maxPostsPerDay = settings.max_posts_per_user_per_day || 5;

  const { count } = await supabase
    .from('board_posts')
    .select('id', { count: 'exact' })
    .eq('board_id', boardId)
    .eq('user_id', userId)
    .gte('created_at', new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString());

  if (count >= maxPostsPerDay) {
    return new Response('Daily post limit reached', { status: 429 });
  }

  // 5. Create post
  const expiryDays = settings.post_expiry_days || 30;
  const expiresAt = new Date(Date.now() + expiryDays * 24 * 60 * 60 * 1000);

  const { data: post } = await supabase
    .from('board_posts')
    .insert({
      board_id: boardId,
      user_id: userId,
      content: content,
      posted_from_location: userLocation,
      expires_at: expiresAt.toISOString()
    })
    .select()
    .single();

  return new Response(JSON.stringify({ post }));
}
```

