### UI: Locked Prompt Card

```tsx
// components/GeolockedPromptCard.tsx

export function GeolockedPromptCard({ prompt }) {
  const [userLocation, setUserLocation] = useState(null);
  const [unlockStatus, setUnlockStatus] = useState(null);
  const [checking, setChecking] = useState(false);

  // Get user location
  const getUserLocation = async () => {
    setChecking(true);

    try {
      const position = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject, {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 0
        });
      });

      const location = {
        lat: position.coords.latitude,
        lng: position.coords.longitude
      };

      setUserLocation(location);

      // Check if can unlock
      const response = await fetch('/api/prompts/check-unlock', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          guidePageId: prompt.id,
          userLocation: location
        })
      });

      const status = await response.json();
      setUnlockStatus(status);

    } catch (error) {
      console.error('Location error:', error);
      alert('Could not get your location. Please enable location services.');
    } finally {
      setChecking(false);
    }
  };

  // Calculate distance for display
  const distance = userLocation && prompt.gps_location
    ? calculateDistance(userLocation, prompt.gps_location)
    : null;

  return (
    <article className="prompt-card prompt-card--geolocked">
      <header className="prompt-card__header">
        <span className="prompt-card__icon" aria-hidden="true">üìç</span>
        <h3>{prompt.title}</h3>
      </header>

      <div className="prompt-card__location">
        <p>
          <strong>Unlocks at:</strong> {prompt.gps_location.place_name}
        </p>
        <p className="text-muted">
          {prompt.gps_location.address}
        </p>

        {distance && (
          <p className="prompt-card__distance">
            {distance > 1000 
              ? `${(distance / 1000).toFixed(1)} km away`
              : `${Math.round(distance)} meters away`}
          </p>
        )}
      </div>

      {!unlockStatus && (
        <div className="prompt-card__actions">
          <button
            onClick={getUserLocation}
            disabled={checking}
            className="btn btn-primary">
            {checking ? 'Checking location...' : 'Unlock Prompt'}
          </button>

          <a href={`https://maps.google.com/?q=${prompt.gps_location.lat},${prompt.gps_location.lng}`}
             target="_blank"
             rel="noopener noreferrer"
             className="btn btn-secondary">
            Get Directions
          </a>
        </div>
      )}

      {unlockStatus?.unlocked && (
        <div className="prompt-card__unlocked">
          <p>‚úì Unlocked! Welcome to {prompt.gps_location.place_name}.</p>
          <a href={`/prompts/${prompt.id}`} 
             className="btn btn-primary">
            Begin Prompt ‚Üí
          </a>
        </div>
      )}

      {unlockStatus && !unlockStatus.unlocked && (
        <div className="prompt-card__locked">
          <p>
            You're {Math.round(unlockStatus.distance_meters)} meters away.
            Get within {unlockStatus.required_radius} meters to unlock.
          </p>
          <button
            onClick={getUserLocation}
            className="btn btn-secondary">
            Check Again
          </button>
        </div>
      )}

      {/* Accessibility bypass */}
      <details className="prompt-card__bypass">
        <summary>Can't visit in person?</summary>
        <p>
          If you have mobility limitations or can't travel to this location,
          contact your curator for alternative access.
        </p>
        <a href={`mailto:curator@example.com?subject=Access request: ${prompt.title}`}
           className="btn btn-ghost btn-sm">
          Request Access
        </a>
      </details>
    </article>
  );
}
```

