  created_at TIMESTAMPTZ DEFAULT NOW()
);

CREATE INDEX idx_nature_obs_user ON nature_observations(user_id);
CREATE INDEX idx_nature_obs_location ON nature_observations 
  USING gist ((gps_location::jsonb));
```

