"""
School Board Meeting Transcript Processor
Batch processing tool for extracting parent quotes from Michigan school board meetings
For CRSLI Report on Public Schooling for African American Students
"""

import os
import json
import csv
from typing import List, Dict, Tuple
from datetime import datetime
from youtube_transcript_api import YouTubeTranscriptApi
from youtube_transcript_api.formatters import TextFormatter
import re
from dataclasses import dataclass
from concurrent.futures import ThreadPoolExecutor, as_completed
import time

@dataclass
class Citation:
    """Proper citation format for quotes"""
    quote: str
    speaker: str  # If identifiable from context
    meeting_date: str
    school_district: str
    video_title: str
    video_url: str
    timestamp: str
    theme: str
    confidence: float  # LLM confidence score if using AI analysis

class SchoolBoardTranscriptProcessor:
    def __init__(self):
        self.formatter = TextFormatter()
        self.districts = {
            'grand_rapids': {
                'name': 'Grand Rapids Public Schools',
                'channel_ids': [],  # To be populated
                'videos': []
            },
            'detroit': {
                'name': 'Detroit Public Schools',
                'channel_ids': [],
                'videos': []
            },
            'lansing': {
                'name': 'Lansing School District',
                'channel_ids': [],
                'videos': []
            },
            'flint': {
                'name': 'Flint Community Schools',
                'channel_ids': [],
                'videos': []
            }
        }

    def get_channel_videos(self, channel_url: str, start_year: int = 2021, end_year: int = 2024) -> List[Dict]:
        """
        Get all video URLs from a YouTube channel within date range
        Note: This requires additional API setup - using YouTube Data API
        """
        # This is a placeholder - you'd need YouTube Data API for full implementation
        # For now, return empty list with structure
        return []

    def extract_video_id(self, url: str) -> str:
        """Extract video ID from YouTube URL"""
        patterns = [
            r'(?:v=|\/)([0-9A-Za-z_-]{11}).*',
            r'(?:embed\/)([0-9A-Za-z_-]{11})',
            r'youtu\.be\/([0-9A-Za-z_-]{11})'
        ]

        for pattern in patterns:
            match = re.search(pattern, url)
            if match:
                return match.group(1)
        return None

    def process_video(self, video_url: str, district_name: str) -> Dict:
        """Process a single video and extract relevant quotes"""
        video_id = self.extract_video_id(video_url)
        if not video_id:
            return {"error": "Invalid URL", "url": video_url}

        try:
            # Get transcript
            transcript_list = YouTubeTranscriptApi.get_transcript(video_id)

            # Extract quotes by theme
            quotes = self.extract_themed_quotes(transcript_list, video_url, district_name)

            return {
                'video_id': video_id,
                'url': video_url,
                'district': district_name,
                'quotes': quotes,
                'processed_date': datetime.now().isoformat()
            }

        except Exception as e:
            return {
                'error': str(e),
                'url': video_url,
                'district': district_name
            }

    def extract_themed_quotes(self, transcript_segments: List[Dict], video_url: str, district: str) -> List[Citation]:
        """
        Extract quotes matching Noor's specific themes with proper citations
        """
        theme_patterns = {
            'academic_achievement_gaps': {
                'keywords': ['achievement gap', 'academic performance', 'test scores', 
                            'falling behind', 'grade level', 'learning loss', 'struggling students'],
                'phrases': ['our students are behind', 'gaps in achievement', 'not meeting standards']
            },
            'equity': {
                'keywords': ['equity', 'equal opportunity', 'fair', 'disparity', 'disadvantaged'],
                'phrases': ['all students deserve', 'equitable resources', 'level playing field']
            },
            'black_students_rural': {
                'keywords': ['rural', 'transportation', 'isolated', 'countryside'],
                'phrases': ['black students in rural', 'African American students outside the city']
            },
            'black_teacher_representation': {
                'keywords': ['black teacher', 'African American educator', 'diversity', 'representation'],
                'phrases': ['need more black teachers', 'teachers who look like our students']
            },
            'parent_involvement': {
                'keywords': ['parent', 'family', 'involvement', 'engagement', 'participate'],
                'phrases': ['parents feel', 'families are involved', 'parent voice']
            },
            'community': {
                'keywords': ['community', 'neighborhood', 'local', 'together'],
                'phrases': ['community support', 'neighborhood schools', 'working together']
            },
            'neighborhood_concerns': {
                'keywords': ['violence', 'safety', 'crime', 'streets', 'after school'],
                'phrases': ['unsafe neighborhoods', 'students face', 'community problems']
            },
            'socioeconomic_issues': {
                'keywords': ['poverty', 'income', 'homeless', 'hungry', 'free lunch', 'economic'],
                'phrases': ['can\'t afford', 'financial struggle', 'low income families']
            }
        }

        citations = []

        # Combine segments into searchable chunks with context
        for i in range(len(transcript_segments)):
            # Get 3-segment context window
            start_idx = max(0, i - 1)
            end_idx = min(len(transcript_segments), i + 2)

            context_segments = transcript_segments[start_idx:end_idx]
            context_text = ' '.join([s['text'] for s in context_segments])

            # Check each theme
            for theme, patterns in theme_patterns.items():
                found = False

                # Check keywords
                for keyword in patterns['keywords']:
                    if keyword.lower() in context_text.lower():
                        found = True
                        break

                # Check phrases
                if not found:
                    for phrase in patterns['phrases']:
                        if phrase.lower() in context_text.lower():
                            found = True
                            break

                if found:
                    # Create citation
                    citation = Citation(
                        quote=context_text.strip(),
                        speaker="Parent/Community Member",  # Would need parsing to identify
                        meeting_date="[Extract from video title]",
                        school_district=district,
                        video_title="[Extract from YouTube]",
                        video_url=video_url,
                        timestamp=self.seconds_to_timestamp(transcript_segments[i]['start']),
                        theme=theme.replace('_', ' ').title(),
                        confidence=0.8  # Placeholder - would be from LLM analysis
                    )
                    citations.append(citation)

        return citations

    def seconds_to_timestamp(self, seconds: float) -> str:
        """Convert seconds to HH:MM:SS format"""
        hours = int(seconds // 3600)
        minutes = int((seconds % 3600) // 60)
        secs = int(seconds % 60)

        if hours > 0:
            return f"{hours:02d}:{minutes:02d}:{secs:02d}"
        return f"{minutes:02d}:{secs:02d}"

    def batch_process_videos(self, video_urls: List[Tuple[str, str]], max_workers: int = 5) -> List[Dict]:
        """
        Process multiple videos in parallel
        video_urls: List of (url, district_name) tuples
        """
        results = []

        with ThreadPoolExecutor(max_workers=max_workers) as executor:
            future_to_video = {
                executor.submit(self.process_video, url, district): (url, district) 
                for url, district in video_urls
            }

            for future in as_completed(future_to_video):
                video_info = future_to_video[future]
                try:
                    result = future.result()
                    results.append(result)
                    print(f"✓ Processed: {video_info[0]}")
                except Exception as e:
                    print(f"✗ Failed: {video_info[0]} - {str(e)}")
                    results.append({
                        'error': str(e),
                        'url': video_info[0],
                        'district': video_info[1]
                    })

                # Rate limiting
                time.sleep(1)

        return results

    def export_to_csv(self, results: List[Dict], filename: str = "school_board_quotes.csv"):
        """Export all citations to CSV for easy review"""
        with open(filename, 'w', newline='', encoding='utf-8') as csvfile:
            fieldnames = ['theme', 'quote', 'speaker', 'school_district', 
                         'meeting_date', 'timestamp', 'video_url', 'confidence']
            writer = csv.DictWriter(csvfile, fieldnames=fieldnames)

            writer.writeheader()
            for result in results:
                if 'quotes' in result:
                    for citation in result['quotes']:
                        writer.writerow({
                            'theme': citation.theme,
                            'quote': citation.quote,
                            'speaker': citation.speaker,
                            'school_district': citation.school_district,
                            'meeting_date': citation.meeting_date,
                            'timestamp': citation.timestamp,
                            'video_url': citation.video_url,
                            'confidence': citation.confidence
                        })

        print(f"Exported quotes to {filename}")

    def create_summary_report(self, results: List[Dict]) -> Dict:
        """Create summary statistics for the report"""
        summary = {
            'total_videos_processed': len(results),
            'successful_extractions': sum(1 for r in results if 'quotes' in r),
            'failed_extractions': sum(1 for r in results if 'error' in r),
            'quotes_by_theme': {},
            'quotes_by_district': {}
        }

        for result in results:
            if 'quotes' in result:
                for quote in result['quotes']:
                    # Count by theme
                    theme = quote.theme
                    summary['quotes_by_theme'][theme] = summary['quotes_by_theme'].get(theme, 0) + 1

                    # Count by district
                    district = quote.school_district
                    summary['quotes_by_district'][district] = summary['quotes_by_district'].get(district, 0) + 1

        return summary


# Example usage script
if __name__ == "__main__":
    processor = SchoolBoardTranscriptProcessor()

    # Example: Process a list of known video URLs
    # Noor would need to provide these or use YouTube Data API to get them
    sample_videos = [
        ("https://www.youtube.com/watch?v=EXAMPLE1", "Grand Rapids Public Schools"),
        ("https://www.youtube.com/watch?v=EXAMPLE2", "Detroit Public Schools"),
        # Add more videos here
    ]

    print("Starting batch processing...")
    results = processor.batch_process_videos(sample_videos)

    # Export to CSV
    processor.export_to_csv(results)

    # Generate summary
    summary = processor.create_summary_report(results)

    # Save full results
    with open('full_results.json', 'w', encoding='utf-8') as f:
        json.dump({
            'summary': summary,
            'detailed_results': results
        }, f, indent=2, default=str)

    print("\nProcessing Complete!")
    print(f"Total videos: {summary['total_videos_processed']}")
    print(f"Successful: {summary['successful_extractions']}")
    print(f"Failed: {summary['failed_extractions']}")
    print("\nQuotes by theme:")
    for theme, count in summary['quotes_by_theme'].items():
        print(f"  {theme}: {count}")

