app = Flask(__name__)

def extract_video_id(url):
    """Extract video ID from YouTube URL"""
    patterns = [
        r'(?:v=|\/)([0-9A-Za-z_-]{11}).*',
        r'(?:embed\/)([0-9A-Za-z_-]{11})',
        r'youtu\.be\/([0-9A-Za-z_-]{11})'
    ]

    for pattern in patterns:
        match = re.search(pattern, url)
        if match:
            return match.group(1)
    return None

def search_quotes(transcript_segments, themes):
    """Search transcript for theme-related quotes"""
    theme_keywords = {
        'achievement_gaps': ['achievement gap', 'behind', 'struggling', 'test scores', 'falling behind'],
        'equity': ['equity', 'equal', 'fair', 'disparity', 'resources'],
        'rural_black_students': ['rural', 'transportation', 'isolated'],
        'teacher_representation': ['black teacher', 'diversity', 'representation'],
        'parent_involvement': ['parent', 'family', 'involvement', 'participate'],
        'community': ['community', 'neighborhood', 'together', 'support'],
        'safety_concerns': ['safety', 'violence', 'streets', 'after school'],
        'socioeconomic': ['poverty', 'income', 'homeless', 'free lunch', 'afford']
    }

    found_quotes = []

    for i, segment in enumerate(transcript_segments):
        # Get context (3 segments before and after)
        start = max(0, i - 3)
        end = min(len(transcript_segments), i + 4)
        context = ' '.join([s['text'] for s in transcript_segments[start:end]])

        for theme in themes:
            if theme in theme_keywords:
                for keyword in theme_keywords[theme]:
                    if keyword.lower() in context.lower():
                        found_quotes.append({
                            'theme': theme.replace('_', ' ').title(),
                            'quote': context,
                            'timestamp': f"{int(segment['start'] // 60)}:{int(segment['start'] % 60):02d}",
                            'keyword_matched': keyword
                        })
                        break

    return found_quotes

