// src/lib/llm-processor.ts
import Anthropic from '@anthropic-ai/sdk';
import OpenAI from 'openai';

export type LLMProvider = 'anthropic' | 'openai';
export type Model = 'haiku-3-5' | 'sonnet-3-5' | 'opus-3' | 'gpt-4o-mini' | 'gpt-4o' | 'auto';

export interface ProcessingConfig {
  provider: LLMProvider;
  apiKey: string;
  model: Model;
  maxCostLimit?: number;
  confidenceThresholds: {
    high: number;
    medium: number;
    low: number;
  };
}

export interface TranscriptSegment {
  text: string;
  startTime: number;
  endTime: number;
  speakerId?: string;
}

export interface Quote {
  id: string;
  text: string;
  speakerId: string;
  speakerRole?: string;
  speakerName?: string;
  speakerConfidence: number;
  timestamp: {
    start: string;
    end: string;
    youtubeLink: string;
  };
  matchedQuestions: string[];
  relevanceScore: number;
  confidence: {
    overall: number;
    quoteRelevance: number;
    speakerAttribution: number;
    reasoning: string;
  };
  aiAnalysis: {
    summary: string;
    reasoning: string;
    tierProcessed: 'haiku' | 'sonnet' | 'opus';
    processingPath: string[];
  };
}

export class TieredLLMProcessor {
  private anthropic?: Anthropic;
  private openai?: OpenAI;
  private config: ProcessingConfig;
  private costTracker = {
    haiku: { tokens: 0, cost: 0 },
    sonnet: { tokens: 0, cost: 0 },
    opus: { tokens: 0, cost: 0 },
    gpt4oMini: { tokens: 0, cost: 0 },
    gpt4o: { tokens: 0, cost: 0 },
  };

  constructor(config: ProcessingConfig) {
    this.config = config;

    if (config.provider === 'anthropic') {
      this.anthropic = new Anthropic({ apiKey: config.apiKey });
    } else {
      this.openai = new OpenAI({ 
        apiKey: config.apiKey,
        dangerouslyAllowBrowser: true // For client-side usage
      });
    }
  }

  async processTranscript(
    transcript: TranscriptSegment[],
    questions: string[],
    contextVariables: Record<string, string>,
    onProgress?: (progress: ProcessingProgress) => void
  ): Promise<ProcessingResult> {
    // Phase 1: Haiku/GPT-4o-mini broad sweep
    const haikuResults = await this.broadSweep(transcript, questions, contextVariables, onProgress);

    // Phase 2: Intelligent escalation to Sonnet/GPT-4o
    const sonnetQueue = this.buildEscalationQueue(haikuResults);
    const sonnetResults = await this.focusedAnalysis(sonnetQueue, onProgress);

    // Phase 3: Selective Opus verification for ambiguous cases
    const opusQueue = this.identifyOpusCandidates(sonnetResults);
    const opusResults = await this.precisionVerification(opusQueue, onProgress);

    // Phase 4: Speaker lane construction
    const speakerLanes = this.constructSpeakerLanes([
      ...haikuResults.highConfidence,
      ...sonnetResults,
      ...opusResults
    ]);

    return {
      quotes: this.mergeAndDeduplicate(haikuResults, sonnetResults, opusResults),
      speakers: speakerLanes,
      processingStats: this.costTracker,
      totalCost: this.calculateTotalCost()
    };
  }

  private async broadSweep(
    transcript: TranscriptSegment[],
    questions: string[],
    context: Record<string, string>,
    onProgress?: (progress: ProcessingProgress) => void
  ): Promise<HaikuResults> {
    const chunks = this.chunkTranscript(transcript, 300); // 5-minute chunks
    const results: SegmentAnalysis[] = [];

    for (let i = 0; i < chunks.length; i++) {
      const chunk = chunks[i];
      onProgress?.({
        phase: 'haiku',
        current: i + 1,
        total: chunks.length,
        message: `Scanning chunk ${i + 1}/${chunks.length} with Haiku...`
      });

      const prompt = this.buildHaikuPrompt(chunk, questions, context);
      const analysis = await this.callLLM('haiku', prompt);
      results.push(this.parseHaikuResponse(analysis, chunk));
    }

    return this.categorizeHaikuResults(results);
  }

  private buildHaikuPrompt(
    chunk: TranscriptSegment[],
    questions: string[],
    context: Record<string, string>
  ): string {
    const text = chunk.map(s => s.text).join(' ');
    const questionList = questions.map((q, i) => `${i + 1}. ${q}`).join('\n');

    return `You are analyzing a transcript segment from a ${context.meeting_type || 'meeting'} in ${context.location || 'the area'}.

TRANSCRIPT SEGMENT:
${text}

QUESTIONS TO IDENTIFY:
${questionList}

Your task is to QUICKLY SCAN for potential matches. Output JSON:
{
  "has_relevant_content": boolean,
  "confidence": 0-100,
  "potential_speakers": ["role1", "role2"],
  "flagged_segments": [
    {
      "text_snippet": "first 50 chars...",
      "likely_questions": [1, 3],
      "confidence": 0-100
    }
  ],
  "speaker_changes_detected": number
}

Focus on ${context.focus_group || 'relevant'} perspectives. Be liberal in flagging - we'll verify later.`;
  }

  private async focusedAnalysis(
    queue: EscalationItem[],
    onProgress?: (progress: ProcessingProgress) => void
  ): Promise<Quote[]> {
    const results: Quote[] = [];

    for (let i = 0; i < queue.length; i++) {
      const item = queue[i];
      onProgress?.({
        phase: 'sonnet',
        current: i + 1,
        total: queue.length,
        message: `Deep analysis ${i + 1}/${queue.length} with Sonnet...`
      });

      const prompt = this.buildSonnetPrompt(item);
      const analysis = await this.callLLM('sonnet', prompt);
      const quote = this.parseSonnetResponse(analysis, item);

      if (quote.confidence.overall >= this.config.confidenceThresholds.medium) {
        results.push(quote);
      }
    }

    return results;
  }

  private buildSonnetPrompt(item: EscalationItem): string {
    return `Analyze this transcript segment in detail. The initial scan suggested it might contain quotes about: ${item.suggestedQuestions.join(', ')}.

CONTEXT (30 seconds before):
${item.contextBefore}

MAIN SEGMENT:
${item.segment.text}

CONTEXT (30 seconds after):
${item.contextAfter}

Extract:
1. The EXACT quote (with proper boundaries)
2. Speaker identification (look for "as a [role]", "my name is", introductions)
3. Evidence for speaker role
4. Which questions this quote answers and why
5. Confidence scores for each aspect

Output as JSON:
{
  "quote": "exact text",
  "speaker_evidence": {
    "likely_role": "parent/teacher/board_member/administrator/student/community_member/unknown",
    "confidence": 0-100,
    "evidence": ["uses 'my child'", "introduces as teacher"],
    "possible_name": "Jane D. if mentioned"
  },
  "relevance": {
    "matched_questions": ["question text"],
    "confidence": 0-100,
    "reasoning": "why this matches"
  },
  "needs_deeper_analysis": boolean
}`;
  }

  private constructSpeakerLanes(quotes: Quote[]): SpeakerLane[] {
    const speakers = new Map<string, SpeakerInfo>();

    // First pass: identify unique speakers
    for (const quote of quotes) {
      const speakerId = this.identifyOrCreateSpeaker(quote, speakers);
      quote.speakerId = speakerId;
    }

    // Second pass: merge likely duplicates
    this.mergeSimilarSpeakers(speakers);

    // Third pass: calculate confidence and statistics
    return Array.from(speakers.values()).map(speaker => ({
      id: speaker.id,
      displayName: speaker.name || `Speaker ${speaker.id.split('_')[1]}`,
      role: speaker.role,
      roleConfidence: speaker.confidence,
      quotes: quotes.filter(q => q.speakerId === speaker.id),
      firstAppearance: speaker.firstAppearance,
      totalSpeakingTime: speaker.totalTime,
      evidence: speaker.evidence
    }));
  }

  private identifyOrCreateSpeaker(
    quote: Quote,
    speakers: Map<string, SpeakerInfo>
  ): string {
    // Check for explicit identification
    const namePattern = /my name is ([A-Z][a-z]+ [A-Z][a-z]+)/i;
    const nameMatch = quote.text.match(namePattern);

    if (nameMatch) {
      const name = nameMatch[1];
      const existingSpeaker = Array.from(speakers.values()).find(s => s.name === name);
      if (existingSpeaker) return existingSpeaker.id;
    }

    // Check speech patterns and role markers
    for (const [id, speaker] of speakers) {
      const similarity = this.calculateSpeakerSimilarity(quote, speaker);
      if (similarity > 0.7) {
        this.updateSpeakerInfo(speaker, quote);
        return id;
      }
    }

    // Create new speaker
    const newId = `speaker_${speakers.size + 1}`;
    speakers.set(newId, {
      id: newId,
      name: nameMatch?.[1],
      role: quote.speakerRole || 'unknown',
      confidence: quote.speakerConfidence,
      firstAppearance: quote.timestamp.start,
      totalTime: 0,
      evidence: [quote.confidence.reasoning]
    });

    return newId;
  }

  private calculateSpeakerSimilarity(quote: Quote, speaker: SpeakerInfo): number {
    let score = 0;

    // Role match
    if (quote.speakerRole === speaker.role) score += 0.4;

    // Temporal proximity (speakers tend to speak in bursts)
    const timeDiff = this.parseTimestamp(quote.timestamp.start) - this.parseTimestamp(speaker.lastSeen || speaker.firstAppearance);
    if (timeDiff < 120) score += 0.3; // Within 2 minutes

    // Language patterns (simplified - would use more sophisticated NLP in production)
    const commonPhrases = ['equity', 'our children', 'the board', 'as I mentioned'];
    const quoteHasPhrases = commonPhrases.filter(p => quote.text.toLowerCase().includes(p));
    const speakerPhrases = speaker.commonPhrases || [];
    const overlap = quoteHasPhrases.filter(p => speakerPhrases.includes(p)).length;
    score += (overlap / Math.max(quoteHasPhrases.length, 1)) * 0.3;

    return Math.min(score, 1);
  }

