import React, { useState } from 'react';
import { ChevronDown, ChevronUp, DollarSign, Heart, TrendingUp, Users, Shield, AlertCircle } from 'lucide-react';

const InvestmentComparison = () => {
  const [expandedModel, setExpandedModel] = useState(null);
  const [selectedScenario, setSelectedScenario] = useState('stable');

  const models = [
    {
      id: 'waqf',
      name: 'Waqf (Endowment)',
      tagline: 'Forever generosity',
      icon: Heart,
      color: 'emerald',

      mechanics: {
        howItWorks: "Capital is donated permanently. Only investment returns are used, preserving principal forever.",
        moneyFlow: "Donor → Waqf Fund → Invested → Returns → Beneficiaries (循環/循环)",
        ownership: "Belongs to God/community - no individual owns it",
        exitStrategy: "None. Money never comes back. That's the point.",
        timeHorizon: "Perpetual (literally forever)"
      },

      implications: {
        forVentures: [
          "No repayment pressure - pure gift",
          "Receive ongoing support from returns",
          "Must align with waqf purpose/values",
          "Often comes with governance oversight"
        ],
        forInvestors: [
          "100% altruistic - no financial return",
          "Tax deduction (if structured properly)",
          "Spiritual/social returns (مثواب/thawab)",
          "Creates lasting legacy"
        ],
        forBloom: [
          "Becomes 'trustee' (nazir) role",
          "Must preserve capital rigorously",
          "Can charge ~1-2% management fee",
          "High accountability to Shariah compliance"
        ]
      },

      crossJurisdiction: {
        complexity: "High",
        challenges: [
          "Islamic law varies by country",
          "Need separate legal entity per jurisdiction",
          "Shariah board certification required",
          "Inheritance laws may conflict with perpetuity"
        ],
        solutions: [
          "Use UAE/Malaysia as 'home base' (strong waqf laws)",
          "Structure as multiple linked waqfs",
          "Hire Shariah advisor ($5-10K/year)",
          "Use cash waqf (easier than property waqf)"
        ]
      },

      scenarios: {
        stable: {
          investment: 100000,
          returns: [3500, 3500, 3500, 3500, 3500],
          principalEnd: 100000,
          narrative: "Conservative 3.5% annual return from low-risk Islamic investments. Principal never touched."
        },
        growth: {
          investment: 100000,
          returns: [2000, 4000, 5000, 6000, 7000],
          principalEnd: 100000,
          narrative: "Growing returns as portfolio matures and ventures succeed. Principal preserved."
        },
        crisis: {
          investment: 100000,
          returns: [1000, 500, 0, 2000, 3000],
          principalEnd: 100000,
          narrative: "Returns volatile during economic crisis, but principal protected. Long-term perspective essential."
        }
      },

      realExamples: [
        {
          name: "Azhar University Waqf (Egypt)",
          established: "972 CE",
          impact: "1000+ years of continuous education funding",
          key: "Survived empires, wars, revolutions - perpetual by design"
        },
        {
          name: "APIF Islamic Development Bank",
          scale: "$767M portfolio",
          impact: "44 projects across education, health, relief",
          key: "3.5% dividends + massive social returns"
        }
      ],

      bestFor: "Long-term institutional building. When you want impact beyond your lifetime.",
      worstFor: "Immediate liquidity needs. When donors need money back."
    },

    {
      id: 'loan',
      name: 'Loan (Qard/Murabaha)',
      tagline: 'Temporary solidarity',
      icon: DollarSign,
      color: 'blue',

      mechanics: {
        howItWorks: "Capital lent with agreement to repay, with or without modest return (0-10%).",
        moneyFlow: "Lender → Venture → Repayment over time → Back to lender → Repeat",
        ownership: "Lender owns capital, venture owns business",
        exitStrategy: "Built-in: repayment schedule (1-10 years)",
        timeHorizon: "Defined term with clear end date"
      },

      implications: {
        forVentures: [
          "Keep 100% ownership/control",
          "Repayment obligation regardless of success",
          "Can plan around fixed costs",
          "Failure = debt burden (or forgiveness clause)"
        ],
        forInvestors: [
          "0-10% financial return expected",
          "Capital returns on schedule",
          "Can re-lend to create revolving fund",
          "Some risk of default (5-20% typical)"
        ],
        forBloom: [
          "Becomes loan servicer/coordinator",
          "Can charge 1-3% origination fee",
          "Must manage defaults and restructuring",
          "Builds credit history data"
        ]
      },

      crossJurisdiction: {
        complexity: "Medium",
        challenges: [
          "Interest rate laws vary (usury limits)",
          "Enforcement of repayment across borders",
          "Currency fluctuation risk",
          "Different insolvency/bankruptcy rules"
        ],
        solutions: [
          "Use Islamic Murabaha (cost-plus, not interest)",
          "Include currency hedging in loan terms",
          "Social collateral > legal enforcement",
          "Graduated repayment based on revenue"
        ]
      },

      scenarios: {
        stable: {
          investment: 100000,
          returns: [5000, 22000, 22000, 22000, 22000, 22000],
          principalEnd: 0,
          narrative: "5-year loan at 5% return. Year 1: interest only. Years 2-6: principal + interest. Total return: $115K."
        },
        growth: {
          investment: 100000,
          returns: [0, 0, 30000, 40000, 45000],
          principalEnd: 0,
          narrative: "Revenue-share loan: 0% if revenue <$X. Once profitable, accelerated repayment. Total return: $115K over 5 years."
        },
        crisis: {
          investment: 100000,
          returns: [3000, 1000, 0, 15000, 20000, 30000, 31000],
          principalEnd: 0,
          narrative: "Loan restructured during crisis. 2-year grace period. Extended to 7 years. Return drops to 3% APR."
        }
      },

      realExamples: [
        {
          name: "Kiva (Global P2P lending)",
          scale: "$1.7B disbursed, 97% repayment",
          impact: "4M+ borrowers, 0% interest to borrowers",
          key: "Lenders accept currency/default risk for impact"
        },
        {
          name: "The Working World (Coop loans)",
          model: "Repay only from profits",
          impact: "Near-zero defaults despite high-risk borrowers",
          key: "Loan forgiveness if failure - removes extraction"
        }
      ],

      bestFor: "Working capital, equipment, scaling. When venture has revenue but needs liquidity.",
      worstFor: "Early-stage/pre-revenue. High failure rate ventures. Extractive if terms too rigid."
    },

    {
      id: 'equity',
      name: 'Equity Investment',
      tagline: 'Shared upside (& downside)',
      icon: TrendingUp,
      color: 'purple',

      mechanics: {
        howItWorks: "Buy ownership stake (10-40%). Return comes from profit share or exit sale.",
        moneyFlow: "Investor → Venture → Profits shared → OR → Sale/Exit → Investor gets multiple",
        ownership: "Investor owns % of company",
        exitStrategy: "Sale to another investor, acquisition, IPO, or buyback",
        timeHorizon: "5-10 years until exit opportunity"
      },

      implications: {
        forVentures: [
          "No repayment pressure - patient capital",
          "Lose some control/ownership",
          "Investor may want board seat",
          "Pressure to 'exit' vs build sustainably"
        ],
        forInvestors: [
          "10-30% annual return IF successful exit",
          "High risk: 50-70% of startups fail",
          "No returns for years, then (maybe) big payout",
          "Can provide strategic support beyond $"
        ],
        forBloom: [
          "Becomes fund manager or co-investor",
          "2-20% management fee typical",
          "Must have exit strategy for each deal",
          "Requires sophisticated legal structure"
        ]
      },

      crossJurisdiction: {
        complexity: "Very High",
        challenges: [
          "Corporate law differs by country",
          "Foreign ownership restrictions",
          "Dividend repatriation rules",
          "Shareholder dispute resolution",
          "Exit market liquidity varies drastically"
        ],
        solutions: [
          "Use holding company in investment-friendly jurisdiction",
          "Shareholders agreement overrides local law where possible",
          "Include mandatory buyback clauses",
          "Accept illiquidity for 7-10 years"
        ]
      },

      scenarios: {
        stable: {
          investment: 100000,
          returns: [0, 0, 0, 0, 250000],
          principalEnd: 0,
          narrative: "Invest in 5 ventures at $20K each. 3 fail, 1 breaks even, 1 exits at 5x. Portfolio return: 2.5x in 5 years (20% IRR)."
        },
        growth: {
          investment: 100000,
          returns: [0, 0, 0, 0, 500000],
          principalEnd: 0,
          narrative: "Home run scenario: One venture becomes regional leader. 10x return on best deal compensates for failures. Portfolio: 5x overall."
        },
        crisis: {
          investment: 100000,
          returns: [0, 0, 0, 0, 30000],
          principalEnd: 0,
          narrative: "All ventures struggle. No exits possible. Fire sale after 5 years recovers 30% of capital. Total loss: -70%."
        }
      },

      realExamples: [
        {
          name: "Acumen Fund",
          model: "Patient capital equity",
          impact: "$158M invested, 10-15 year holds",
          key: "Accept below-market returns (3-5%) for impact"
        },
        {
          name: "Omidyar Network",
          scale: "$1.4B deployed",
          impact: "Hybrid: some grants, some equity",
          key: "Different return expectations per deal stage"
        }
      ],

      bestFor: "High-growth potential. When venture could become very valuable. Sophisticated investors.",
      worstFor: "Lifestyle businesses. Founders who want control. Regions with weak exit markets."
    },

    {
      id: 'solidarity',
      name: 'Solidarity Fund / Cooperative',
      tagline: 'We rise together',
      icon: Users,
      color: 'orange',

      mechanics: {
        howItWorks: "Members contribute % of revenue/profit to shared pool. Pool redistributes based on collective decisions.",
        moneyFlow: "All members → Pool → Allocated by vote/need → Recipients → Payback → Repeat",
        ownership: "Collective ownership of pool, individual ownership of ventures",
        exitStrategy: "Withdraw from cooperative (may forfeit contributions)",
        timeHorizon: "Ongoing - no fixed term"
      },

      implications: {
        forVentures: [
          "Pay 2-5% of revenue as ongoing contribution",
          "Access support when needed (loans, grants, services)",
          "Voice in collective decisions",
          "Social safety net if you struggle"
        ],
        forInvestors: [
          "No direct financial return",
          "Entire ecosystem becomes more valuable",
          "ROI = stronger network effects",
          "Can be member + contributor"
        ],
        forBloom: [
          "Becomes coordinator/facilitator",
          "Can receive operational stipend from pool",
          "Democratic governance - not in control",
          "Administrative complexity managing redistribution"
        ]
      },

      crossJurisdiction: {
        complexity: "Medium-High",
        challenges: [
          "Cooperative law varies wildly",
          "Moving money between countries (fees, taxes)",
          "Different accounting standards",
          "Governance across time zones/languages"
        ],
        solutions: [
          "Use digital payment rails (Wise, PayPal, crypto)",
          "Federated model: local pools + global coordination",
          "Service swaps reduce cash movement",
          "Virtual governance with translation"
        ]
      },

      scenarios: {
        stable: {
          investment: 100000,
          returns: [3000, 3000, 3000, 3000, 3000],
          principalEnd: 150000,
          narrative: "50 ventures contribute 3% of revenue. Pool provides emergency loans, shared services. Pool grows as ventures grow. Value: stability."
        },
        growth: {
          investment: 100000,
          returns: [2000, 4000, 8000, 15000, 25000],
          principalEnd: 250000,
          narrative: "Network effects compound. Strong ventures pull up weak ones. Pool grows exponentially. New members join for access."
        },
        crisis: {
          investment: 100000,
          returns: [1000, 500, -2000, 1000, 3000],
          principalEnd: 80000,
          narrative: "Year 3: massive withdrawals deplete pool. Solidarity tested. Those who stay rebuild trust. Long-term survivors stronger."
        }
      },

      realExamples: [
        {
          name: "Mondragon Corporation (Spain)",
          scale: "81,000 member-owners, €12B revenue",
          impact: "70 years of profit-sharing, near-zero unemployment among members",
          key: "10% of profits to solidarity fund. Failing coops get support."
        },
        {
          name: "Gam'eya (ROSCA - Middle East)",
          model: "Rotating credit associations",
          impact: "Centuries old, billions in informal capital circulation",
          key: "100% social collateral. Trust > contracts."
        }
      ],

      bestFor: "Tight-knit community. Shared values/mission. Long-term ecosystem building.",
      worstFor: "Members seeking quick exits. Lack of trust. Highly diverse/anonymous group."
    },

    {
      id: 'hybrid',
      name: 'Hybrid / Blended Finance',
      tagline: 'Best of all worlds',
      icon: Shield,
      color: 'indigo',

      mechanics: {
        howItWorks: "Combines multiple models: grants for capacity, loans for working capital, equity for growth, waqf for long-term sustainability.",
        moneyFlow: "Tiered capital stack: grants → patient loans → commercial loans → equity, each with different risk/return",
        ownership: "Layered: some given, some lent, some owned",
        exitStrategy: "Different for each layer",
        timeHorizon: "Varies by instrument (1-20 years)"
      },

      implications: {
        forVentures: [
          "Right capital for right stage",
          "Early: grants/waqf for R&D",
          "Middle: loans for scaling",
          "Late: equity if high growth",
          "Complexity in managing multiple stakeholders"
        ],
        forInvestors: [
          "Choose risk/return profile",
          "Philanthropic: waqf layer (0% return)",
          "Conservative: senior loans (3-5% return)",
          "Aggressive: equity layer (15-30% return)",
          "Blended portfolio averages to market rate"
        ],
        forBloom: [
          "Maximum flexibility but maximum complexity",
          "Need sophisticated fund management",
          "Can attract diverse capital sources",
          "Must balance competing interests"
        ]
      },

      crossJurisdiction: {
        complexity: "Very High",
        challenges: [
          "Every layer has different legal requirements",
          "Waterfall payment structures across borders",
          "Reporting to multiple stakeholder types",
          "Regulatory arbitrage opportunities (and risks)"
        ],
        solutions: [
          "Use master fund with sub-funds per instrument",
          "Clear term sheets defining priority",
          "Professional fund administration ($50K+/year)",
          "Accept high setup costs for scalability"
        ]
      },

      scenarios: {
        stable: {
          investment: 100000,
          returns: [4000, 8000, 12000, 16000, 20000],
          principalEnd: 100000,
          narrative: "$30K waqf (0% return, perpetual), $40K loans (5% return, 5yr), $30K equity (sold at 3x after 5yr). Blended: 8% annual return + impact."
        },
        growth: {
          investment: 100000,
          returns: [2000, 5000, 15000, 30000, 80000],
          principalEnd: 120000,
          narrative: "Equity layer hits home run (10x), subsidizes waqf layer. Loans repay steadily. Overall portfolio: 12% IRR + preserved capital for next round."
        },
        crisis: {
          investment: 100000,
          returns: [1000, 2000, 0, 3000, 10000],
          principalEnd: 60000,
          narrative: "Equity layer wipes out. Loans restructured. Waqf principal preserved. Loss cushioned by diversification. Recover 60% of capital + ongoing waqf returns."
        }
      },

      realExamples: [
        {
          name: "Root Capital",
          model: "Grants for TA + loans for working capital",
          impact: "$1.6B in loans to 750+ agricultural businesses",
          key: "Philanthropy de-risks lending to unbankable businesses"
        },
        {
          name: "APIF (Islamic Dev Bank)",
          structure: "Waqf corpus + Mudarabah returns + IsDB loans",
          impact: "$767M portfolio, 3.5% stable dividends",
          key: "Perpetual capital base enables patient lending"
        }
      ],

      bestFor: "Complex situations. Diverse capital sources. Sophisticated financial management capacity.",
      worstFor: "Early-stage organizations. Limited admin capacity. Simple needs."
    }
  ];

  const comparisonDimensions = [
    { key: 'returnToInvestor', label: 'Return to Investor', waqf: '0%', loan: '0-10%', equity: '15-30% (if exit)', solidarity: '0% (indirect)', hybrid: '5-12% blended' },
    { key: 'returnTimeline', label: 'When Returns Come', waqf: 'Never (reinvested)', loan: '1-10 years', equity: '5-10 years', solidarity: 'Ongoing services', hybrid: '1-10 years varied' },
    { key: 'ventureOwnership', label: 'Venture Ownership', waqf: '100% keeps', loan: '100% keeps', equity: '60-90% keeps', solidarity: '100% keeps', hybrid: 'Depends on layer' },
    { key: 'repaymentPressure', label: 'Repayment Pressure', waqf: 'None', loan: 'High (fixed)', equity: 'None (but exit pressure)', solidarity: 'Moderate (social)', hybrid: 'Varied' },
    { key: 'riskToInvestor', label: 'Risk to Investor', waqf: 'Low (perpetual)', loan: 'Medium (10-20% default)', equity: 'High (50-70% fail)', solidarity: 'Low (diversified)', hybrid: 'Medium (layered)' },
    { key: 'legalComplexity', label: 'Legal Complexity', waqf: 'High (Shariah)', loan: 'Medium', equity: 'Very High', solidarity: 'Medium', hybrid: 'Very High' },
    { key: 'crossBorderEase', label: 'Cross-Border Ease', waqf: 'Hard', loan: 'Medium', equity: 'Hard', solidarity: 'Easy (if informal)', hybrid: 'Hard' },
    { key: 'alignmentIncentives', label: 'Alignment of Incentives', waqf: 'High (mission)', loan: 'Medium (repay vs grow)', equity: 'Low (exit vs sustain)', solidarity: 'Very High (mutual)', hybrid: 'Varied' },
    { key: 'scalePotential', label: 'Scale Potential', waqf: 'Very High (perpetual)', loan: 'High (revolving)', equity: 'Very High (if exits)', solidarity: 'Medium (trust-limited)', hybrid: 'Very High (diverse capital)' }
  ];

  const ColorMap = {
    emerald: 'bg-emerald-50 border-emerald-200 text-emerald-700',
    blue: 'bg-blue-50 border-blue-200 text-blue-700',
    purple: 'bg-purple-50 border-purple-200 text-purple-700',
    orange: 'bg-orange-50 border-orange-200 text-orange-700',
    indigo: 'bg-indigo-50 border-indigo-200 text-indigo-700'
  };

  const ButtonColorMap = {
    emerald: 'bg-emerald-500 hover:bg-emerald-600',
    blue: 'bg-blue-500 hover:bg-blue-600',
    purple: 'bg-purple-500 hover:bg-purple-600',
    orange: 'bg-orange-500 hover:bg-orange-600',
    indigo: 'bg-indigo-500 hover:bg-indigo-600'
  };

  const formatCurrency = (val) => {
    return new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD', minimumFractionDigits: 0 }).format(val);
  };

  const model = models.find(m => m.id === expandedModel);

  return (
    <div className="w-full max-w-7xl mx-auto p-6 bg-gray-50">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Investment Models for Bloom Ecosystem</h1>
        <p className="text-gray-600">From extraction to generosity: Compare financial structures across jurisdictions</p>
      </div>

      {/* Quick Comparison Table */}
      <div className="bg-white rounded-lg shadow-sm p-6 mb-8 overflow-x-auto">
        <h2 className="text-xl font-bold mb-4 flex items-center gap-2">
          <AlertCircle size={24} className="text-blue-500" />
          Quick Comparison: Key Dimensions
        </h2>
        <table className="w-full text-sm">
          <thead>
            <tr className="border-b-2 border-gray-200">
              <th className="text-left p-2 font-semibold">Dimension</th>
              <th className="text-left p-2 font-semibold text-emerald-700">Waqf</th>
              <th className="text-left p-2 font-semibold text-blue-700">Loan</th>
              <th className="text-left p-2 font-semibold text-purple-700">Equity</th>
              <th className="text-left p-2 font-semibold text-orange-700">Solidarity</th>
              <th className="text-left p-2 font-semibold text-indigo-700">Hybrid</th>
            </tr>
          </thead>
          <tbody>
            {comparisonDimensions.map((dim, idx) => (
              <tr key={dim.key} className={idx % 2 === 0 ? 'bg-gray-50' : ''}>
                <td className="p-2 font-medium text-gray-700">{dim.label}</td>
                <td className="p-2 text-emerald-700">{dim.waqf}</td>
                <td className="p-2 text-blue-700">{dim.loan}</td>
                <td className="p-2 text-purple-700">{dim.equity}</td>
                <td className="p-2 text-orange-700">{dim.solidarity}</td>
                <td className="p-2 text-indigo-700">{dim.hybrid}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Model Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-8">
        {models.map((m) => {
          const Icon = m.icon;
          return (
            <button
              key={m.id}
              onClick={() => setExpandedModel(expandedModel === m.id ? null : m.id)}
              className={`p-6 rounded-lg border-2 text-left transition-all ${ColorMap[m.color]} ${
                expandedModel === m.id ? 'ring-4 ring-offset-2 ring-gray-300' : ''
              }`}
            >
              <div className="flex items-start justify-between mb-3">
                <Icon size={32} />
                {expandedModel === m.id ? <ChevronUp size={24} /> : <ChevronDown size={24} />}
              </div>
              <h3 className="text-xl font-bold mb-1">{m.name}</h3>
              <p className="text-sm opacity-80">{m.tagline}</p>
            </button>
          );
        })}
      </div>

      {/* Expanded Model Details */}
      {model && (
        <div className="bg-white rounded-lg shadow-lg p-8 mb-8">
          <div className="flex items-center gap-4 mb-6">
            {React.createElement(model.icon, { size: 40, className: `text-${model.color}-600` })}
            <div>
              <h2 className="text-2xl font-bold">{model.name}</h2>
              <p className="text-gray-600">{model.tagline}</p>
            </div>
          </div>

          {/* Mechanics */}
          <div className="mb-6">
            <h3 className="text-lg font-bold mb-3 flex items-center gap-2">
              <div className={`w-1 h-6 ${ButtonColorMap[model.color].split(' ')[0]}`} />
              How It Works
            </h3>
            <div className="grid md:grid-cols-2 gap-4">
              {Object.entries(model.mechanics).map(([key, value]) => (
                <div key={key} className="bg-gray-50 p-4 rounded">
                  <div className="font-semibold text-sm text-gray-600 mb-1">
                    {key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
                  </div>
                  <div className="text-sm">{value}</div>
                </div>
              ))}
            </div>
          </div>

          {/* Implications */}
          <div className="mb-6">
            <h3 className="text-lg font-bold mb-3 flex items-center gap-2">
              <div className={`w-1 h-6 ${ButtonColorMap[model.color].split(' ')[0]}`} />
              Implications for Stakeholders
            </h3>
            <div className="grid md:grid-cols-3 gap-4">
              {Object.entries(model.implications).map(([stakeholder, points]) => (
                <div key={stakeholder} className="bg-gray-50 p-4 rounded">
                  <div className="font-semibold mb-2 capitalize">{stakeholder.replace('for', 'For ')}</div>
                  <ul className="space-y-1">
                    {points.map((point, idx) => (
                      <li key={idx} className="text-sm flex gap-2">
                        <span className="text-gray-400">•</span>
                        <span>{point}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          </div>

          {/* Cross-Jurisdiction */}
          <div className="mb-6">
            <h3 className="text-lg font-bold mb-3 flex items-center gap-2">
              <div className={`w-1 h-6 ${ButtonColorMap[model.color].split(' ')[0]}`} />
              Cross-Jurisdiction Considerations
            </h3>
            <div className="bg-amber-50 border border-amber-200 rounded p-4 mb-3">
              <div className="font-semibold text-amber-900">Complexity Level: {model.crossJurisdiction.complexity}</div>
            </div>
            <div className="grid md:grid-cols-2 gap-4">
              <div>
                <div className="font-semibold mb-2 text-red-700">Challenges</div>
                <ul className="space-y-1">
                  {model.crossJurisdiction.challenges.map((c, idx) => (
                    <li key={idx} className="text-sm flex gap-2">
                      <span className="text-red-400">⚠</span>
                      <span>{c}</span>
                    </li>
                  ))}
                </ul>
              </div>
              <div>
                <div className="font-semibold mb-2 text-green-700">Solutions</div>
                <ul className="space-y-1">
                  {model.crossJurisdiction.solutions.map((s, idx) => (
                    <li key={idx} className="text-sm flex gap-2">
                      <span className="text-green-400">✓</span>
                      <span>{s}</span>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>

          {/* Scenarios */}
          <div className="mb-6">
            <h3 className="text-lg font-bold mb-3 flex items-center gap-2">
              <div className={`w-1 h-6 ${ButtonColorMap[model.color].split(' ')[0]}`} />
              Financial Scenarios: $100K Investment Over 5 Years
            </h3>
