#!/usr/bin/env python3
import openpyxl
from deep_translator import GoogleTranslator
import time
import re
from openpyxl.worksheet.formula import ArrayFormula

def is_formula(value):
    """Check if a value is an Excel formula"""
    if isinstance(value, str) and value.startswith('='):
        return True
    if isinstance(value, ArrayFormula):
        return True
    return False

def is_number(value):
    """Check if a value is a number"""
    return isinstance(value, (int, float))

def should_translate(value):
    """Determine if a cell value should be translated"""
    if value is None or value == '':
        return False
    if is_number(value):
        return False
    if is_formula(value):
        return False
    if isinstance(value, str):
        # Don't translate if it's mostly numbers/symbols
        if re.match(r'^[\d\s\+\-\(\)\.,%$‚Ç¨¬£¬•/]+$', str(value)):
            return False
        # Don't translate URLs
        if 'http://' in str(value) or 'https://' in str(value) or '.com' in str(value):
            return False
        # Don't translate email addresses
        if '@' in str(value) and '.' in str(value):
            return False
        # Don't translate phone numbers
        if re.match(r'^\+?\d[\d\s\-\(\)]+$', str(value)):
            return False
        return True
    return False

def translate_text(text, translator, cache):
    """Translate text with caching to avoid redundant API calls"""
    if not text or text.strip() == '':
        return text

    # Check cache first
    if text in cache:
        return cache[text]

    try:
        # Split by newlines to preserve formatting
        if '\n' in text:
            lines = text.split('\n')
            translated_lines = []
            for line in lines:
                if line.strip():
                    translated = translator.translate(line)
                    translated_lines.append(translated if translated else line)
                else:
                    translated_lines.append(line)
            result = '\n'.join(translated_lines)
        else:
            result = translator.translate(text)
            if not result:
                result = text

        cache[text] = result
        time.sleep(0.1)  # Rate limiting
        return result
    except Exception as e:
        print(f"Translation error for '{text[:50]}...': {e}")
        return text

def translate_workbook(input_path, output_path):
    """Translate all text in an Excel workbook to Arabic while preserving structure"""
    print("Loading workbook...")
    wb = openpyxl.load_workbook(input_path)

    translator = GoogleTranslator(source='en', target='ar')
    translation_cache = {}

    total_cells = 0
    translated_cells = 0

    for sheet_name in wb.sheetnames:
        print(f"\nProcessing sheet: {sheet_name}")
        sheet = wb[sheet_name]

        # Translate sheet name if needed
        if should_translate(sheet_name):
            try:
                translated_name = translate_text(sheet_name, translator, translation_cache)
                # Excel sheet names have restrictions, keep original if translation causes issues
                if len(translated_name) <= 31 and not any(c in translated_name for c in [':', '\\', '/', '?', '*', '[', ']']):
                    sheet.title = translated_name
            except:
                pass  # Keep original name if translation fails

        # Iterate through all cells
        for row in sheet.iter_rows():
            for cell in row:
                total_cells += 1

                if cell.value and should_translate(cell.value):
                    original_value = cell.value
                    translated_value = translate_text(str(original_value), translator, translation_cache)
                    cell.value = translated_value
                    translated_cells += 1

                    if translated_cells % 50 == 0:
                        print(f"  Translated {translated_cells} cells so far...")

    print(f"\nSaving translated workbook...")
    print(f"Total cells processed: {total_cells}")
    print(f"Cells translated: {translated_cells}")

    wb.save(output_path)
    print(f"Saved to: {output_path}")

if __name__ == "__main__":
    input_file = "/mnt/user-data/uploads/Solar_Module_Workbook_-_BEIT__1_.xlsx"
    output_file = "/mnt/user-data/outputs/Solar_Module_Workbook_Arabic.xlsx"

    translate_workbook(input_file, output_file)

