const pptxgen = require("pptxgenjs");
const { html2pptx } = require("@ant/html2pptx");
const path = require("path");

// Slide files for Version A (Classical Academic)
const slides = [
  "slide-a1.html",
  "slide-a2.html",
  "slide-a3.html",
  "slide-a4.html",
  "slide-a5.html",
  "slide-a6.html",
  "slide-a7.html",
  "slide-a8.html",
  "slide-a9.html",
  "slide-a10.html",
  "slide-a11.html"
];

async function createPresentation() {
  try {
    const pptx = new pptxgen();
    pptx.layout = "LAYOUT_16x9";
    pptx.defineLayout({
      name: "LAYOUT_16x9",
      width: 10,
      height: 5.625
    });

    console.log("Starting presentation creation...");

    for (const slideFile of slides) {
      const slidePath = path.join("/home/claude", slideFile);
      console.log(`Processing ${slideFile}...`);

      try {
        await html2pptx(slidePath, pptx);
        console.log(`✓ Added ${slideFile}`);
      } catch (err) {
        console.error(`✗ Error processing ${slideFile}:`, err.message);
      }
    }

    const outputPath = "/mnt/user-data/outputs/Islamic_Psychology_Version_A.pptx";
    await pptx.writeFile(outputPath);
    console.log(`\n✓ Presentation saved to: ${outputPath}`);
    console.log(`Total slides: ${slides.length}`);

  } catch (err) {
    console.error("Error creating presentation:", err);
    process.exit(1);
  }
}

createPresentation();

