const pptxgen = require("pptxgenjs");
const { html2pptx } = require("@ant/html2pptx");
const path = require("path");

const slides = ["slide-c1.html", "slide-c2.html", "slide-c3.html", "slide-c4.html",
                "slide-c5.html", "slide-c6.html", "slide-c7.html", "slide-c8.html",
                "slide-c9.html", "slide-c10.html", "slide-c11.html"];

async function createPresentation() {
  try {
    const pptx = new pptxgen();
    pptx.layout = "LAYOUT_16x9";
    pptx.defineLayout({ name: "LAYOUT_16x9", width: 10, height: 5.625 });

    console.log("Creating Version C (Concentric Circles)...");
    for (const slideFile of slides) {
      const slidePath = path.join("/home/claude", slideFile);
      try {
        await html2pptx(slidePath, pptx);
        console.log(`✓ ${slideFile}`);
      } catch (err) {
        console.error(`✗ ${slideFile}: ${err.message}`);
      }
    }

    const outputPath = "/mnt/user-data/outputs/Islamic_Psychology_Version_C.pptx";
    await pptx.writeFile(outputPath);
    console.log(`\n✓ Saved: ${outputPath}`);
  } catch (err) {
    console.error("Error:", err);
    process.exit(1);
  }
}

createPresentation();
EOF

NODE_PATH="$(npm root -g)" node /home/claude/create-presentation-c.js 2>&1

