const pptxgen = require("pptxgenjs");
const { html2pptx } = require("@ant/html2pptx");
const path = require("path");

const slides = [
  "slide-refined-1.html",
  "slide-refined-2.html",
  "slide-refined-3.html",
  "slide-refined-4.html",
  "slide-refined-5.html",
  "slide-refined-6.html",
  "slide-refined-7.html",
  "slide-refined-8.html",
  "slide-refined-9.html",
  "slide-refined-10.html",
  "slide-refined-11.html"
];

async function createPresentation() {
  try {
    const pptx = new pptxgen();
    pptx.layout = "LAYOUT_16x9";
    pptx.defineLayout({ name: "LAYOUT_16x9", width: 10, height: 5.625 });

    console.log("✨ Creating refined, beautiful presentation...");
    for (const slideFile of slides) {
      const slidePath = path.join("/home/claude", slideFile);
      try {
        await html2pptx(slidePath, pptx);
        console.log(`   ✓ ${slideFile}`);
      } catch (err) {
        console.error(`   ✗ ${slideFile}: ${err.message}`);
      }
    }

    const outputPath = "/mnt/user-data/outputs/Islamic_Psychology_Heritage_Beautiful.pptx";
    await pptx.writeFile(outputPath);
    console.log(`\n✓ Beautiful presentation saved to:\n   ${outputPath}\n`);
  } catch (err) {
    console.error("Error:", err);
    process.exit(1);
  }
}

createPresentation();
EOF

