    34: 0.95, 35: 0.95,
    36: 0.98, 37: 0.98, 38: 0.98
};

// Calculate birth year (38 years old in 2023 based on notebook)
const BIRTH_YEAR = 1985;

// Calculate missed prayers by year
let totalMissedPrayers = 0;
let missedByPeriod = [];
let currentPeriodStart = 0;
let currentPeriodRate = 0;
let currentPeriodDescription = "";

// Process each year
Object.entries(yearlyCompletionRates).forEach(([age, rate], index) => {
    const year = parseInt(BIRTH_YEAR) + parseInt(age);
    const isExempt = rate === 0;
    const yearlyPrayers = PRAYERS_PER_DAY * DAYS_IN_YEAR;
    const missedPrayers = Math.round(yearlyPrayers * (1 - rate));

    // Determine if this is a new period
    let isNewPeriod = false;
    if (index === 0) {
        isNewPeriod = true;
    } else {
        const prevAge = parseInt(Object.keys(yearlyCompletionRates)[index-1]);
        const prevRate = yearlyCompletionRates[prevAge];

        // Check if rate changed significantly or exempt status changed
        if (Math.abs(rate - prevRate) >= 0.1 || (isExempt !== (prevRate === 0))) {
            isNewPeriod = true;
        }
    }

    // If new period, close previous period and start new one
    if (isNewPeriod && index > 0) {
        missedByPeriod.push({
            ageRange: `${currentPeriodStart}-${parseInt(age)-1}`,
            yearRange: `${parseInt(BIRTH_YEAR) + parseInt(currentPeriodStart)}-${parseInt(BIRTH_YEAR) + parseInt(age) - 1}`,
            completionRate: currentPeriodRate,
            description: currentPeriodDescription,
            exempt: currentPeriodRate === 0
        });

        currentPeriodStart = parseInt(age);
    }

    // For first entry, set initial period
    if (index === 0) {
        currentPeriodStart = parseInt(age);
    }

    // Update current period info
    currentPeriodRate = rate;
    if (rate === 0) {
        currentPeriodDescription = "Non-Muslim Period";
    } else if (rate >= 0.95) {
        currentPeriodDescription = "Highly Committed";
    } else if (rate >= 0.75) {
        currentPeriodDescription = "Strong Practice";
    } else if (rate >= 0.5) {
        currentPeriodDescription = "Moderate Practice";
    } else {
        currentPeriodDescription = "Inconsistent Practice";
    }

    // Add to total if not exempt
    if (!isExempt) {
        totalMissedPrayers += missedPrayers;
    }

    // For the last entry, close the period
    if (index === Object.keys(yearlyCompletionRates).length - 1) {
        missedByPeriod.push({
            ageRange: `${currentPeriodStart}-${age}`,
            yearRange: `${parseInt(BIRTH_YEAR) + parseInt(currentPeriodStart)}-${parseInt(BIRTH_YEAR) + parseInt(age)}`,
            completionRate: currentPeriodRate,
            description: currentPeriodDescription,
            exempt: currentPeriodRate === 0
        });
    }
});

// Calculate detailed missed prayers by period
const detailedMissedByPeriod = [];
missedByPeriod.forEach(period => {
    const startAge = parseInt(period.ageRange.split('-')[0]);
    const endAge = parseInt(period.ageRange.split('-')[1]);
    let periodMissedPrayers = 0;

    // Calculate missed prayers for each year in the period
    for (let age = startAge; age <= endAge; age++) {
        const yearlyPrayers = PRAYERS_PER_DAY * DAYS_IN_YEAR;
        const rate = yearlyCompletionRates[age];
        const missedPrayers = Math.round(yearlyPrayers * (1 - rate));
        periodMissedPrayers += missedPrayers;
    }

    detailedMissedByPeriod.push({
        ...period,
        missedPrayers: periodMissedPrayers,
        countTowardsTotal: !period.exempt
    });
});

// Account for recent makeup prayers
const recentMakeupPrayers = {
    "Past year average (2.5/day)": 2.5 * 365,
    "Last Ramadan (5/day)": 5 * 30,
    "Current Ramadan (7/day for 15 days)": 7 * 15
};
const totalMakeupCompleted = Object.values(recentMakeupPrayers).reduce((sum, val) => sum + val, 0);
const remainingPrayers = Math.max(0, totalMissedPrayers - totalMakeupCompleted);

// Calculate time to complete by next Ramadan
const TODAY = new Date();
const NEXT_RAMADAN_START = new Date(2026, 1, 20); // Approximate date for Ramadan 2026
const DAYS_UNTIL_RAMADAN = Math.floor((NEXT_RAMADAN_START - TODAY) / (1000 * 60 * 60 * 24));
const PRAYERS_NEEDED_PER_DAY = Math.ceil(remainingPrayers / DAYS_UNTIL_RAMADAN);

// Time estimates
const AVERAGE_PRAYER_TIME_MINUTES = 5; 
const AVERAGE_PAGE_READING_TIME_MINUTES = 1.5;
const PAGES_PER_PRAYER = 2;
const READING_TIME_PER_PRAYER = PAGES_PER_PRAYER * AVERAGE_PAGE_READING_TIME_MINUTES;
const TOTAL_TIME_PER_PRAYER = AVERAGE_PRAYER_TIME_MINUTES + READING_TIME_PER_PRAYER;
const DAILY_PRAYER_TIME_MINUTES = PRAYERS_NEEDED_PER_DAY * TOTAL_TIME_PER_PRAYER;
const DAILY_PRAYER_TIME_HOURS = (DAILY_PRAYER_TIME_MINUTES / 60).toFixed(1);

// Alternative timeframes
const alternativeTimeframes = [
    {name: "12 months", days: 365},
    {name: "18 months", days: 365 * 1.5},
    {name: "24 months", days: 365 * 2}
];

// Calculate distribution across prayer times
function distributeQuotaOptimally(total, slots) {
    const base = Math.floor(total / slots);
    const remainder = total % slots;

    // Create base distribution
    const distribution = Array(slots).fill(base);

    // Add remainder prioritizing first and last slots
    for (let i = 0; i < remainder; i++) {
        if (i % 2 === 0) {
            distribution[0]++;
        } else {
            distribution[slots-1]++;
        }
    }

    return distribution;
}

// Distribution of prayers
const prayerDistribution = distributeQuotaOptimally(PRAYERS_NEEDED_PER_DAY, 5);
const prayerNames = ["Fajr", "Dhuhr", "Asr", "Maghrib", "Isha"];
const prayerSchedule = {};
prayerNames.forEach((name, index) => {
    prayerSchedule[name] = prayerDistribution[index];
});

// Output results
console.log("Prayer Makeup Calculation Based on Personal Records");
console.log("=================================================");

console.log("\nDetailed Missed Prayers by Period:");
detailedMissedByPeriod.forEach(period => {
    if (period.exempt) {
        console.log(`- Ages ${period.ageRange} (${period.yearRange}): ${period.missedPrayers.toLocaleString()} prayers - ${period.description} (exempt under Maliki fiqh)`);
    } else {
        console.log(`- Ages ${period.ageRange} (${period.yearRange}): ${period.missedPrayers.toLocaleString()} prayers - ${period.description}`);
    }
});

console.log(`\nTotal missed prayers (excluding exempt periods): ${totalMissedPrayers.toLocaleString()}`);

console.log("\nRecent Makeup Prayers Completed:");
Object.entries(recentMakeupPrayers).forEach(([description, count]) => {
    console.log(`- ${description}: ${Math.round(count).toLocaleString()} prayers`);
});
console.log(`- Total makeup prayers completed: ${Math.round(totalMakeupCompleted).toLocaleString()}`);

console.log(`\nRemaining prayers to make up: ${Math.round(remainingPrayers).toLocaleString()}`);

console.log(`\nDays until next Ramadan: ${DAYS_UNTIL_RAMADAN}`);

console.log("\nPlan to complete by next Ramadan:");
console.log(`- Prayers needed per day: ${PRAYERS_NEEDED_PER_DAY}`);
console.log(`- Daily time commitment: ${DAILY_PRAYER_TIME_MINUTES} minutes (${DAILY_PRAYER_TIME_HOURS} hours)`);

console.log("\nRecommended daily distribution:");
Object.entries(prayerSchedule).forEach(([prayer, count]) => {
    console.log(`- ${prayer}: ${count} makeup prayers (${Math.round(count * TOTAL_TIME_PER_PRAYER)} minutes)`);
});

console.log("\nAlternative Timeframes:");
alternativeTimeframes.forEach(timeframe => {
    const prayersPerDay = Math.ceil(remainingPrayers / timeframe.days);
    const hoursPerDay = (prayersPerDay * TOTAL_TIME_PER_PRAYER / 60).toFixed(1);
    console.log(`- ${timeframe.name}: ${prayersPerDay} prayers per day (${hoursPerDay} hours daily)`);
});

// Quran reading estimates
const PAGES_IN_QURAN = 604;
const pagesReadDaily = PRAYERS_NEEDED_PER_DAY * PAGES_PER_PRAYER;
const daysPerQuran = Math.ceil(PAGES_IN_QURAN / pagesReadDaily);
const quransCompleted = Math.floor(DAYS_UNTIL_RAMADAN / daysPerQuran);

console.log("\nQuran Reading Progress:");
console.log(`- Pages read daily: ${pagesReadDaily}`);
console.log(`- Complete one Quran every: ${daysPerQuran} days`);
console.log(`- Total Qurans completed by next Ramadan: ${quransCompleted}`);

